        (§ try )
        (§ block
            (§ return (+ (.. peers (size)) (.. pendingPeers (size))))
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    #_private
    (§ enum #_"LocalhostCheckState"
