        (Preconditions/checkState (.. lock (isHeldByCurrentThread)))

        (§ if (== localhostCheckState LocalhostCheckState/NOT_TRIED))
        (§ block
            ;; Do a fast blocking connect to see if anything is listening.
            (§ let #_"Socket" (§ name ßsocket) nil)
            (§ block
                (§ try )
                (§ block
                    (§ ass (§ name ßsocket) (§ new #_"Socket"))
                    (.. ßsocket (connect (§ new #_"InetSocketAddress" (InetAddresses/forString "127.0.0.1"), (.. params (getPort))), vConnectTimeoutMillis))
                    (§ ass (§ name localhostCheckState) LocalhostCheckState/FOUND)
