    #_public
    (§ method #_"ListenableFuture" (§ fn stopAsync) [])
    (§ block
        (Preconditions/checkState vRunning)
        (§ ass (§ name vRunning) false)
        (§ let #_"ListenableFuture" (§ name ßfuture) (.. executor (submit (§ new #_"Runnable")
        (§ block
        (§ anon
                #_override
                #_public
                (§ method #_"void" (§ fn run) [])
