        (.. peerFilterProviders (remove ßwallet))
        (.. ßwallet (removeCoinsReceivedEventListener walletCoinsReceivedEventListener))
        (.. ßwallet (removeKeyChainEventListener walletKeyEventListener))
        (.. ßwallet (setTransactionBroadcaster nil))
        (§ for (§ var #_"Peer" (§ name ßpeer)) :for peers)
        (§ block
            (.. ßpeer (removeWallet ßwallet))
        )
        nil
    )

    #_public
    (§ enum #_"FilterRecalculateMode"
        (§ item SEND_IF_CHANGED)
        (§ item FORCE_SEND_FOR_REFRESH)
        (§ item DONT_SEND)
    )

    #_private
    (§ field- #_"Map<FilterRecalculateMode, SettableFuture<BloomFilter>>" (§ name inFlightRecalculations) (Maps/newHashMap))

    ;;;
     ; Recalculates the bloom filter given to peers as well as the timestamp after which full blocks are downloaded
     ; (instead of only headers).  Note that calls made one after another may return the same future, if the request
     ; wasn't processed yet (i.e. calls are deduplicated).
     ;
     ; @param mode In what situations to send the filter to connected peers.
     ; @return A future that completes once the filter has been calculated (note: this does not mean acknowledged by remote peers).
     ;;
    #_public
    (§ method #_"ListenableFuture<BloomFilter>" (§ fn recalculateFastCatchupAndFilter) [#_"FilterRecalculateMode" (§ name ßmode)])
    (§ block
        (§ let #_"SettableFuture<BloomFilter>" (§ name ßfuture) (SettableFuture/create))
        (§ block
            (§ sync inFlightRecalculations)
            (§ block
                (§ if (!= (.. inFlightRecalculations (get ßmode)) nil))
                (§ block
                    (§ return (.. inFlightRecalculations (get ßmode)))
                )

                (.. inFlightRecalculations (put ßmode, ßfuture))
            )
            (§ let #_"Runnable" (§ name ßcommand) (§ new #_"Runnable"
            (§ block
            (§ anon
                    #_override
                    #_public
                    (§ method #_"void" (§ fn run) [])
                    (§ block
                        (§ try )
                        (§ block
                            (go)
                        )
                        (§ catch #_"Throwable" (§ name ße))
                        (§ block
                            (.. log (error "Exception when trying to recalculate Bloom filter", ße)) ;; The executor swallows exceptions :( ;; )
                        )
                        nil
                    )

                    #_public
                    (§ method #_"void" (§ fn go) [])
                    (§ block
                        (Preconditions/checkState (not (.. lock (isHeldByCurrentThread))))
                        ;; Fully verifying mode doesn't use this optimization (it can't as it needs to see all transactions).
                        (§ if (or (and (some? chain) (.. chain (shouldVerifyTransactions))) (not vBloomFilteringEnabled)))
                        (§ block
                            (§ return nil)
                        )

                        ;; We only ever call bloomFilterMerger.calculate on jobQueue, so we cannot be calculating two filters at once.
                        (§ let #_"FilterMerger.Result" (§ name ßresult) (.. bloomFilterMerger (calculate (ImmutableList/copyOf peerFilterProviders)))) ;; COW
                        (§ block
                            (§ let #_"boolean" (§ name ßsend))
                            (§ block
                                (§ switch ßmode)
                                (§ block
                                    (§ case SEND_IF_CHANGED)
                                    (§ block
                                        (§ ass (§ name ßsend) (.. ßresult changed))
                                        (§ break )
                                    )
                                    (§ case DONT_SEND)
                                    (§ block
                                        (§ ass (§ name ßsend) false)
                                        (§ break )
                                    )
                                    (§ case FORCE_SEND_FOR_REFRESH)
                                    (§ block
                                        (§ ass (§ name ßsend) true)
                                        (§ break )
                                    )
                                    (§ default )
                                    (§ block
                                        (§ throw (§ new #_"UnsupportedOperationException"))
                                    )
                                )
                                (§ if ßsend)
                                (§ block
                                    (§ for (§ var #_"Peer" (§ name ßpeer)) :for peers) ;; COW
                                    (§ block
                                        ;; Only query the mempool if this recalculation request is not in order to lower the observed FP
                                        ;; rate.  There's no point querying the mempool when doing this because the FP rate can only go
                                        ;; down, and we will have seen all the relevant txns before: it's pointless to ask for them again.
                                        (.. ßpeer (setBloomFilter (.. ßresult filter), (!= ßmode FilterRecalculateMode/FORCE_SEND_FOR_REFRESH)))
                                    )
                                    ;; Reset the false positive estimate so that we don't send a flood of filter updates
                                    ;; if the estimate temporarily overshoots our threshold.
                                    (§ if (some? chain))
                                    (§ block
                                        (.. chain (resetFalsePositiveEstimate))
                                    )
                                )
                                ;; Do this last so that bloomFilter is already set when it gets called.
                                (setFastCatchupTimeSecs (.. ßresult earliestKeyTimeSecs))
                                (§ sync inFlightRecalculations)
                                (§ block
                                    (.. inFlightRecalculations (put ßmode, nil))
                                )
                                (.. ßfuture (set (.. ßresult filter)))
                                nil
                            )
                        )
                    )
                )
            )))
            (§ try )
            (§ block
                (.. executor (execute command))
            )
            (§ catch #_"RejectedExecutionException" (§ name _))
            (§ block
                ;; Can happen during shutdown.
            )
            ßfuture
        )
    )

    ;;;
     ; <p>Sets the false positive rate of bloom filters given to peers.  The default is {@link #DEFAULT_BLOOM_FILTER_FP_RATE}.</p>
     ;
     ; <p>Be careful regenerating the bloom filter too often, as it decreases anonymity because remote nodes can
     ; compare transactions against both the new and old filters to significantly decrease the false positive rate.</p>
     ;
     ; <p>See the docs for {@link BloomFilter#BloomFilter(int, double, long, BloomFilter.BloomUpdate)} for a brief
     ; explanation of anonymity when using bloom filters.</p>
     ;;
    #_public
    (§ method #_"void" (§ fn setBloomFilterFalsePositiveRate) [#_"double" (§ name ßbloomFilterFPRate)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (.. bloomFilterMerger (setBloomFilterFPRate ßbloomFilterFPRate))
            (recalculateFastCatchupAndFilter FilterRecalculateMode/SEND_IF_CHANGED)
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        nil
    )

