    ;;;
     ; Creates a version message to send, constructs a Peer object and attempts to connect it.
     ; Returns the peer on success or null on failure.
     ; @param address Remote network address.
     ; @param incrementMaxConnections Whether to consider this connection an attempt to fill our quota, or something explicitly requested.
     ; @return Peer or null.
     ;;
    #_nilable
    #_guarded-by(§ opt "lock")
    #_protected
    (§ method #_"Peer" (§ fn connectTo) [#_"PeerAddress" (§ name ßaddress), #_"boolean" (§ name ßincrementMaxConnections), #_"int" (§ name ßconnectTimeoutMillis)])
    (§ block
        (Preconditions/checkState (.. lock (isHeldByCurrentThread)))
        (§ let #_"VersionMessage" (§ name ßver) (.. (getVersionMessage) (duplicate)))
        (§ block
            (§ ass (§ name (.. ßver bestHeight)) (if (some? chain) (.. chain (getBestChainHeight)) 0))
            (§ ass (§ name (.. ßver time)) (Utils/currentTimeSeconds))

            (§ let #_"Peer" (§ name ßpeer) (createPeer ßaddress, ßver))
            (§ block
                (.. ßpeer (addConnectedEventListener Threading/SAME_THREAD, startupListener))
                (.. ßpeer (addDisconnectedEventListener Threading/SAME_THREAD, startupListener))
                (.. ßpeer (setMinProtocolVersion vMinRequiredProtocolVersion))
                (.. pendingPeers (add ßpeer))

                (§ try )
                (§ block
                    (.. log (info "Attempting connection to {}     ({} connected, {} pending, {} max)", ßaddress, (.. peers (size)), (.. pendingPeers (size)), maxConnections))
                    (§ let #_"ListenableFuture<SocketAddress>" (§ name ßfuture) (.. channels (openConnection (.. ßaddress (toSocketAddress)), ßpeer)))
                    (§ block
                        (§ if (.. ßfuture (isDone)))
                        (§ block
                            (Uninterruptibles/getUninterruptibly ßfuture)
                        )
                    )
                )
                (§ catch #_"ExecutionException" (§ name ße))
                (§ block
                    (§ let #_"Throwable" (§ name ßcause) (Throwables/getRootCause ße))
                    (§ block
                        (.. log (warn (str "Failed to connect to " ßaddress ": " (.. ßcause (getMessage)))))
                        (handlePeerDeath ßpeer, ßcause)
                        (§ return nil)
                    )
                )
                (.. ßpeer (setSocketTimeout ßconnectTimeoutMillis))
                ;; When the channel has connected and version negotiated successfully, handleNewPeer will end up being called
                ;; on a worker thread.
                (§ if ßincrementMaxConnections)
                (§ block
                    ;; We don't use setMaxConnections here as that would trigger a recursive attempt to establish a new
                    ;; outbound connection.
                    (§ ass (§ name maxConnections) (+ maxConnections 1))
                )
                ßpeer
            )
        )
    )

    ;;; You can override this to customise the creation of {@link Peer} objects. ;;
    #_guarded-by(§ opt "lock")
    #_protected
    (§ method #_"Peer" (§ fn createPeer) [#_"PeerAddress" (§ name ßaddress), #_"VersionMessage" (§ name ßver)])
    (§ block
        (§ new #_"Peer" params, ßver, ßaddress, chain, downloadTxDependencyDepth)
    )

    ;;;
     ; Sets the timeout between when a connection attempt to a peer begins and when the version message exchange completes.
     ; This does not apply to currently pending peers.
     ;;
    #_public
    (§ method #_"void" (§ fn setConnectTimeoutMillis) [#_"int" (§ name ßconnectTimeoutMillis)])
    (§ block
        (§ ass (§ name (.. this vConnectTimeoutMillis)) ßconnectTimeoutMillis)
        nil
    )

    ;;;
     ; <p>Start downloading the blockchain from the first available peer.</p>
     ;
     ; <p>If no peers are currently connected, the download will be started once a peer starts.  If the peer dies,
     ; the download will resume with another peer.</p>
     ;
     ; @param listener A listener for chain download events, may not be null.
     ;;
    #_public
    (§ method #_"void" (§ fn startBlockChainDownload) [#_"PeerDataEventListener" (§ name ßlistener)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ if (some? downloadPeer))
            (§ block
                (§ if (!= (.. this downloadListener) nil))
                (§ block
                    (removeDataEventListenerFromPeer downloadPeer, (.. this downloadListener))
                )
                (§ if (some? ßlistener))
                (§ block
                    (addDataEventListenerToPeer Threading/USER_THREAD, downloadPeer, ßlistener)
                )
            )
            (§ ass (§ name (.. this downloadListener)) ßlistener)
            ;; TODO: Be more nuanced about which peer to download from.  We can also try
            ;; downloading from multiple peers and handle the case when a new peer comes along
            ;; with a longer chain after we thought we were done.
            (§ if (not (.. peers (isEmpty))))
            (§ block
                (startBlockChainDownloadFromPeer (.. peers (iterator) (next))) ;; Will add the new download listener.
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        nil
    )

    ;;;
     ; Register a data event listener against a single peer (i.e. for blockchain download).
     ; Handling registration/deregistration on peer death/add is outside the scope of these methods.
     ;;
    #_private
    #_static
    (§ defn- #_"void" (§ fn addDataEventListenerToPeer) [#_"Executor" (§ name ßexecutor), #_"Peer" (§ name ßpeer), #_"PeerDataEventListener" (§ name ßdownloadListener)])
    (§ block
        (.. ßpeer (addBlocksDownloadedEventListener ßexecutor, ßdownloadListener))
        (.. ßpeer (addChainDownloadStartedEventListener ßexecutor, ßdownloadListener))
        (.. ßpeer (addGetDataEventListener ßexecutor, ßdownloadListener))
        (.. ßpeer (addPreMessageReceivedEventListener ßexecutor, ßdownloadListener))
        nil
    )

    ;;;
     ; Remove a registered data event listener against a single peer (i.e. for blockchain download).
     ; Handling registration/deregistration on peer death/add is outside the scope of these methods.
     ;;
    #_private
    #_static
    (§ defn- #_"void" (§ fn removeDataEventListenerFromPeer) [#_"Peer" (§ name ßpeer), #_"PeerDataEventListener" (§ name ßlistener)])
    (§ block
        (.. ßpeer (removeBlocksDownloadedEventListener ßlistener))
        (.. ßpeer (removeChainDownloadStartedEventListener ßlistener))
        (.. ßpeer (removeGetDataEventListener ßlistener))
        (.. ßpeer (removePreMessageReceivedEventListener ßlistener))
        nil
    )

    ;;;
     ; Download the blockchain from peers.  Convenience that uses a {@link DownloadProgressTracker} for you.
     ;
     ; This method waits until the download is complete.  "Complete" is defined as downloading
     ; from at least one peer all the blocks that are in that peer's inventory.
     ;;
    #_public
    (§ method #_"void" (§ fn downloadBlockChain) [])
    (§ block
        (§ let #_"DownloadProgressTracker" (§ name ßlistener) (§ new #_"DownloadProgressTracker"))
        (§ block
            (startBlockChainDownload ßlistener)
            (§ try )
            (§ block
                (.. ßlistener (await))
            )
            (§ catch #_"InterruptedException" (§ name ße))
            (§ block
                (§ throw (§ new #_"RuntimeException" ße))
            )
            nil
        )
    )

    #_protected
    (§ method #_"void" (§ fn handleNewPeer) [#_"Peer" (§ name ßpeer)])
    (§ block
        (§ let #_"int" (§ name ßnewSize) -1)
        (§ block
            (.. lock (lock))
            (§ try )
            (§ block
                (.. groupBackoff (trackSuccess))
                (.. backoffMap (get (.. ßpeer (getAddress))) (trackSuccess))

                ;; Sets up the newly connected peer so it can do everything it needs to.
                (.. pendingPeers (remove ßpeer))
                (.. peers (add ßpeer))
                (§ ass (§ name ßnewSize) (.. peers (size)))
                (.. log (info "{}: New ßpeer      ({} connected, {} pending, {} max)", ßpeer, ßnewSize, (.. pendingPeers (size)), maxConnections))
                ;; Give the peer a filter that can be used to probabilistically drop transactions that
                ;; aren't relevant to our wallet.  We may still receive some false positives, which is
                ;; OK because it helps improve wallet privacy.  Old nodes will just ignore the message.
                (§ if (!= (.. bloomFilterMerger (getLastFilter)) nil))
                (§ block
                    (.. ßpeer (setBloomFilter (.. bloomFilterMerger (getLastFilter))))
                )
                (.. ßpeer (setDownloadData false))
                ;; TODO: The peer should calculate the fast catchup time from the added wallets here.
                (§ for (§ var #_"Wallet" (§ name ßwallet)) :for wallets)
                (§ block
                    (.. ßpeer (addWallet ßwallet))
                )
                (§ if (nil? downloadPeer))
                (§ block
                    ;; Kick off chain download if we aren't already doing it.
                    (setDownloadPeer (selectDownloadPeer peers))
                    (§ let #_"boolean" (§ name ßshouldDownloadChain) (and (some? downloadListener) (some? chain)))
                    (§ block
                        (§ if ßshouldDownloadChain)
                        (§ block
                            (startBlockChainDownloadFromPeer downloadPeer)
                        )
                    )
                )
                ;; Make sure the peer knows how to upload transactions that are requested from us.
                (.. ßpeer (addBlocksDownloadedEventListener Threading/SAME_THREAD, peerListener))
                (.. ßpeer (addGetDataEventListener Threading/SAME_THREAD, peerListener))

                ;; And set up event listeners for clients.  This will allow them to find out about new transactions and blocks.
                (§ for (§ var #_"ListenerRegistration<BlocksDownloadedEventListener>" (§ name ßregistration)) :for peersBlocksDownloadedEventListeners)
                (§ block
                    (.. ßpeer (addBlocksDownloadedEventListener (.. ßregistration executor), (.. ßregistration listener)))
                )
                (§ for (§ var #_"ListenerRegistration<ChainDownloadStartedEventListener>" (§ name ßregistration)) :for peersChainDownloadStartedEventListeners)
                (§ block
                    (.. ßpeer (addChainDownloadStartedEventListener (.. ßregistration executor), (.. ßregistration listener)))
                )
                (§ for (§ var #_"ListenerRegistration<PeerConnectedEventListener>" (§ name ßregistration)) :for peerConnectedEventListeners)
                (§ block
                    (.. ßpeer (addConnectedEventListener (.. ßregistration executor), (.. ßregistration listener)))
                )

                ;; We intentionally do not add disconnect listeners to peers.
                (§ for (§ var #_"ListenerRegistration<GetDataEventListener>" (§ name ßregistration)) :for peerGetDataEventListeners)
                (§ block
                    (.. ßpeer (addGetDataEventListener (.. ßregistration executor), (.. ßregistration listener)))
                )
                (§ for (§ var #_"ListenerRegistration<OnTransactionBroadcastListener>" (§ name ßregistration)) :for peersTransactionBroadastEventListeners)
                (§ block
                    (.. ßpeer (addOnTransactionBroadcastListener (.. ßregistration executor), (.. ßregistration listener)))
                )
                (§ for (§ var #_"ListenerRegistration<PreMessageReceivedEventListener>" (§ name ßregistration)) :for peersPreMessageReceivedEventListeners)
                (§ block
                    (.. ßpeer (addPreMessageReceivedEventListener (.. ßregistration executor), (.. ßregistration listener)))
                )
            )
            (§ finally )
            (§ block
                (.. lock (unlock))
            )

            (§ let #_"int" (§ name ßfNewSize) ßnewSize)
            (§ block
                (§ for (§ var ListenerRegistration<PeerConnectedEventListener> registration) :for peerConnectedEventListeners)
                (§ block
                    (.. registration executor (execute (§ new #_"Runnable")
                    (§ anon
                        #_override
                        #_public
                        (§ method #_"void" (§ fn run) [])
                        (§ block
                            (.. registration listener (onPeerConnected ßpeer, ßfNewSize))
                            nil
                        )
                    )))
                )
                nil
            )
        )
    )

    #_nilable
    #_private
    #_volatile
    (§ field- #_"ListenableScheduledFuture<?>" (§ name vPingTask))

    #_suppress(§ opt "NonAtomicOperationOnVolatileField")
    #_private
    (§ method- #_"void" (§ fn setupPinging) [])
    (§ block
        (§ if (<= (getPingIntervalMsec) 0))
        (§ block
            (§ return nil) ;; Disabled.
        )

        (§ ass (§ name vPingTask) (.. executor (scheduleAtFixedRate (§ new #_"Runnable")
        (§ anon
            #_override
            #_public
            (§ method #_"void" (§ fn run) [])
            (§ block
                (§ try )
                (§ block
                    (§ if (<= (getPingIntervalMsec) 0))
                    (§ block
                        (§ let #_"ListenableScheduledFuture<?>" (§ name ßtask) vPingTask)
                        (§ block
                            (§ if (some? ßtask))
                            (§ block
                                (.. ßtask (cancel false))
                                (§ ass (§ name vPingTask) nil)
                            )
                            (§ return nil) ;; Disabled.
                        )
                    )
                    (§ for (§ var #_"Peer" (§ name ßpeer)) :for (getConnectedPeers))
                    (§ block
                        (§ if (< (.. ßpeer (getPeerVersionMessage) clientVersion) (.. params (getProtocolVersionNum NetworkParameters/ProtocolVersion/PONG))))
                        (§ block
                            (§ continue )
                        )
                        (.. ßpeer (ping))
                    )
                )
                (§ catch #_"Throwable" (§ name ße))
                (§ block
                    (.. log (error "Exception in ping loop", ße)) ;; The executor swallows exceptions :( ;; )
                )
                nil
            )
        ), (getPingIntervalMsec), (getPingIntervalMsec), TimeUnit/MILLISECONDS)))
        nil
    )

    #_private
    (§ method- #_"void" (§ fn setDownloadPeer) [#_nilable #_"Peer" (§ name ßpeer)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ if (== downloadPeer ßpeer))
            (§ block
                (§ return nil)
            )
            (§ if (some? downloadPeer))
            (§ block
                (.. log (info "Unsetting download ßpeer: {}", downloadPeer))
                (§ if (some? downloadListener))
                (§ block
                    (removeDataEventListenerFromPeer downloadPeer, downloadListener)
                )
                (.. downloadPeer (setDownloadData false))
            )
            (§ ass (§ name downloadPeer) ßpeer)
            (§ if (some? downloadPeer))
            (§ block
                (.. log (info "Setting download ßpeer: {}", downloadPeer))
                (§ if (some? downloadListener))
                (§ block
                    (addDataEventListenerToPeer Threading/SAME_THREAD, ßpeer, downloadListener)
                )
                (.. downloadPeer (setDownloadData true))
                (§ if (some? chain))
                (§ block
                    (.. downloadPeer (setDownloadParameters fastCatchupTimeSecs, (some? (.. bloomFilterMerger (getLastFilter)))))
                )
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        nil
    )

    ;;;
     ; Tells the PeerGroup to download only block headers before a certain time and bodies after that.
     ; Call this before starting block chain download.
     ; Do not use a time > NOW - 1 block, as it will break some block download logic.
     ;;
    #_public
    (§ method #_"void" (§ fn setFastCatchupTimeSecs) [#_"long" (§ name ßsecondsSinceEpoch)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (Preconditions/checkState (or (nil? chain) (not (.. chain (shouldVerifyTransactions)))), "Fast catchup is incompatible with fully verifying")

            (§ ass (§ name fastCatchupTimeSecs) ßsecondsSinceEpoch)
            (§ if (some? downloadPeer))
            (§ block
                (.. downloadPeer (setDownloadParameters ßsecondsSinceEpoch, (some? (.. bloomFilterMerger (getLastFilter)))))
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        nil
    )

    ;;;
     ; Returns the current fast catchup time.  The contents of blocks before this time won't be downloaded as they
     ; cannot contain any interesting transactions.  If you use {@link PeerGroup#addWallet(Wallet)} this just returns
     ; the min of the wallets earliest key times.
     ; @return a time in seconds since the epoch.
     ;;
    #_public
    (§ method #_"long" (§ fn getFastCatchupTimeSecs) [])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ return fastCatchupTimeSecs)
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    #_protected
    (§ method #_"void" (§ fn handlePeerDeath) [#_"Peer" (§ name ßpeer), #_nilable #_"Throwable" (§ name ßexception)])
    (§ block
        ;; Peer deaths can occur during startup if a connect attempt after peer discovery aborts immediately.
        (§ if (not (isRunning)))
        (§ block
            (§ return nil)
        )

        (§ let #_"int" (§ name ßnumPeers))
        (§ block
            (§ let #_"int" (§ name ßnumConnectedPeers) 0)
            (§ block
                (.. lock (lock))
                (§ try )
                (§ block
                    (.. pendingPeers (remove ßpeer))
                    (.. peers (remove ßpeer))

                    (§ let #_"PeerAddress" (§ name ßaddress) (.. ßpeer (getAddress)))
                    (§ block

                        (.. log (info "{}: Peer died      ({} connected, {} pending, {} max)", ßaddress, (.. peers (size)), (.. pendingPeers (size)), maxConnections))
                        (§ if (== ßpeer downloadPeer))
                        (§ block
                            (.. log (info "Download ßpeer died. Picking a new one."))
                            (setDownloadPeer nil)
                            ;; Pick a new one and possibly tell it to download the chain.
                            (§ let #_"Peer" (§ name ßnewDownloadPeer) (selectDownloadPeer peers))
                            (§ block
                                (§ if (some? ßnewDownloadPeer))
                                (§ block
                                    (setDownloadPeer ßnewDownloadPeer)
                                    (§ if (some? downloadListener))
                                    (§ block
                                        (startBlockChainDownloadFromPeer ßnewDownloadPeer)
                                    )
                                )
                            )
                        )
                        (§ ass (§ name ßnumPeers) (+ (.. peers (size)) (.. pendingPeers (size))))
                        (§ ass (§ name ßnumConnectedPeers) (.. peers (size)))

                        (.. groupBackoff (trackFailure))

                        (§ if (§ insta ßexception #_"NoRouteToHostException"))
                        (§ block
                            (§ if (and (§ insta (.. ßaddress (getAddr)) #_"Inet6Address") (not ipv6Unreachable)))
                            (§ block
                                (§ ass (§ name ipv6Unreachable) true)
                                (.. log (warn "IPv6 ßpeer connect failed due to routing failure, ignoring IPv6 addresses from now on"))
                            )
                        )
                        (§ else )
                        (§ block
                            (.. backoffMap (get ßaddress) (trackFailure))
                            ;; Put back on inactive list.
                            (.. inactives (offer ßaddress))
                        )

                        (§ if (< ßnumPeers (getMaxConnections)))
                        (§ block
                            (triggerConnections)
                        )
                    )
                )
                (§ finally )
                (§ block
                    (.. lock (unlock))
                )

                (.. ßpeer (removeBlocksDownloadedEventListener peerListener))
                (.. ßpeer (removeGetDataEventListener peerListener))
                (§ for (§ var #_"Wallet" (§ name ßwallet)) :for wallets)
                (§ block
                    (.. ßpeer (removeWallet ßwallet))
                )

                (§ let #_"int" (§ name ßfNumConnectedPeers) ßnumConnectedPeers)
                (§ block

                    (§ for (§ var #_"ListenerRegistration<BlocksDownloadedEventListener>" (§ name ßregistration)) :for peersBlocksDownloadedEventListeners)
                    (§ block
                        (.. ßpeer (removeBlocksDownloadedEventListener (.. ßregistration listener)))
                    )
                    (§ for (§ var #_"ListenerRegistration<ChainDownloadStartedEventListener>" (§ name ßregistration)) :for peersChainDownloadStartedEventListeners)
                    (§ block
                        (.. ßpeer (removeChainDownloadStartedEventListener (.. ßregistration listener)))
                    )
                    (§ for (§ var #_"ListenerRegistration<GetDataEventListener>" (§ name ßregistration)) :for peerGetDataEventListeners)
                    (§ block
                        (.. ßpeer (removeGetDataEventListener (.. ßregistration listener)))
                    )
                    (§ for (§ var #_"ListenerRegistration<PreMessageReceivedEventListener>" (§ name ßregistration)) :for peersPreMessageReceivedEventListeners)
                    (§ block
                        (.. ßpeer (removePreMessageReceivedEventListener (.. ßregistration listener)))
                    )
                    (§ for (§ var #_"ListenerRegistration<OnTransactionBroadcastListener>" (§ name ßregistration)) :for peersTransactionBroadastEventListeners)
                    (§ block
                        (.. ßpeer (removeOnTransactionBroadcastListener (.. ßregistration listener)))
                    )

                    (§ for (§ var ListenerRegistration<PeerDisconnectedEventListener> registration) :for peerDisconnectedEventListeners)
                    (§ block
                        (.. registration executor (execute (§ new #_"Runnable")
                        (§ anon
                            #_override
                            #_public
                            (§ method #_"void" (§ fn run) [])
                            (§ block
                                (.. registration listener (onPeerDisconnected ßpeer, ßfNumConnectedPeers))
                                nil
                            )
                        )))
                        (.. ßpeer (removeDisconnectedEventListener (.. registration listener)))
                    )
                    nil
                )
            )
        )
    )

    #_guarded-by(§ opt "lock")
    #_private
    (§ field- #_"int" (§ name stallPeriodSeconds) 10)
    #_guarded-by(§ opt "lock")
    #_private
    (§ field- #_"int" (§ name stallMinSpeedBytesSec) (* Block/HEADER_SIZE 20))

    ;;;
     ; Configures the stall speed: the speed at which a peer is considered to be serving us the block chain
     ; unacceptably slowly.  Once a peer has served us data slower than the given data rate for the given
     ; number of seconds, it is considered stalled and will be disconnected, forcing the chain download to continue
     ; from a different peer.  The defaults are chosen conservatively, but if you are running on a platform that is
     ; CPU constrained or on a very slow network e.g. EDGE, the default settings may need adjustment to
     ; avoid false stalls.
     ;
     ; @param periodSecs How many seconds the download speed must be below blocksPerSec, defaults to 10.
     ; @param bytesPerSecond Download speed (only blocks/txns count) must be consistently below this for a stall, defaults to the bandwidth required for 20 block headers per second.
     ;;
    #_public
    (§ method #_"void" (§ fn setStallThreshold) [#_"int" (§ name ßperiodSecs), #_"int" (§ name ßbytesPerSecond)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ ass (§ name stallPeriodSeconds) ßperiodSecs)
            (§ ass (§ name stallMinSpeedBytesSec) ßbytesPerSecond)
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        nil
    )

    #_private
    (§ class #_"ChainDownloadSpeedCalculator" (§ implements #_"BlocksDownloadedEventListener", #_"Runnable")
        #_private
        (§ field- #_"int" (§ name blocksInLastSecond))
        #_private
        (§ field- #_"int" (§ name txnsInLastSecond))
        #_private
        (§ field- #_"int" (§ name origTxnsInLastSecond))
        #_private
        (§ field- #_"long" (§ name bytesInLastSecond))

        ;; If we take more stalls than this, we assume we're on some kind of terminally slow network and the
        ;; stall threshold just isn't set properly.  We give up on stall disconnects after that.
        #_private
        (§ field- #_"int" (§ name maxStalls) 3)

        ;; How many seconds the peer has until we start measuring its speed.
        #_private
        (§ field- #_"int" (§ name warmupSeconds) -1)

        ;; Used to calculate a moving average.
        #_private
        (§ field- #_"long[]" (§ name samples))
        #_private
        (§ field- #_"int" (§ name cursor))

        #_private
        (§ field- #_"boolean" (§ name syncDone))

        #_override
        #_public
        #_synchronized
        (§ method #_"void" (§ fn onBlocksDownloaded) [#_"Peer" (§ name ßpeer), #_"Block" (§ name ßblock), #_nilable #_"FilteredBlock" (§ name ßfilteredBlock), #_"int" (§ name ßblocksLeft)])
        (§ block
            (§ ass (§ name blocksInLastSecond) (+ blocksInLastSecond 1))
            (§ ass (§ name bytesInLastSecond) (+ bytesInLastSecond Block/HEADER_SIZE))
            (§ let #_"List<Transaction>" (§ name ßblockTransactions) (.. ßblock (getTransactions)))
            (§ block
                ;; This whole area of the type hierarchy is a mess.
                (§ let #_"int" (§ name ßtxCount) (+ (if (some? ßblockTransactions) (countAndMeasureSize ßblockTransactions) 0) (if (some? ßfilteredBlock) (countAndMeasureSize (.. ßfilteredBlock (getAssociatedTransactions) (values))) 0)))
                (§ block
                    (§ ass (§ name txnsInLastSecond) (+ txnsInLastSecond ßtxCount))
                    (§ if (some? ßfilteredBlock))
                    (§ block
                        (§ ass (§ name origTxnsInLastSecond) (+ origTxnsInLastSecond (.. ßfilteredBlock (getTransactionCount))))
                    )
                    nil
                )
            )
        )

        #_private
        (§ method- #_"int" (§ fn countAndMeasureSize) [#_"Collection<Transaction>" (§ name ßtransactions)])
        (§ block
            (§ for (§ var #_"Transaction" (§ name ßtransaction)) :for ßtransactions)
            (§ block
                (§ ass (§ name bytesInLastSecond) (+ bytesInLastSecond (.. ßtransaction (getMessageSize))))
            )
            (.. ßtransactions (size))
        )

        #_override
        #_public
        (§ method #_"void" (§ fn run) [])
        (§ block
            (§ try )
            (§ block
                (calculate)
            )
            (§ catch #_"Throwable" (§ name ße))
            (§ block
                (.. log (error "Error in speed calculator", ße))
            )
            nil
        )

        #_private
        (§ method- #_"void" (§ fn calculate) [])
        (§ block
            (§ let #_"int" (§ name ßminSpeedBytesPerSec))
            (§ block
                (§ let #_"int" (§ name ßperiod))
                (§ block

                    (.. lock (lock))
                    (§ try )
                    (§ block
                        (§ ass (§ name ßminSpeedBytesPerSec) stallMinSpeedBytesSec)
                        (§ ass (§ name ßperiod) stallPeriodSeconds)
                    )
                    (§ finally )
                    (§ block
                        (.. lock (unlock))
                    )

                    (§ sync this)
                    (§ block
                        (§ if (or (nil? samples) (!= (.. samples length) ßperiod)))
                        (§ block
                            (§ ass (§ name samples) (§ new #_"long[]" (§ count ßperiod)))
                            ;; *2 because otherwise a single low sample could cause an immediate disconnect which is too harsh.
                            (Arrays/fill samples, (* ßminSpeedBytesPerSec 2))
                            (§ ass (§ name warmupSeconds) 15)
                        )

                        (§ let #_"boolean" (§ name ßbehindPeers) (and (some? chain) (< (.. chain (getBestChainHeight)) (getMostCommonChainHeight))))
                        (§ block
                            (§ if (not ßbehindPeers))
                            (§ block
                                (§ ass (§ name syncDone) true)
                            )
                            (§ if (not syncDone))
                            (§ block
                                (§ if (< warmupSeconds 0))
                                (§ block
                                    ;; Calculate the moving average.
                                    (§ ass (§ name (§ ai samples cursor)) bytesInLastSecond)
                                    (§ ass (§ name cursor) (+ cursor 1))
                                    (§ if (== cursor (.. samples length)))
                                    (§ block
                                        (§ ass (§ name cursor) 0)
                                    )
                                    (§ let #_"long" (§ name ßaverage) 0)
                                    (§ block
                                        (§ for (§ var #_"long" (§ name ßsample)) :for samples)
                                        (§ block
                                            (§ ass (§ name ßaverage) (+ ßaverage ßsample))
                                        )
                                        (§ ass (§ name ßaverage) (/ ßaverage (.. samples length)))

                                        (.. log (info (String/format Locale/US, "%d blocks/sec, %d tx/sec, %d pre-filtered tx/sec, avg/last %.2f/%.2f kilobytes per sec (stall threshold <%.2f KB/sec for %d seconds)", blocksInLastSecond, txnsInLastSecond, origTxnsInLastSecond, (/ ßaverage 1024.0), (/ bytesInLastSecond 1024.0), (/ ßminSpeedBytesPerSec 1024.0), (.. samples length))))

                                        (§ if (and (< ßaverage ßminSpeedBytesPerSec) (< 0 maxStalls)))
                                        (§ block
                                            (§ ass (§ name maxStalls) (- maxStalls 1))
                                            (§ if (== maxStalls 0))
                                            (§ block
                                                ;; We could consider starting to drop the Bloom filtering FP rate at this point, because
                                                ;; we tried a bunch of peers and no matter what we don't seem to be able to go any faster.
                                                ;; This implies we're bandwidth bottlenecked and might want to start using bandwidth
                                                ;; more effectively.  Of course if there's a MITM that is deliberately throttling us,
                                                ;; this is a good way to make us take away all the FPs from our Bloom filters ... but
                                                ;; as they don't give us a whole lot of privacy either way that's not inherently a big
                                                ;; deal.
                                                (.. log (warn "This network seems to be slower than the requested stall threshold - won't do stall disconnects any more."))
                                            )
                                            (§ else )
                                            (§ block
                                                (§ let #_"Peer" (§ name ßpeer) (getDownloadPeer))
                                                (§ block
                                                    (.. log (warn (String/format Locale/US, "Chain download stalled: received %.2f KB/sec for %d seconds, require ßaverage of %.2f KB/sec, disconnecting %s", (/ ßaverage 1024.0), (.. samples length), (/ ßminSpeedBytesPerSec 1024.0), ßpeer)))
                                                    (.. ßpeer (close))
                                                    ;; Reset the sample buffer and give the next peer time to get going.
                                                    (§ ass (§ name samples) nil)
                                                    (§ ass (§ name warmupSeconds) ßperiod)
                                                )
                                            )
                                        )
                                    )
                                )
                                (§ else )
                                (§ block
                                    (§ ass (§ name warmupSeconds) (- warmupSeconds 1))
                                    (§ if (< 0 bytesInLastSecond))
                                    (§ block
                                        (.. log (info (String/format Locale/US, "%d blocks/sec, %d tx/sec, %d pre-filtered tx/sec, last %.2f kilobytes per sec", blocksInLastSecond, txnsInLastSecond, origTxnsInLastSecond, (/ bytesInLastSecond 1024.0))))
                                    )
                                )
                            )
                            (§ ass (§ name blocksInLastSecond) 0)
                            (§ ass (§ name txnsInLastSecond) 0)
                            (§ ass (§ name origTxnsInLastSecond) 0)
                            (§ ass (§ name bytesInLastSecond) 0)
                        )
                    )
                    nil
                )
            )
        )
    )
    #_nilable
    #_private
    (§ field- #_"ChainDownloadSpeedCalculator" (§ name chainDownloadSpeedCalculator))

    #_private
    (§ method- #_"void" (§ fn startBlockChainDownloadFromPeer) [#_"Peer" (§ name ßpeer)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (setDownloadPeer ßpeer)

            (§ if (nil? chainDownloadSpeedCalculator))
            (§ block
                ;; Every second, run the calculator which will log how fast we are downloading the chain.
                (§ ass (§ name chainDownloadSpeedCalculator) (§ new #_"ChainDownloadSpeedCalculator"))
                (.. executor (scheduleAtFixedRate chainDownloadSpeedCalculator, 1, 1, TimeUnit/SECONDS))
            )
            (.. ßpeer (addBlocksDownloadedEventListener Threading/SAME_THREAD, chainDownloadSpeedCalculator))

            ;; startBlockChainDownload will setDownloadData(true) on itself automatically.
            (.. ßpeer (startBlockChainDownload))
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        nil
    )

    ;;;
     ; Returns a future that is triggered when the number of connected peers is equal to the given number of peers.
     ; By using this with {@link org.bitcoinj.core.PeerGroup#getMaxConnections()} you can wait until the
     ; network is fully online.  To block immediately, just call get() on the result.  Just calls
     ; {@link #waitForPeersOfVersion(int, long)} with zero as the protocol version.
     ;
     ; @param numPeers How many peers to wait for.
     ; @return a future that will be triggered when the number of connected peers >= numPeers.
     ;;
    #_public
    (§ method #_"ListenableFuture<List<Peer>>" (§ fn waitForPeers) [#_"int" (§ name ßnumPeers)])
    (§ block
        (waitForPeersOfVersion ßnumPeers, 0)
    )

    ;;;
     ; Returns a future that is triggered when there are at least the requested number of connected peers that support
     ; the given protocol version or higher.  To block immediately, just call get() on the result.
     ;
     ; @param numPeers How many peers to wait for.
     ; @param protocolVersion The protocol version the awaited peers must implement (or better).
     ; @return a future that will be triggered when the number of connected peers implementing protocolVersion or higher >= numPeers.
     ;;
    #_public
    (§ method #_"ListenableFuture<List<Peer>>" (§ fn waitForPeersOfVersion) [#_"int" (§ name ßnumPeers), #_"long" (§ name ßprotocolVersion)])
    (§ block
        (§ let #_"List<Peer>" (§ name ßfoundPeers) (findPeersOfAtLeastVersion ßprotocolVersion))
        (§ block
            (§ if (<= ßnumPeers (.. ßfoundPeers (size))))
            (§ block
                (§ return (Futures/immediateFuture ßfoundPeers))
            )

            (§ let #_"SettableFuture<List<Peer>>" (§ name ßfuture) (SettableFuture/create))
            (§ block
                (addConnectedEventListener (§ new #_"PeerConnectedEventListener")
                (§ anon
                    #_override
                    #_public
                    (§ method #_"void" (§ fn onPeerConnected) [#_"Peer" (§ name ßpeer), #_"int" (§ name ßpeerCount)])
                    (§ block
                        (§ let #_"List<Peer>" (§ name ßpeers) (findPeersOfAtLeastVersion ßprotocolVersion))
                        (§ block
                            (§ if (<= ßnumPeers (.. ßpeers (size))))
                            (§ block
                                (.. ßfuture (set ßpeers))
                                (removeConnectedEventListener this)
                            )
                            nil
                        )
                    )
                ))
                ßfuture
            )
        )
    )

    ;;;
     ; Returns an array list of peers that implement the given protocol version or better.
     ;;
    #_public
    (§ method #_"List<Peer>" (§ fn findPeersOfAtLeastVersion) [#_"long" (§ name ßprotocolVersion)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ let #_"ArrayList<Peer>" (§ name ßresults) (§ new #_"ArrayList<Peer>" (.. peers (size))))
            (§ block
                (§ for (§ var #_"Peer" (§ name ßpeer)) :for peers)
                (§ block
                    (§ if (<= ßprotocolVersion (.. ßpeer (getPeerVersionMessage) clientVersion)))
                    (§ block
                        (.. ßresults (add ßpeer))
                    )
                )
                (§ return ßresults)
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    ;;;
     ; Returns a future that is triggered when there are at least the requested number of connected peers that support
     ; the given protocol version or higher.  To block immediately, just call get() on the result.
     ;
     ; @param numPeers How many peers to wait for.
     ; @param mask An integer representing a bit mask that will be ANDed with the peers advertised service masks.
     ; @return a future that will be triggered when the number of connected peers implementing protocolVersion or higher >= numPeers.
     ;;
    #_public
    (§ method #_"ListenableFuture<List<Peer>>" (§ fn waitForPeersWithServiceMask) [#_"int" (§ name ßnumPeers), #_"int" (§ name ßmask)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ let #_"List<Peer>" (§ name ßfoundPeers) (findPeersWithServiceMask ßmask))
            (§ block
                (§ if (<= ßnumPeers (.. ßfoundPeers (size))))
                (§ block
                    (§ return (Futures/immediateFuture ßfoundPeers))
                )

                (§ let #_"SettableFuture<List<Peer>>" (§ name ßfuture) (SettableFuture/create))
                (§ block
                    (addConnectedEventListener (§ new #_"PeerConnectedEventListener")
                    (§ anon
                        #_override
                        #_public
                        (§ method #_"void" (§ fn onPeerConnected) [#_"Peer" (§ name ßpeer), #_"int" (§ name ßpeerCount)])
                        (§ block
                            (§ let #_"List<Peer>" (§ name ßpeers) (findPeersWithServiceMask ßmask))
                            (§ block
                                (§ if (<= ßnumPeers (.. ßpeers (size))))
                                (§ block
                                    (.. ßfuture (set ßpeers))
                                    (removeConnectedEventListener this)
                                )
                                nil
                            )
                        )
                    ))
                    (§ return ßfuture)
                )
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    ;;;
     ; Returns an array list of peers that match the requested service bit mask.
     ;;
    #_public
    (§ method #_"List<Peer>" (§ fn findPeersWithServiceMask) [#_"int" (§ name ßmask)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ let #_"ArrayList<Peer>" (§ name ßresults) (§ new #_"ArrayList<Peer>" (.. peers (size))))
            (§ block
                (§ for (§ var #_"Peer" (§ name ßpeer)) :for peers)
                (§ block
                    (§ if (== (& (.. ßpeer (getPeerVersionMessage) localServices) ßmask) ßmask))
                    (§ block
                        (.. ßresults (add ßpeer))
                    )
                )
                (§ return ßresults)
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    ;;;
     ; Returns the number of connections that are required before transactions will be broadcast.  If there aren't
     ; enough, {@link PeerGroup#broadcastTransaction(Transaction)} will wait until the minimum number is reached so
     ; propagation across the network can be observed.  If no value has been set using
     ; {@link PeerGroup#setMinBroadcastConnections(int)} a default of 80% of whatever
     ; {@link org.bitcoinj.core.PeerGroup#getMaxConnections()} returns is used.
     ;;
    #_public
    (§ method #_"int" (§ fn getMinBroadcastConnections) [])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ if (== minBroadcastConnections 0))
            (§ block
                (§ let #_"int" (§ name ßmax) (getMaxConnections))
                (§ block
                    (§ if (<= ßmax 1))
                    (§ block
                        (§ return ßmax)
                    )
                    (§ else )
                    (§ block
                        (§ return (§ cast #_"int" (Math/round (* (getMaxConnections) 0.8))))
                    )
                )
            )
            (§ return minBroadcastConnections)
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    ;;;
     ; See {@link org.bitcoinj.core.PeerGroup#getMinBroadcastConnections()}.
     ;;
    #_public
    (§ method #_"void" (§ fn setMinBroadcastConnections) [#_"int" (§ name ßvalue)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ ass (§ name minBroadcastConnections) ßvalue)
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        nil
    )

    ;;;
     ; Calls {@link PeerGroup#broadcastTransaction(Transaction, int)} with getMinBroadcastConnections() as the number
     ; of connections to wait for before commencing broadcast.
     ;;
    #_override
    #_public
    (§ method #_"TransactionBroadcast" (§ fn broadcastTransaction) [#_"Transaction" (§ name ßtx)])
    (§ block
        (broadcastTransaction ßtx, (Math/max 1, (getMinBroadcastConnections)))
    )

    ;;;
     ; <p>Given a transaction, sends it un-announced to one peer and then waits for it to be received back from
     ; other peers.  Once all connected peers have announced the transaction, the future available via the
     ; {@link org.bitcoinj.core.TransactionBroadcast#future()} method will be completed.  If anything goes
     ; wrong the exception will be thrown when get() is called, or you can receive it via a callback on the
     ; {@link ListenableFuture}.  This method returns immediately, so if you want it to block just call get() on the
     ; result.</p>
     ;
     ; <p>Note that if the PeerGroup is limited to only one connection (discovery is not activated) then the future
     ; will complete as soon as the transaction was successfully written to that peer.</p>
     ;
     ; <p>The transaction won't be sent until there are at least minConnections active connections available.
     ; A good choice for proportion would be between 0.5 and 0.8 but if you want faster transmission during initial
     ; bringup of the peer group you can lower it.</p>
     ;
     ; <p>The returned {@link org.bitcoinj.core.TransactionBroadcast} object can be used to get progress feedback,
     ; which is calculated by watching the transaction propagate across the network and be announced by peers.</p>
     ;;
    #_public
    (§ method #_"TransactionBroadcast" (§ fn broadcastTransaction) [#_"Transaction" (§ name ßtx), #_"int" (§ name ßminConnections)])
    (§ block
        ;; If we don't have a record of where this tx came from already, set it to be ourselves so Peer doesn't end up
        ;; redownloading it from the network redundantly.
        (§ if (.. ßtx (getConfidence) (getSource) (equals TransactionConfidence/Source/UNKNOWN)))
        (§ block
            (.. log (info "Transaction source unknown, setting to SELF: {}", (.. ßtx (getHashAsString))))
            (.. ßtx (getConfidence) (setSource TransactionConfidence/Source/SELF))
        )
        (§ let #_"TransactionBroadcast" (§ name ßbroadcast) (§ new #_"TransactionBroadcast" this, ßtx))
        (§ block
            (.. ßbroadcast (setMinConnections ßminConnections))
            ;; Send the TX to the wallet once we have a successful broadcast.
            (Futures/addCallback (.. ßbroadcast (future)), (§ new #_"FutureCallback<Transaction>")
            (§ anon
                #_override
                #_public
                (§ method #_"void" (§ fn onSuccess) [#_"Transaction" (§ name ßtransaction)])
                (§ block
                    (.. runningBroadcasts (remove ßbroadcast))
                    ;; OK, now tell the wallet about the transaction.  If the wallet created the transaction,
                    ;; then it already knows and will ignore this.  If it's a transaction we received from
                    ;; somebody else via a side channel and are now broadcasting, this will put it into the
                    ;; wallet now we know it's valid.
                    (§ for (§ var #_"Wallet" (§ name ßwallet)) :for wallets)
                    (§ block
                        ;; Assumption here is there are no dependencies of the created transaction.
                        ;;
                        ;; We may end up with two threads trying to do this in parallel - the wallet will
                        ;; ignore whichever one loses the race.
                        (§ try )
                        (§ block
                            (.. ßwallet (receivePending ßtransaction, nil))
                        )
                        (§ catch #_"VerificationException" (§ name ße))
                        (§ block
                            (§ throw (§ new #_"RuntimeException" ße)) ;; Cannot fail to verify a tx we created ourselves.
                        )
                    )
                    nil
                )

                #_override
                #_public
                (§ method #_"void" (§ fn onFailure) [#_"Throwable" (§ name ßthrowable)])
                (§ block
                    ;; This can happen if we get a reject message from a peer.
                    (.. runningBroadcasts (remove ßbroadcast))
                    nil
                )
            ))
            ;; Keep a reference to the TransactionBroadcast object.  This is important because otherwise, the entire tree
            ;; of objects we just created would become garbage if the user doesn't hold on to the returned future, and
            ;; eventually be collected.  This in turn could result in the transaction not being committed to the wallet
            ;; at all.
            (.. runningBroadcasts (add ßbroadcast))
            (.. ßbroadcast (broadcast))
            ßbroadcast
        )
    )

