    ;;;
     ; Returns our peers most commonly reported chain height.  If multiple heights are tied, the highest is returned.
     ; If no peers are connected, returns zero.
     ;;
    #_public
    (§ method #_"int" (§ fn getMostCommonChainHeight) [])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ return (getMostCommonChainHeight (.. this peers)))
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    ;;;
     ; Returns most commonly reported chain height from the given list of {@link Peer}s.
     ; If multiple heights are tied, the highest is returned.  If no peers are connected, returns zero.
     ;;
    #_public
    #_static
    (§ defn #_"int" (§ fn getMostCommonChainHeight) [#_"List<Peer>" (§ name ßpeers)])
    (§ block
        (§ if (.. ßpeers (isEmpty)))
        (§ block
            (§ return 0)
        )

        (§ let #_"List<Integer>" (§ name ßheights) (§ new #_"ArrayList<>" (.. ßpeers (size))))
        (§ block
            (§ for (§ var #_"Peer" (§ name ßpeer)) :for ßpeers)
            (§ block
                (.. ßheights (add (§ cast #_"int" (.. ßpeer (getBestHeight)))))
            )
            (Utils/maxOfMostFreq ßheights)
        )
    )

    ;;;
     ; Given a list of Peers, return a Peer to be used as the download peer.  If you don't want PeerGroup to manage
     ; download peer statuses for you, just override this and always return null.
     ;;
    #_nilable
    #_protected
    (§ method #_"Peer" (§ fn selectDownloadPeer) [#_"List<Peer>" (§ name ßpeers)])
    (§ block
        ;; Characteristics to select for in order of importance:
        ;;  - Chain height is reasonable (majority of nodes).
        ;;  - High enough protocol version for the features we want (but we'll settle for less).
        ;;  - Randomly, to try and spread the load.
        (§ if (.. ßpeers (isEmpty)))
        (§ block
            (§ return nil)
        )

        ;; Make sure we don't select a peer that is behind/synchronizing itself.
        (§ let #_"int" (§ name ßmostCommonChainHeight) (getMostCommonChainHeight ßpeers))
        (§ block
            (§ let #_"List<Peer>" (§ name ßcandidates) (§ new #_"ArrayList<>"))
            (§ block
                (§ for (§ var #_"Peer" (§ name ßpeer)) :for ßpeers)
                (§ block
                    (§ if (== (.. ßpeer (getBestHeight)) ßmostCommonChainHeight))
                    (§ block
                        (.. ßcandidates (add ßpeer))
                    )
                )

                ;; Of the candidates, find the peers that meet the minimum protocol version we want to target.  We could select
                ;; the highest version we've seen on the assumption that newer versions are always better but we don't want to
                ;; zap peers if they upgrade early.  If we can't find any peers that have our preferred protocol version or
                ;; better then we'll settle for the highest we found instead.
                (§ let #_"int" (§ name ßhighestVersion) 0)
                (§ block
                    (§ let #_"int" (§ name ßpreferredVersion) 0)
                    (§ block

                        ;; if/when PREFERRED_VERSION is not equal to vMinRequiredProtocolVersion, reenable the last test in PeerGroupTest.downloadPeerSelection
                        (§ let #_"int" (§ name ßPREFERRED_VERSION) (.. params (getProtocolVersionNum NetworkParameters/ProtocolVersion/BLOOM_FILTER)))
                        (§ block
                            (§ for (§ var #_"Peer" (§ name ßpeer)) :for ßcandidates)
                            (§ block
                                (§ ass (§ name ßhighestVersion) (Math/max (.. ßpeer (getPeerVersionMessage) clientVersion), ßhighestVersion))
                                (§ ass (§ name ßpreferredVersion) (Math/min ßhighestVersion, ßPREFERRED_VERSION))
                            )
                            (§ let #_"ArrayList<Peer>" (§ name ßcandidates2) (§ new #_"ArrayList<>" (.. ßcandidates (size))))
                            (§ block
                                (§ for (§ var #_"Peer" (§ name ßpeer)) :for ßcandidates)
                                (§ block
                                    (§ if (<= ßpreferredVersion (.. ßpeer (getPeerVersionMessage) clientVersion)))
                                    (§ block
                                        (.. ßcandidates2 (add ßpeer))
                                    )
                                )
                                (§ let #_"int" (§ name ßindex) (§ cast #_"int" (* (Math/random) (.. ßcandidates2 (size)))))
                                (§ block
                                    (.. ßcandidates2 (get ßindex))
                                )
                            )
                        )
                    )
                )
            )
        )
    )

