;;;
 ; Handles high-level message (de)serialization for peers, acting as the bridge between the
 ; {@link org.bitcoinj.net} classes and {@link Peer}.
 ;;
#_public
#_abstract
(§ class #_"PeerSocketHandler" (§ extends #_"AbstractTimeoutHandler") (§ implements #_"StreamConnection")
    #_private
    #_static
    (§ def- #_"Logger" (§ name log) (LoggerFactory/getLogger (§ klass #_"PeerSocketHandler")))

    #_private
    (§ field- #_"MessageSerializer" (§ name serializer))
    #_protected
    (§ field #_"PeerAddress" (§ name peerAddress))
    ;; If we close() before we know our writeTarget, set this to true to call writeTarget.closeConnection() right away.
    #_private
    (§ field- #_"boolean" (§ name closePending))
    ;; writeTarget will be thread-safe, and may call into PeerGroup, which calls us, so we should call it unlocked.
    #_testing
    #_protected
    (§ field #_"MessageWriteTarget" (§ name writeTarget))

    ;; The ByteBuffers passed to us from the writeTarget are static in size, and usually smaller than some messages we
    ;; will receive.  For SPV clients, this should be rare (i.e. we're mostly dealing with small transactions), but for
    ;; messages which are larger than the read buffer, we have to keep a temporary buffer with its bytes.
    #_private
    (§ field- #_"byte[]" (§ name largeReadBuffer))
    #_private
    (§ field- #_"int" (§ name largeReadBufferPos))
    #_private
    (§ field- #_"BitcoinSerializer.BitcoinPacketHeader" (§ name header))

    #_private
    (§ field- #_"Lock" (§ name lock) (Threading/lock "PeerSocketHandler"))

    #_public
    (§ constructor #_"PeerSocketHandler" [#_"NetworkParameters" (§ name ßparams), #_"InetSocketAddress" (§ name ßremoteIp)])
    (§ block
        (Preconditions/checkNotNull ßparams)
        (§ ass (§ name serializer) (.. ßparams (getDefaultSerializer)))
        (§ ass (§ name (.. this peerAddress)) (§ new #_"PeerAddress" ßparams, ßremoteIp))
        this
    )

    #_public
    (§ constructor #_"PeerSocketHandler" [#_"NetworkParameters" (§ name ßparams), #_"PeerAddress" (§ name ßpeerAddress)])
    (§ block
        (Preconditions/checkNotNull ßparams)
        (§ ass (§ name serializer) (.. ßparams (getDefaultSerializer)))
        (§ ass (§ name (.. this ßpeerAddress)) (Preconditions/checkNotNull ßpeerAddress))
        this
    )

    ;;;
     ; Sends the given message to the peer.  Due to the asynchronousness of network programming, there is no guarantee
     ; the peer will have received it.  Throws NotYetConnectedException if we are not yet connected to the remote peer.
     ; TODO: Maybe use something other than the unchecked NotYetConnectedException here.
     ;;
    #_public
    (§ method #_"void" (§ fn sendMessage) [#_"Message" (§ name ßmessage)])
        (§ throws #_"NotYetConnectedException")
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ if (nil? writeTarget))
            (§ block
                (§ throw (§ new #_"NotYetConnectedException"))
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        ;; TODO: Some round-tripping could be avoided here.
        (§ let #_"ByteArrayOutputStream" (§ name ßout) (§ new #_"ByteArrayOutputStream"))
        (§ block
            (§ try )
            (§ block
                (.. serializer (serialize ßmessage, ßout))
                (.. writeTarget (writeBytes (.. ßout (toByteArray))))
            )
            (§ catch #_"IOException" (§ name ße))
            (§ block
                (exceptionCaught ße)
            )
            nil
        )
    )

    ;;;
     ; Closes the connection to the peer if one exists, or immediately closes the connection as soon as it opens.
     ;;
    #_public
    (§ method #_"void" (§ fn close) [])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ if (nil? writeTarget))
            (§ block
                (§ ass (§ name closePending) true)
                (§ return nil)
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        (.. writeTarget (closeConnection))
        nil
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn timeoutOccurred) [])
    (§ block
        (.. log (info "{}: Timed out", (getAddress)))
        (close)
        nil
    )

    ;;;
     ; Called every time a message is received from the network.
     ;;
    #_protected
    #_abstract
    (§ method #_"void" (§ fn processMessage) [#_"Message" __m])
        (§ throws #_"Exception")

    #_override
    #_public
    (§ method #_"int" (§ fn receiveBytes) [#_"ByteBuffer" (§ name ßbuff)])
    (§ block
        (Preconditions/checkArgument (and (== (.. ßbuff (position)) 0) (<= (+ BitcoinSerializer/BitcoinPacketHeader/HEADER_LENGTH 4) (.. ßbuff (capacity)))))
        (§ try )
        (§ block
            ;; Repeatedly try to deserialize messages until we hit a BufferUnderflowException.
            (§ let #_"boolean" (§ name ßfirstMessage) true)
            (§ block
                (§ while true)
                (§ block
                    ;; If we are in the middle of reading a message, try to fill that one first, before we expect another.
                    (§ if (some? largeReadBuffer))
                    (§ block
                        ;; This can only happen in the first iteration.
                        (Preconditions/checkState ßfirstMessage)
                        ;; Read new bytes into the largeReadBuffer.
                        (§ let #_"int" (§ name ßbytesToGet) (Math/min (.. ßbuff (remaining)), (- (.. largeReadBuffer length) largeReadBufferPos)))
                        (§ block
                            (.. ßbuff (get largeReadBuffer, largeReadBufferPos, ßbytesToGet))
                            (§ ass (§ name largeReadBufferPos) (+ largeReadBufferPos ßbytesToGet))
                            ;; Check the largeReadBuffer's status.
                            (§ if (== largeReadBufferPos (.. largeReadBuffer length)))
                            (§ block
                                ;; ...processing a message if one is available.
                                (processMessage (.. serializer (deserializePayload header, (ByteBuffer/wrap largeReadBuffer))))
                                (§ ass (§ name largeReadBuffer) nil)
                                (§ ass (§ name header) nil)
                                (§ ass (§ name ßfirstMessage) false)
                            )
                            (§ else ) ;; ...or just returning if we don't have enough bytes yet.
                            (§ block
                                (§ return (.. ßbuff (position)))
                            )
                        )
                    )
                    ;; Now try to deserialize any messages left in buff.
                    (§ let #_"Message" (§ name ßmessage))
                    (§ block
                        (§ let #_"int" (§ name ßpreSerializePosition) (.. ßbuff (position)))
                        (§ block
                            (§ try )
                            (§ block
                                (§ ass (§ name ßmessage) (.. serializer (deserialize ßbuff)))
                            )
                            (§ catch #_"BufferUnderflowException" (§ name ße))
                            (§ block
                                ;; If we went through the whole buffer without a full message, we need to use the largeReadBuffer.
                                (§ if (and ßfirstMessage (== (.. ßbuff (limit)) (.. ßbuff (capacity)))))
                                (§ block
                                    ;; ...so reposition the buffer to 0 and read the next message header.
                                    (.. ßbuff (position 0))
                                    (§ try )
                                    (§ block
                                        (.. serializer (seekPastMagicBytes ßbuff))
                                        (§ ass (§ name header) (.. serializer (deserializeHeader ßbuff)))
                                        ;; Initialize the largeReadBuffer with the next message's size and fill it with any bytes left in buff.
                                        (§ ass (§ name largeReadBuffer) (§ new #_"byte[]" (§ count (.. header size))))
                                        (§ ass (§ name largeReadBufferPos) (.. ßbuff (remaining)))
                                        (.. ßbuff (get largeReadBuffer, 0, largeReadBufferPos))
                                    )
                                    (§ catch #_"BufferUnderflowException" (§ name ße1))
                                    (§ block
                                        ;; If we went through a whole buffer's worth of bytes without getting a header, give up.
                                        ;; In cases where the buff is just really small, we could create a second largeReadBuffer
                                        ;; that we use to deserialize the magic+header, but that is rather complicated when the buff
                                        ;; should probably be at least that big anyway (for efficiency).
                                        (§ throw (§ new #_"ProtocolException" (str "No magic bytes+header after reading " (.. ßbuff (capacity)) " bytes")))
                                    )
                                )
                                (§ else )
                                (§ block
                                    ;; Reposition the buffer to its original position, which saves us from skipping messages by
                                    ;; seeking past part of the magic bytes before all of them are in the buffer.
                                    (.. ßbuff (position ßpreSerializePosition))
                                )
                                (§ return (.. ßbuff (position)))
                            )
                            ;; Process our freshly deserialized message.
                            (processMessage ßmessage)
                            (§ ass (§ name ßfirstMessage) false)
                        )
                    )
                )
            )
        )
        (§ catch #_"Exception" (§ name ße))
        (§ block
            (exceptionCaught ße)
            ;; Returning -1 also throws an IllegalStateException upstream and kills the connection.
            (§ return -1)
        )
    )

    ;;;
     ; Sets the {@link MessageWriteTarget} used to write messages to the peer.  This should almost never be called,
     ; it is called automatically by {@link org.bitcoinj.net.NioClient} or
     ; {@link org.bitcoinj.net.NioClientManager} once the socket finishes initialization.
     ;;
    #_override
    #_public
    (§ method #_"void" (§ fn setWriteTarget) [#_"MessageWriteTarget" (§ name ßwriteTarget)])
    (§ block
        (Preconditions/checkArgument (some? ßwriteTarget))

        (.. lock (lock))
        (§ let #_"boolean" (§ name ßcloseNow) false)
        (§ block
            (§ try )
            (§ block
                (Preconditions/checkArgument (== (.. this ßwriteTarget) nil))

                (§ ass (§ name ßcloseNow) closePending)
                (§ ass (§ name (.. this ßwriteTarget)) ßwriteTarget)
            )
            (§ finally )
            (§ block
                (.. lock (unlock))
            )
            (§ if ßcloseNow)
            (§ block
                (.. ßwriteTarget (closeConnection))
            )
            nil
        )
    )

    #_override
    #_public
    (§ method #_"int" (§ fn getMaxMessageSize) [])
    (§ block
        Message/MAX_SIZE
    )

    ;;;
     ; @return the IP address and port of peer.
     ;;
    #_public
    (§ method #_"PeerAddress" (§ fn getAddress) [])
    (§ block
        peerAddress
    )

    ;;; Catch any exceptions, logging them and then closing the channel. ;;
    #_private
    (§ method- #_"void" (§ fn exceptionCaught) [#_"Exception" (§ name ße)])
    (§ block
        (§ let #_"PeerAddress" (§ name ßaddr) (getAddress))
        (§ block
            (§ let #_"String" (§ name ßs) (if (some? ßaddr) (.. ßaddr (toString)) "?"))
            (§ block
                (§ if (or (§ insta ße #_"ConnectException") (§ insta ße #_"IOException")))
                (§ block
                    ;; Short message for network errors
                    (.. log (info (str ßs " - " (.. ße (getMessage)))))
                )
                (§ else )
                (§ block
                    (.. log (warn (str ßs " - "), ße))
                    (§ let #_"Thread.UncaughtExceptionHandler" (§ name ßhandler) Threading/uncaughtExceptionHandler)
                    (§ block
                        (§ if (some? ßhandler))
                        (§ block
                            (.. ßhandler (uncaughtException (Thread/currentThread), ße))
                        )
                    )
                )

                (close)
                nil
            )
        )
    )
)

#_(ns org.bitcoinj.core #_"Ping"
