    #_private
    (§ field- #_"boolean" (§ name hasNonce))

    #_public
    (§ constructor #_"Ping" [#_"NetworkParameters" (§ name ßparams), #_"byte[]" (§ name ßpayloadBytes)])
        (§ throws #_"ProtocolException")
    (§ block
        (§ super ßparams, ßpayloadBytes, 0)
        this
    )

    ;;;
     ; Create a Ping with a nonce value.
     ; Only use this if the remote node has a protocol version > 60000.
     ;;
    #_public
    (§ constructor #_"Ping" [#_"long" (§ name ßnonce)])
    (§ block
        (§ ass (§ name (.. this ßnonce)) ßnonce)
        (§ ass (§ name (.. this hasNonce)) true)
        this
    )

    ;;;
     ; Create a Ping without a nonce value.
     ; Only use this if the remote node has a protocol version <= 60000.
     ;;
    #_public
    (§ constructor #_"Ping" [])
    (§ block
        (§ ass (§ name (.. this hasNonce)) false)
        this
    )

    #_override
    #_public
    (§ method #_"void" (§ fn bitcoinSerializeToStream) [#_"OutputStream" (§ name ßstream)])
        (§ throws #_"IOException")
    (§ block
        (§ if hasNonce)
        (§ block
            (Utils/int64ToByteStreamLE nonce, ßstream)
        )
        nil
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn parse) [])
        (§ throws #_"ProtocolException")
    (§ block
        (§ try )
        (§ block
            (§ ass (§ name nonce) (readInt64))
            (§ ass (§ name hasNonce) true)
        )
        (§ catch #_"ProtocolException" (§ name ße))
        (§ block
            (§ ass (§ name hasNonce) false)
        )
        (§ ass (§ name length) (if hasNonce 8 0))
        nil
    )

    #_public
    (§ method #_"boolean" (§ fn hasNonce) [])
    (§ block
        hasNonce
    )

    #_public
    (§ method #_"long" (§ fn getNonce) [])
    (§ block
        nonce
    )
)

