    ;;;
     ; Gets the sum of the inputs, regardless of who owns them.
     ;;
    #_public
    (§ method #_"Coin" (§ fn getInputSum) [])
    (§ block
        (§ let #_"Coin" (§ name ßinputTotal) Coin/ZERO)
        (§ block

            (§ for (§ var #_"TransactionInput" (§ name ßinput)) :for inputs)
            (§ block
                (§ let #_"Coin" (§ name ßinputValue) (.. ßinput (getValue)))
                (§ block
                    (§ if (some? ßinputValue))
                    (§ block
                        (§ ass (§ name ßinputTotal) (.. ßinputTotal (add ßinputValue)))
                    )
                )
            )

            ßinputTotal
        )
    )

    ;;;
     ; Calculates the sum of the outputs that are sending coins to a key in the wallet.
     ;;
    #_public
    (§ method #_"Coin" (§ fn getValueSentToMe) [#_"TransactionBag" (§ name ßtransactionBag)])
    (§ block
        ;; This is tested in WalletTest.
        (§ let #_"Coin" (§ name ßv) Coin/ZERO)
        (§ block
            (§ for (§ var #_"TransactionOutput" (§ name ßo)) :for outputs)
            (§ block
                (§ if (.. ßo (isMine ßtransactionBag)))
                (§ block
                    (§ ass (§ name ßv) (.. ßv (add (.. ßo (getValue)))))
                )
            )
            ßv
        )
    )

    ;;;
     ; Returns a map of block [hashes] which contain the transaction mapped to relativity counters, or null if this
     ; transaction doesn't have that data because it's not stored in the wallet or because it has never appeared in a
     ; block.
     ;;
    #_nilable
    #_public
    (§ method #_"Map<Sha256Hash, Integer>" (§ fn getAppearsInHashes) [])
    (§ block
        (when (some? appearsInHashes) (ImmutableMap/copyOf appearsInHashes))
    )

    ;;;
     ; Convenience wrapper around getConfidence().getConfidenceType().
     ; @return true if this transaction hasn't been seen in any block yet.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isPending) [])
    (§ block
        (== (.. (getConfidence) (getConfidenceType)) TransactionConfidence/ConfidenceType/PENDING)
    )

    ;;;
     ; <p>Puts the given block in the internal set of blocks in which this transaction appears.  This is
     ; used by the wallet to ensure transactions that appear on side chains are recorded properly even though
     ; the block stores do not save the transaction data at all.</p>
     ;
     ; <p>If there is a re-org this will be called once for each block that was previously seen, to update which block
     ; is the best chain.  The best chain block is guaranteed to be called last. So this must be idempotent.</p>
     ;
     ; <p>Sets updatedAt to be the earliest valid block time where this tx was seen.</p>
     ;
     ; @param block     The {@link StoredBlock} in which the transaction has appeared.
     ; @param bestChain Whether to set the updatedAt timestamp from the block header (only if not already set).
     ; @param relativityOffset A number that disambiguates the order of transactions within a block.
     ;;
    #_public
    (§ method #_"void" (§ fn setBlockAppearance) [#_"StoredBlock" (§ name ßblock), #_"boolean" (§ name ßbestChain), #_"int" (§ name ßrelativityOffset)])
    (§ block
        (§ let #_"long" (§ name ßblockTime) (* (.. ßblock (getHeader) (getTimeSeconds)) 1000))
        (§ block
            (§ if (and ßbestChain (or (nil? updatedAt) (== (.. updatedAt (getTime)) 0) (< ßblockTime (.. updatedAt (getTime))))))
            (§ block
                (§ ass (§ name updatedAt) (§ new #_"Date" ßblockTime))
            )

            (addBlockAppearance (.. ßblock (getHeader) (getHash)), ßrelativityOffset)

            (§ if ßbestChain)
            (§ block
                (§ let #_"TransactionConfidence" (§ name ßtransactionConfidence) (getConfidence))
                (§ block
                    ;; This sets type to BUILDING and depth to one.
                    (.. ßtransactionConfidence (setAppearedAtChainHeight (.. ßblock (getHeight))))
                )
            )
            nil
        )
    )

    #_public
    (§ method #_"void" (§ fn addBlockAppearance) [#_"Sha256Hash" (§ name ßblockHash), #_"int" (§ name ßrelativityOffset)])
    (§ block
        ;; TODO: This could be a lot more memory efficient as we'll typically only store one element.
        (§ if (nil? appearsInHashes))
        (§ block
            (§ ass (§ name appearsInHashes) (§ new #_"TreeMap<>"))
        )

        (.. appearsInHashes (put ßblockHash, ßrelativityOffset))
        nil
    )

    ;;;
     ; Calculates the sum of the inputs that are spending coins with keys in the wallet.  This requires the
     ; transactions sending coins to those keys to be in the wallet.  This method will not attempt to download
     ; the blocks containing the input transactions if the key is in the wallet but the transactions are not.
     ;
     ; @return sum of the inputs that are spending coins with keys in the wallet.
     ;;
    #_public
    (§ method #_"Coin" (§ fn getValueSentFromMe) [#_"TransactionBag" (§ name ßwallet)])
        (§ throws #_"ScriptException")
    (§ block
        ;; This is tested in WalletTest.
        (§ let #_"Coin" (§ name ßv) Coin/ZERO)
        (§ block
            (§ for (§ var #_"TransactionInput" (§ name ßinput)) :for inputs)
            (§ block
                ;; This input is taking value from a transaction in our wallet.  To discover the value,
                ;; we must find the connected transaction.
                (§ let #_"TransactionOutput" (§ name ßconnected) (.. ßinput (getConnectedOutput (.. ßwallet (getTransactionPool Pool/UNSPENT)))))
                (§ block
                    (§ if (nil? ßconnected))
                    (§ block
                        (§ ass (§ name ßconnected) (.. ßinput (getConnectedOutput (.. ßwallet (getTransactionPool Pool/SPENT)))))
                    )
                    (§ if (nil? ßconnected))
                    (§ block
                        (§ ass (§ name ßconnected) (.. ßinput (getConnectedOutput (.. ßwallet (getTransactionPool Pool/PENDING)))))
                    )

                    ;; The connected output may be the change to the sender of a previous input sent to this wallet.
                    ;; In this case we ignore it.
                    (§ if (and (some? ßconnected) (.. ßconnected (isMine ßwallet))))
                    (§ block
                        (§ ass (§ name ßv) (.. ßv (add (.. ßconnected (getValue)))))
                    )
                )
            )
            ßv
        )
    )

    ;;;
     ; Gets the sum of the outputs of the transaction.  If the outputs are less than the inputs, it does not count the fee.
     ; @return the sum of the outputs regardless of who owns them.
     ;;
    #_public
    (§ method #_"Coin" (§ fn getOutputSum) [])
    (§ block
        (§ let #_"Coin" (§ name ßtotalOut) Coin/ZERO)
        (§ block

            (§ for (§ var #_"TransactionOutput" (§ name ßoutput)) :for outputs)
            (§ block
                (§ ass (§ name ßtotalOut) (.. ßtotalOut (add (.. ßoutput (getValue)))))
            )

            ßtotalOut
        )
    )

    #_nilable
    #_private
    (§ field- #_"Coin" (§ name cachedValue))
    #_nilable
    #_private
    (§ field- #_"TransactionBag" (§ name cachedForBag))

    ;;;
     ; Returns the difference of {@link Transaction#getValueSentToMe(TransactionBag)} and {@link Transaction#getValueSentFromMe(TransactionBag)}.
     ;;
    #_public
    (§ method #_"Coin" (§ fn getValue) [#_"TransactionBag" (§ name ßwallet)])
        (§ throws #_"ScriptException")
    (§ block
        ;; FIXME: TEMP PERF HACK FOR ANDROID - this crap can go away once we have a real payments API.
        (§ let #_"boolean" (§ name ßisAndroid) (Utils/isAndroidRuntime))
        (§ block
            (§ if (and ßisAndroid (some? cachedValue) (== cachedForBag ßwallet)))
            (§ block
                (§ return cachedValue)
            )

            (§ let #_"Coin" (§ name ßresult) (.. (getValueSentToMe ßwallet) (subtract (getValueSentFromMe ßwallet))))
            (§ block
                (§ if ßisAndroid)
                (§ block
                    (§ ass (§ name cachedValue) ßresult)
                    (§ ass (§ name cachedForBag) ßwallet)
                )
                ßresult
            )
        )
    )

    ;;;
     ; The transaction fee is the difference of the value of all inputs and the value of all outputs.
     ; Currently, the fee can only be determined for transactions created by us.
     ;
     ; @return fee, or null if it cannot be determined.
     ;;
    #_public
    (§ method #_"Coin" (§ fn getFee) [])
    (§ block
        (§ let #_"Coin" (§ name ßfee) Coin/ZERO)
        (§ block
            (§ if (or (.. inputs (isEmpty)) (.. outputs (isEmpty)))) ;; Incomplete transaction.
            (§ block
                (§ return nil)
            )

            (§ for (§ var #_"TransactionInput" (§ name ßinput)) :for inputs)
            (§ block
                (§ if (== (.. ßinput (getValue)) nil))
                (§ block
                    (§ return nil)
                )
                (§ ass (§ name ßfee) (.. ßfee (add (.. ßinput (getValue)))))
            )
            (§ for (§ var #_"TransactionOutput" (§ name ßoutput)) :for outputs)
            (§ block
                (§ ass (§ name ßfee) (.. ßfee (subtract (.. ßoutput (getValue)))))
            )
            ßfee
        )
    )

    ;;;
     ; Returns true if any of the outputs is marked as spent.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isAnyOutputSpent) [])
    (§ block
        (§ for (§ var #_"TransactionOutput" (§ name ßoutput)) :for outputs)
        (§ block
            (§ if (not (.. ßoutput (isAvailableForSpending))))
            (§ block
                (§ return true)
            )
        )

        false
    )

    ;;;
     ; Returns false if this transaction has at least one output that is owned by the given wallet and unspent,
     ; true otherwise.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isEveryOwnedOutputSpent) [#_"TransactionBag" (§ name ßtransactionBag)])
    (§ block
        (§ for (§ var #_"TransactionOutput" (§ name ßoutput)) :for outputs)
        (§ block
            (§ if (and (.. ßoutput (isAvailableForSpending)) (.. ßoutput (isMine ßtransactionBag))))
            (§ block
                (§ return false)
            )
        )

        true
    )

    ;;;
     ; Returns the earliest time at which the transaction was seen (broadcast or included into the chain),
     ; or the epoch if that information isn't available.
