    #_public
    (§ method #_"Date" (§ fn getUpdateTime) [])
    (§ block
        ;; Older wallets did not store this field.  Set to the epoch.
        (§ if (nil? updatedAt))
        (§ block
            (§ ass (§ name updatedAt) (§ new #_"Date" 0))
        )
        updatedAt
    )

    #_public
    (§ method #_"void" (§ fn setUpdateTime) [#_"Date" (§ name ßupdatedAt)])
    (§ block
        (§ ass (§ name (.. this ßupdatedAt)) ßupdatedAt)
        nil
    )

    ;;;
     ; These constants are a part of a scriptSig signature on the inputs.  They define the details of how a
     ; transaction can be redeemed, specifically, they control how the hash of the transaction is calculated.
