        #_public
        (§ method #_"byte" (§ fn byteValue) [])
        (§ block
            (§ cast #_"byte" (.. this value))
        )
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn unCache) [])
    (§ block
        (.. super (unCache))
        (§ ass (§ name hash) nil)
        nil
    )

    #_protected
    #_static
    (§ defn #_"int" (§ fn calcLength) [#_"byte[]" (§ name ßbuf), #_"int" (§ name ßoffset)])
    (§ block
        ;; jump past version (uint32)
        (§ let #_"int" (§ name ßcursor) (+ ßoffset 4))
        (§ block

            (§ let #_"VarInt" (§ name ßvarint) (§ new #_"VarInt" ßbuf, ßcursor))
            (§ block
                (§ let #_"long" (§ name ßtxInCount) (.. ßvarint value))
                (§ block
                    (§ ass (§ name ßcursor) (+ ßcursor (.. ßvarint (getOriginalSizeInBytes))))

                    (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi ßtxInCount) :for (§ recur (+ ßi 1)))
                    (§ block
                        ;; 36 = length of previous_outpoint
                        (§ ass (§ name ßcursor) (+ ßcursor 36))
                        (§ ass (§ name ßvarint) (§ new #_"VarInt" ßbuf, ßcursor))
                        (§ let #_"long" (§ name ßscriptLen) (.. ßvarint value))
                        (§ block
                            ;; 4 = length of sequence field (unint32)
                            (§ ass (§ name ßcursor) (+ ßcursor ßscriptLen 4 (.. ßvarint (getOriginalSizeInBytes))))
                        )
                    )

                    (§ ass (§ name ßvarint) (§ new #_"VarInt" ßbuf, ßcursor))
                    (§ let #_"long" (§ name ßtxOutCount) (.. ßvarint value))
                    (§ block
                        (§ ass (§ name ßcursor) (+ ßcursor (.. ßvarint (getOriginalSizeInBytes))))

                        (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi ßtxOutCount) :for (§ recur (+ ßi 1)))
                        (§ block
                            ;; 8 = length of tx value field (uint64)
                            (§ ass (§ name ßcursor) (+ ßcursor 8))
                            (§ ass (§ name ßvarint) (§ new #_"VarInt" ßbuf, ßcursor))
                            (§ let #_"long" (§ name ßscriptLen) (.. ßvarint value))
                            (§ block
                                (§ ass (§ name ßcursor) (+ ßcursor ßscriptLen (.. ßvarint (getOriginalSizeInBytes))))
                            )
                        )
                        ;; 4 = length of lock_time field (uint32)
                        (+ (- ßcursor ßoffset) 4)
                    )
                )
            )
        )
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn parse) [])
        (§ throws #_"ProtocolException")
    (§ block
        (§ ass (§ name cursor) offset)

        (§ ass (§ name version) (readUint32))
        (§ ass (§ name optimalEncodingMessageSize) 4)

        ;; First come the inputs.
        (§ let #_"long" (§ name ßnumInputs) (readVarInt))
        (§ block
            (§ ass (§ name optimalEncodingMessageSize) (+ optimalEncodingMessageSize (VarInt/sizeOf ßnumInputs)))
            (§ ass (§ name inputs) (§ new #_"ArrayList<>" (§ cast #_"int" ßnumInputs)))
            (§ for (§ var #_"long" (§ name ßi) 0) :for (< ßi ßnumInputs) :for (§ recur (+ ßi 1)))
            (§ block
                (§ let #_"TransactionInput" (§ name ßinput) (§ new #_"TransactionInput" params, this, payload, cursor, serializer))
                (§ block
                    (.. inputs (add ßinput))
                    (§ let #_"long" (§ name ßscriptLen) (readVarInt TransactionOutPoint/MESSAGE_LENGTH))
                    (§ block
                        (§ ass (§ name optimalEncodingMessageSize) (+ optimalEncodingMessageSize TransactionOutPoint/MESSAGE_LENGTH (VarInt/sizeOf ßscriptLen) ßscriptLen 4))
                        (§ ass (§ name cursor) (+ cursor ßscriptLen 4))
                    )
                )
            )
            ;; Now the outputs.
            (§ let #_"long" (§ name ßnumOutputs) (readVarInt))
            (§ block
                (§ ass (§ name optimalEncodingMessageSize) (+ optimalEncodingMessageSize (VarInt/sizeOf ßnumOutputs)))
                (§ ass (§ name outputs) (§ new #_"ArrayList<>" (§ cast #_"int" ßnumOutputs)))
                (§ for (§ var #_"long" (§ name ßi) 0) :for (< ßi ßnumOutputs) :for (§ recur (+ ßi 1)))
                (§ block
                    (§ let #_"TransactionOutput" (§ name ßoutput) (§ new #_"TransactionOutput" params, this, payload, cursor, serializer))
                    (§ block
                        (.. outputs (add ßoutput))
                        (§ let #_"long" (§ name ßscriptLen) (readVarInt 8))
                        (§ block
                            (§ ass (§ name optimalEncodingMessageSize) (+ optimalEncodingMessageSize 8 (VarInt/sizeOf ßscriptLen) ßscriptLen))
                            (§ ass (§ name cursor) (+ cursor ßscriptLen))
                        )
                    )
                )
                (§ ass (§ name lockTime) (readUint32))
                (§ ass (§ name optimalEncodingMessageSize) (+ optimalEncodingMessageSize 4))
                (§ ass (§ name length) (- cursor offset))
                nil
            )
        )
    )

    #_public
    (§ method #_"int" (§ fn getOptimalEncodingMessageSize) [])
    (§ block
        (§ if (!= optimalEncodingMessageSize 0))
        (§ block
            (§ return optimalEncodingMessageSize)
        )

        (§ ass (§ name optimalEncodingMessageSize) (getMessageSize))
        optimalEncodingMessageSize
    )

    ;;;
     ; The priority (coin age) calculation doesn't use the regular message size, but rather one adjusted downwards
     ; for the number of inputs.  The goal is to incentivise cleaning up the UTXO set with free transactions, if one
     ; can do so.
     ;;
    #_public
    (§ method #_"int" (§ fn getMessageSizeForPriorityCalc) [])
    (§ block
        (§ let #_"int" (§ name ßsize) (getMessageSize))
        (§ block
            (§ for (§ var #_"TransactionInput" (§ name ßinput)) :for inputs)
            (§ block
                ;; 41: min size of an input
                ;; 110: enough to cover a compressed pubkey p2sh redemption (somewhat arbitrary)
                (§ let #_"int" (§ name ßbenefit) (+ 41 (Math/min 110, (.. ßinput (getScriptSig) (getProgram) length))))
                (§ block
                    (§ if (< ßbenefit ßsize))
                    (§ block
                        (§ ass (§ name ßsize) (- ßsize ßbenefit))
                    )
                )
            )
            ßsize
        )
    )

    ;;;
     ; A coinbase transaction is one that creates a new coin.  They are the first transaction in each block and their
     ; value is determined by a formula that all implementations of Bitcoin share.  In 2011 the value of a coinbase
     ; transaction is 50 coins, but in future it will be less.  A coinbase transaction is defined not only by its
     ; position in a block but by the data in the inputs.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isCoinBase) [])
    (§ block
        (and (== (.. inputs (size)) 1) (.. inputs (get 0) (isCoinBase)))
    )

    ;;;
     ; A transaction is mature if it is either a building coinbase tx that is as deep or deeper than the required coinbase depth, or a non-coinbase tx.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isMature) [])
    (§ block
        (§ if (not (isCoinBase)))
        (§ block
            (§ return true)
        )

        (§ if (!= (.. (getConfidence) (getConfidenceType)) ConfidenceType/BUILDING))
        (§ block
            (§ return false)
        )

        (<= (.. params (getSpendableCoinbaseDepth)) (.. (getConfidence) (getDepthInBlocks)))
    )

