    ;;;
     ; A human readable version of the transaction useful for debugging.  The format is not guaranteed to be stable.
     ; @param chain If provided, will be used to estimate lock times (if set).  Can be null.
     ;;
    #_public
    (§ method #_"String" (§ fn toString) [#_nilable #_"AbstractBlockChain" (§ name ßchain)])
    (§ block
        (§ let #_"StringBuilder" (§ name ßsb) (§ new #_"StringBuilder"))
        (§ block
            (.. ßsb (append "  ") (append (getHashAsString)) (append "\n"))
            (§ if (some? updatedAt))
            (§ block
                (.. ßsb (append "  updated: ") (append (Utils/dateTimeFormat updatedAt)) (append "\n"))
            )
            (§ if (!= version 1))
            (§ block
                (.. ßsb (append "  version ") (append version) (append "\n"))
            )
            (§ if (isTimeLocked))
            (§ block
                (.. ßsb (append "  time locked until "))
                (§ if (< lockTime LOCKTIME_THRESHOLD))
                (§ block
                    (.. ßsb (append "block ") (append lockTime))
                    (§ if (some? ßchain))
                    (§ block
                        (.. ßsb (append " (estimated to be reached at ") (append (Utils/dateTimeFormat (.. ßchain (estimateBlockTime (§ cast #_"int" lockTime))))) (append ")"))
                    )
                )
                (§ else )
                (§ block
                    (.. ßsb (append (Utils/dateTimeFormat (* lockTime 1000))))
                )
                (.. ßsb (append "\n"))
            )
            (§ if (isOptInFullRBF))
            (§ block
                (.. ßsb (append "  opts into full replace-by-fee\n"))
            )
            (§ if (isCoinBase))
            (§ block
                (§ let #_"String" (§ name ßscript))
                (§ block
                    (§ let #_"String" (§ name ßscript2))
                    (§ block
                        (§ try )
                        (§ block
                            (§ ass (§ name ßscript) (.. inputs (get 0) (getScriptSig) (toString)))
                            (§ ass (§ name ßscript2) (.. outputs (get 0) (getScriptPubKey) (toString)))
                        )
                        (§ catch #_"ScriptException" (§ name _))
                        (§ block
                            (§ ass (§ name ßscript) "???")
                            (§ ass (§ name ßscript2) "???")
                        )
                        (.. ßsb (append "     == COINBASE TXN (scriptSig ") (append ßscript) (append ")  (scriptPubKey ") (append ßscript2) (append ")\n"))
                        (§ return (.. ßsb (toString)))
                    )
                )
            )

            (§ if (not (.. inputs (isEmpty))))
            (§ block
                (§ for (§ var #_"TransactionInput" (§ name ßin)) :for inputs)
                (§ block
                    (.. ßsb (append "     ßin   "))

                    (§ try )
                    (§ block
                        (§ let #_"String" (§ name ßscriptSigStr) (.. ßin (getScriptSig) (toString)))
                        (§ block
                            (.. ßsb (append (if (not (Strings/isNullOrEmpty ßscriptSigStr)) ßscriptSigStr "<no scriptSig>")))
                            (§ let #_"Coin" (§ name ßvalue) (.. ßin (getValue)))
                            (§ block
                                (§ if (some? ßvalue))
                                (§ block
                                    (.. ßsb (append " ") (append (.. ßvalue (toFriendlyString))))
                                )
                                (.. ßsb (append "\n          outpoint:"))
                                (§ let #_"TransactionOutPoint" (§ name ßoutpoint) (.. ßin (getOutpoint)))
                                (§ block
                                    (.. ßsb (append (.. ßoutpoint (toString))))
                                    (§ let #_"TransactionOutput" (§ name ßconnectedOutput) (.. ßoutpoint (getConnectedOutput)))
                                    (§ block
                                        (§ if (some? ßconnectedOutput))
                                        (§ block
                                            (§ let #_"Script" (§ name ßscriptPubKey) (.. ßconnectedOutput (getScriptPubKey)))
                                            (§ block
                                                (§ if (or (.. ßscriptPubKey (isSentToAddress)) (.. ßscriptPubKey (isPayToScriptHash))))
                                                (§ block
                                                    (.. ßsb (append " hash160:") (append (.. Utils/HEX (encode (.. ßscriptPubKey (getPubKeyHash))))))
                                                )
                                            )
                                        )
                                        (§ if (.. ßin (hasSequence)))
                                        (§ block
                                            (.. ßsb (append "\n          sequence:") (append (Long/toHexString (.. ßin (getSequenceNumber)))))
                                            (§ if (.. ßin (isOptInFullRBF)))
                                            (§ block
                                                (.. ßsb (append ", opts into full RBF"))
                                            )
                                        )
                                    )
                                )
                            )
                        )
                    )
                    (§ catch #_"Exception" (§ name ße))
                    (§ block
                        (.. ßsb (append "[exception: ") (append (.. ße (getMessage))) (append "]"))
                    )
                    (.. ßsb (append "\n"))
                )
            )
            (§ else )
            (§ block
                (.. ßsb (append "     INCOMPLETE: No inputs!\n"))
            )

            (§ for (§ var #_"TransactionOutput" (§ name ßout)) :for outputs)
            (§ block
                (.. ßsb (append "     ßout  "))
                (§ try )
                (§ block
                    (§ let #_"String" (§ name ßscriptPubKeyStr) (.. ßout (getScriptPubKey) (toString)))
                    (§ block
                        (.. ßsb (append (if (not (Strings/isNullOrEmpty ßscriptPubKeyStr)) ßscriptPubKeyStr "<no scriptPubKey>")) (append " ") (append (.. ßout (getValue) (toFriendlyString))))
                        (§ if (not (.. ßout (isAvailableForSpending))))
                        (§ block
                            (.. ßsb (append " Spent"))
                        )
                        (§ let #_"TransactionInput" (§ name ßspentBy) (.. ßout (getSpentBy)))
                        (§ block
                            (§ if (some? ßspentBy))
                            (§ block
                                (.. ßsb (append " by ") (append (.. ßspentBy (getParentTransaction) (getHashAsString))))
                            )
                        )
                    )
                )
                (§ catch #_"Exception" (§ name ße))
                (§ block
                    (.. ßsb (append "[exception: ") (append (.. ße (getMessage))) (append "]"))
                )
                (.. ßsb (append "\n"))
            )

            (§ let #_"Coin" (§ name ßfee) (getFee))
            (§ block
                (§ if (some? ßfee))
                (§ block
                    (§ let #_"int" (§ name ßsize) (.. (unsafeBitcoinSerialize) length))
                    (§ block
                        (.. ßsb (append "     ßfee  ") (append (.. ßfee (multiply 1000) (divide ßsize) (toFriendlyString))) (append "/kB, ") (append (.. ßfee (toFriendlyString))) (append " for ") (append ßsize) (append " bytes\n"))
                    )
                )
                (§ if (some? purpose))
                (§ block
                    (.. ßsb (append "     prps ") (append purpose) (append "\n"))
                )
                (.. ßsb (toString))
            )
        )
    )

