    #_public
    (§ method #_"TransactionInput" (§ fn addSignedInput) [#_"TransactionOutPoint" (§ name ßprevOut), #_"Script" (§ name ßscriptPubKey), #_"ECKey" (§ name ßsigKey), #_"SigHash" (§ name ßsigHash), #_"boolean" (§ name ßanyoneCanPay)])
        (§ throws #_"ScriptException")
