        ;; Verify the API user didn't try to do operations out of order.
        (Preconditions/checkState (not (.. outputs (isEmpty))), "Attempting to sign tx without outputs.")

        (§ let #_"TransactionInput" (§ name ßinput) (§ new #_"TransactionInput" params, this, (§ new #_"byte[]"), ßprevOut))
        (§ block
            (addInput ßinput)
            (§ let #_"Sha256Hash" (§ name ßhash) (hashForSignature (- (.. inputs (size)) 1), ßscriptPubKey, ßsigHash, ßanyoneCanPay))
            (§ block
                (§ let #_"ECKey.ECDSASignature" (§ name ßecSig) (.. ßsigKey (sign ßhash)))
                (§ block
                    (§ let #_"TransactionSignature" (§ name ßtxSig) (§ new #_"TransactionSignature" ßecSig, ßsigHash, ßanyoneCanPay))
