                            (.. ßinput (setScriptSig (ScriptBuilder/createInputScript ßtxSig)))
                        )
                        (§ elseif (.. ßscriptPubKey (isSentToAddress)))
                        (§ block
                            (.. ßinput (setScriptSig (ScriptBuilder/createInputScript ßtxSig, ßsigKey)))
                        )
                        (§ else )
                        (§ block
                            (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_UNKNOWN_ERROR, (str "Don't know how to sign for this kind of scriptPubKey: " ßscriptPubKey)))
                        )
                        ßinput
                    )
                )
            )
        )
    )

