     ; @return linked list of outputs relevant to the wallet in this transaction.
     ;;
    #_public
    (§ method #_"List<TransactionOutput>" (§ fn getWalletOutputs) [#_"TransactionBag" (§ name ßtransactionBag)])
    (§ block
        (§ let #_"List<TransactionOutput>" (§ name ßwalletOutputs) (§ new #_"LinkedList<>"))
        (§ block

            (§ for (§ var #_"TransactionOutput" (§ name ßo)) :for outputs)
            (§ block
                (§ if (.. ßo (isMine ßtransactionBag)))
                (§ block
                    (.. ßwalletOutputs (add ßo))
                )
            )

            ßwalletOutputs
        )
    )

