    #_public
    (§ method #_"int" (§ fn getSigOpCount) [])
        (§ throws #_"ScriptException")
    (§ block
        (§ let #_"int" (§ name ßsigOps) 0)
        (§ block
            (§ for (§ var #_"TransactionInput" (§ name ßinput)) :for inputs)
            (§ block
                (§ ass (§ name ßsigOps) (+ ßsigOps (Script/getSigOpCount (.. ßinput (getScriptBytes)))))
            )
            (§ for (§ var #_"TransactionOutput" (§ name ßoutput)) :for outputs)
            (§ block
                (§ ass (§ name ßsigOps) (+ ßsigOps (Script/getSigOpCount (.. ßoutput (getScriptBytes)))))
            )
