#_(ns org.bitcoinj.core #_"UnsafeByteArrayOutputStream"
    (:import [java.io ByteArrayOutputStream IOException OutputStream]))

;;;
 ; An unsynchronized implementation of ByteArrayOutputStream that will return the backing byte array
 ; if its length == size().  This avoids unneeded array copy where the BOS is simply being used to
 ; extract a byte array of known length from a 'serialized to stream' method.
 ;
 ; Unless the final length can be accurately predicted the only performance this will yield is due
 ; to unsynchronized methods.
 ;
 ; @author git
 ;;
#_public
(§ class #_"UnsafeByteArrayOutputStream" (§ extends #_"ByteArrayOutputStream")
    #_public
    (§ constructor #_"UnsafeByteArrayOutputStream" [])
    (§ block
        (§ super 32)
        this
    )

    #_public
    (§ constructor #_"UnsafeByteArrayOutputStream" [#_"int" (§ name ßsize)])
    (§ block
        (§ super ßsize)
        this
    )

    ;;;
     ; Writes the specified byte to this byte array output stream.
     ;
     ; @param b The byte to be written.
     ;;
    #_override
    #_public
    (§ method #_"void" (§ fn write) [#_"int" (§ name ßb)])
    (§ block
        (§ let #_"int" (§ name ßn) (+ count 1))
        (§ block
            (§ if (< (.. buf length) ßn))
            (§ block
                (§ ass (§ name buf) (Utils/copyOf buf, (Math/max (<< (.. buf length) 1), ßn)))
            )
            (§ ass (§ name (§ ai buf count)) (§ cast #_"byte" ßb))
            (§ ass (§ name count) ßn)
            nil
        )
    )

    ;;;
     ; Writes <code>len</code> bytes from the specified byte array
     ; starting at offset <code>off</code> to this byte array output stream.
     ;
     ; @param b   The data.
     ; @param off The start offset in the data.
     ; @param len The number of bytes to write.
     ;;
    #_override
    #_public
    (§ method #_"void" (§ fn write) [#_"byte[]" (§ name ßb), #_"int" (§ name ßoff), #_"int" (§ name ßlen)])
    (§ block
        (§ if (or (< ßoff 0) (< (.. ßb length) ßoff) (< ßlen 0) (< (.. ßb length) (+ ßoff ßlen)) (< (+ ßoff ßlen) 0)))
        (§ block
            (§ throw (§ new #_"IndexOutOfBoundsException"))
        )

        (§ if (!= ßlen 0))
        (§ block
            (§ let #_"int" (§ name ßn) (+ count ßlen))
            (§ block
                (§ if (< (.. buf length) ßn))
                (§ block
                    (§ ass (§ name buf) (Utils/copyOf buf, (Math/max (<< (.. buf length) 1), ßn)))
                )
                (System/arraycopy ßb, ßoff, buf, count, ßlen)
                (§ ass (§ name count) ßn)
            )
        )
        nil
    )

    ;;;
     ; Writes the complete contents of this byte array output stream to
     ; the specified output stream argument, as if by calling the output
     ; stream's write method using <code>out.write(buf, 0, count)</code>.
     ;
     ; @param out The output stream to which to write the data.
     ; @throws IOException if an I/O error occurs.
     ;;
    #_override
    #_public
    (§ method #_"void" (§ fn writeTo) [#_"OutputStream" (§ name ßout)])
        (§ throws #_"IOException")
    (§ block
        (.. ßout (write buf, 0, count))
        nil
    )

    ;;;
     ; Resets the <code>count</code> field of this byte array output
     ; stream to zero, so that all currently accumulated output in the
     ; output stream is discarded.  The output stream can be used again,
     ; reusing the already allocated buffer space.
     ;
     ; @see java.io.ByteArrayInputStream#count
     ;;
    #_override
    #_public
    (§ method #_"void" (§ fn reset) [])
    (§ block
        (§ ass (§ name count) 0)
        nil
    )

    ;;;
     ; Creates a newly allocated byte array.  Its size is the current
     ; size of this output stream and the valid contents of the buffer
     ; have been copied into it.
     ;
     ; @return the current contents of this output stream, as a byte array.
     ; @see java.io.ByteArrayOutputStream#size()
     ;;
    #_override
    #_public
    (§ method #_"byte[]" (§ fn toByteArray) [])
    (§ block
        (if (== count (.. buf length)) buf (Utils/copyOf buf, count))
    )

    ;;;
     ; Returns the current size of the buffer.
     ;
     ; @return the value of the <code>count</code> field, which is the number
     ;         of valid bytes in this output stream.
     ; @see java.io.ByteArrayOutputStream#count
     ;;
    #_override
    #_public
    (§ method #_"int" (§ fn size) [])
    (§ block
        count
    )
)

#_(ns org.bitcoinj.core #_"Utils"
    (:import [java.io ByteArrayOutputStream IOException InputStream OutputStream UnsupportedEncodingException]
             [java.math BigInteger]
             [java.net URL]
