;;;
 ; A collection of various utility methods that are helpful for working with the Bitcoin protocol.
 ; To enable debug logging from the library, run with -Dbitcoinj.logging=true on your command line.
 ;;
#_public
(§ class #_"Utils"
    ;;; The string that prefixes all text messages signed using Bitcoin keys. ;;
    #_public
    #_static
    (§ def #_"String" (§ name BITCOIN_SIGNED_MESSAGE_HEADER) "Bitcoin Signed Message:\n")
    #_public
    #_static
    (§ def #_"byte[]" (§ name BITCOIN_SIGNED_MESSAGE_HEADER_BYTES) (.. BITCOIN_SIGNED_MESSAGE_HEADER (getBytes Charsets/UTF_8)))

    #_public
    #_static
    (§ def #_"Joiner" (§ name SPACE_JOINER) (Joiner/on " "))

    #_private
    #_static
    (§ def- #_"BlockingQueue<Boolean>" (§ name mockSleepQueue))

    ;;;
     ; The regular {@link java.math.BigInteger#toByteArray()} includes the sign bit of the number and
     ; might result in an extra byte addition.  This method removes this extra byte.
     ;
     ; Assuming only positive numbers, it's possible to discriminate if an extra byte
     ; is added by checking if the first element of the array is 0 (0000_0000).
     ; Due to the minimal representation provided by BigInteger, it means that the bit sign
     ; is the least significant bit 0000_000<b>0</b>.  Otherwise the representation is not minimal.
     ; For example, if the sign bit is 0000_00<b>0</b>0, then the representation is not minimal due to the rightmost zero.
     ;
     ; @param b The integer to format into a byte array.
     ; @param numBytes The desired size of the resulting byte array.
     ; @return numBytes byte long array.
     ;;
    #_public
    #_static
    (§ defn #_"byte[]" (§ fn bigIntegerToBytes) [#_"BigInteger" (§ name ßb), #_"int" (§ name ßnumBytes)])
    (§ block
        (Preconditions/checkArgument (<= 0 (.. ßb (signum))), "b must be positive or zero")
        (Preconditions/checkArgument (< 0 ßnumBytes), "numBytes must be positive")

        (§ let #_"byte[]" (§ name ßsrc) (.. ßb (toByteArray)))
        (§ block
            (§ let #_"byte[]" (§ name ßdest) (§ new #_"byte[]" (§ count ßnumBytes)))
            (§ block
                (§ let #_"boolean" (§ name ßisFirstByteOnlyForSign) (== (§ ai ßsrc 0) 0))
                (§ block
                    (§ let #_"int" (§ name ßlength) (if ßisFirstByteOnlyForSign (- (.. ßsrc length) 1) (.. ßsrc length)))
                    (§ block

                        (Preconditions/checkArgument (<= ßlength ßnumBytes), (str "The given number does not fit in " ßnumBytes))

                        (§ let #_"int" (§ name ßsrcPos) (if ßisFirstByteOnlyForSign 1 0))
                        (§ block
                            (§ let #_"int" (§ name ßdestPos) (- ßnumBytes ßlength))
                            (§ block
                                (System/arraycopy ßsrc, ßsrcPos, ßdest, ßdestPos, ßlength)
                                ßdest
                            )
                        )
                    )
                )
            )
        )
    )

    #_public
    #_static
    (§ defn #_"void" (§ fn uint32ToByteArrayBE) [#_"long" (§ name ßval), #_"byte[]" (§ name ßout), #_"int" (§ name ßoffset)])
    (§ block
        (§ ass (§ name (§ ai ßout ßoffset)) (§ cast #_"byte" (& 0xff (>> ßval 24))))
        (§ ass (§ name (§ ai ßout (+ ßoffset 1))) (§ cast #_"byte" (& 0xff (>> ßval 16))))
        (§ ass (§ name (§ ai ßout (+ ßoffset 2))) (§ cast #_"byte" (& 0xff (>> ßval 8))))
        (§ ass (§ name (§ ai ßout (+ ßoffset 3))) (§ cast #_"byte" (& 0xff ßval)))
        nil
    )

    #_public
    #_static
    (§ defn #_"void" (§ fn uint32ToByteArrayLE) [#_"long" (§ name ßval), #_"byte[]" (§ name ßout), #_"int" (§ name ßoffset)])
    (§ block
        (§ ass (§ name (§ ai ßout ßoffset)) (§ cast #_"byte" (& 0xff ßval)))
        (§ ass (§ name (§ ai ßout (+ ßoffset 1))) (§ cast #_"byte" (& 0xff (>> ßval 8))))
        (§ ass (§ name (§ ai ßout (+ ßoffset 2))) (§ cast #_"byte" (& 0xff (>> ßval 16))))
        (§ ass (§ name (§ ai ßout (+ ßoffset 3))) (§ cast #_"byte" (& 0xff (>> ßval 24))))
        nil
    )

    #_public
    #_static
    (§ defn #_"void" (§ fn uint64ToByteArrayLE) [#_"long" (§ name ßval), #_"byte[]" (§ name ßout), #_"int" (§ name ßoffset)])
    (§ block
        (§ ass (§ name (§ ai ßout ßoffset)) (§ cast #_"byte" (& 0xff ßval)))
        (§ ass (§ name (§ ai ßout (+ ßoffset 1))) (§ cast #_"byte" (& 0xff (>> ßval 8))))
        (§ ass (§ name (§ ai ßout (+ ßoffset 2))) (§ cast #_"byte" (& 0xff (>> ßval 16))))
        (§ ass (§ name (§ ai ßout (+ ßoffset 3))) (§ cast #_"byte" (& 0xff (>> ßval 24))))
        (§ ass (§ name (§ ai ßout (+ ßoffset 4))) (§ cast #_"byte" (& 0xff (>> ßval 32))))
        (§ ass (§ name (§ ai ßout (+ ßoffset 5))) (§ cast #_"byte" (& 0xff (>> ßval 40))))
        (§ ass (§ name (§ ai ßout (+ ßoffset 6))) (§ cast #_"byte" (& 0xff (>> ßval 48))))
        (§ ass (§ name (§ ai ßout (+ ßoffset 7))) (§ cast #_"byte" (& 0xff (>> ßval 56))))
        nil
    )

    #_public
    #_static
    (§ defn #_"void" (§ fn uint32ToByteStreamLE) [#_"long" (§ name ßval), #_"OutputStream" (§ name ßstream)])
        (§ throws #_"IOException")
    (§ block
        (.. ßstream (write (§ cast #_"int" (& 0xff ßval))))
        (.. ßstream (write (§ cast #_"int" (& 0xff (>> ßval 8)))))
        (.. ßstream (write (§ cast #_"int" (& 0xff (>> ßval 16)))))
        (.. ßstream (write (§ cast #_"int" (& 0xff (>> ßval 24)))))
        nil
    )

    #_public
    #_static
    (§ defn #_"void" (§ fn int64ToByteStreamLE) [#_"long" (§ name ßval), #_"OutputStream" (§ name ßstream)])
        (§ throws #_"IOException")
    (§ block
        (.. ßstream (write (§ cast #_"int" (& 0xff ßval))))
        (.. ßstream (write (§ cast #_"int" (& 0xff (>> ßval 8)))))
        (.. ßstream (write (§ cast #_"int" (& 0xff (>> ßval 16)))))
        (.. ßstream (write (§ cast #_"int" (& 0xff (>> ßval 24)))))
        (.. ßstream (write (§ cast #_"int" (& 0xff (>> ßval 32)))))
        (.. ßstream (write (§ cast #_"int" (& 0xff (>> ßval 40)))))
        (.. ßstream (write (§ cast #_"int" (& 0xff (>> ßval 48)))))
        (.. ßstream (write (§ cast #_"int" (& 0xff (>> ßval 56)))))
        nil
    )

    #_public
    #_static
    (§ defn #_"void" (§ fn uint64ToByteStreamLE) [#_"BigInteger" (§ name ßval), #_"OutputStream" (§ name ßstream)])
        (§ throws #_"IOException")
    (§ block
        (§ let #_"byte[]" (§ name ßbytes) (.. ßval (toByteArray)))
        (§ block
            (§ if (< 8 (.. ßbytes length)))
            (§ block
                (§ throw (§ new #_"RuntimeException" "Input too large to encode into a uint64"))
            )

            (§ ass (§ name ßbytes) (reverseBytes ßbytes))
            (.. ßstream (write ßbytes))
            (§ if (< (.. ßbytes length) 8))
            (§ block
                (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi (- 8 (.. ßbytes length))) :for (§ recur (+ ßi 1)))
                (§ block
                    (.. ßstream (write 0))
                )
            )
            nil
        )
    )

    ;;;
     ; Work around lack of unsigned types in Java.
     ;;
    #_public
    #_static
    (§ defn #_"boolean" (§ fn isLessThanUnsigned) [#_"long" (§ name ßn1), #_"long" (§ name ßn2)])
    (§ block
        (< (UnsignedLongs/compare ßn1, ßn2) 0)
    )

    ;;;
     ; Work around lack of unsigned types in Java.
     ;;
    #_public
    #_static
    (§ defn #_"boolean" (§ fn isLessThanOrEqualToUnsigned) [#_"long" (§ name ßn1), #_"long" (§ name ßn2)])
    (§ block
        (<= (UnsignedLongs/compare ßn1, ßn2) 0)
    )

    ;;;
     ; Hex encoding used throughout the framework.  Use with HEX.encode(byte[]) or HEX.decode(CharSequence).
     ;;
    #_public
    #_static
    (§ def #_"BaseEncoding" (§ name HEX) (.. (BaseEncoding/base16) (lowerCase)))

    ;;;
     ; Returns a copy of the given byte array in reverse order.
     ;;
    #_public
    #_static
    (§ defn #_"byte[]" (§ fn reverseBytes) [#_"byte[]" (§ name ßbytes)])
    (§ block
        ;; We could use the XOR trick here, but it's easier to understand if we don't.
        ;; If we find this is really a performance issue, the matter can be revisited.
        (§ let #_"byte[]" (§ name ßbuf) (§ new #_"byte[]" (§ count (.. ßbytes length))))
        (§ block
            (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi (.. ßbytes length)) :for (§ recur (+ ßi 1)))
            (§ block
                (§ ass (§ name (§ ai ßbuf ßi)) (§ ai ßbytes (- (.. ßbytes length) 1 ßi)))
            )
            ßbuf
        )
    )

    ;;;
     ; Returns a copy of the given byte array with the bytes of each double-word (4 bytes) reversed.
     ;
     ; @param bytes Length must be divisible by 4.
     ; @param trimLength Trim output to this length.  If positive, must be divisible by 4.
     ;;
    #_public
    #_static
    (§ defn #_"byte[]" (§ fn reverseDwordBytes) [#_"byte[]" (§ name ßbytes), #_"int" (§ name ßtrimLength)])
    (§ block
        (Preconditions/checkArgument (== (% (.. ßbytes length) 4) 0))
        (Preconditions/checkArgument (or (< ßtrimLength 0) (== (% ßtrimLength 4) 0)))

        (§ let #_"byte[]" (§ name ßrev) (§ new #_"byte[]" (§ count (if (< -1 ßtrimLength (.. ßbytes length)) ßtrimLength (.. ßbytes length)))))
        (§ block

            (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi (.. ßrev length)) :for (§ recur (+ ßi 4)))
            (§ block
                (System/arraycopy ßbytes, ßi, ßrev, ßi, 4)
                (§ for (§ var #_"int" (§ name ßj) 0) :for (< ßj 4) :for (§ recur (+ ßj 1)))
                (§ block
                    (§ ass (§ name (§ ai ßrev (+ ßi ßj))) (§ ai ßbytes (- (+ ßi 3) ßj)))
                )
            )
            ßrev
        )
    )

    ;;; Parse 4 bytes from the byte array (starting at the offset) as unsigned 32-bit integer in little endian format. ;;
    #_public
    #_static
    (§ defn #_"long" (§ fn readUint32) [#_"byte[]" (§ name ßbytes), #_"int" (§ name ßoffset)])
    (§ block
        (| (& 0xff (§ ai ßbytes ßoffset)) (<< (& 0xff (§ ai ßbytes (+ ßoffset 1))) 8) (<< (& 0xff (§ ai ßbytes (+ ßoffset 2))) 16) (<< (& 0xff (§ ai ßbytes (+ ßoffset 3))) 24))
    )

    ;;; Parse 8 bytes from the byte array (starting at the offset) as signed 64-bit integer in little endian format. ;;
    #_public
    #_static
    (§ defn #_"long" (§ fn readInt64) [#_"byte[]" (§ name ßbytes), #_"int" (§ name ßoffset)])
    (§ block
        (| (& 0xff (§ ai ßbytes ßoffset)) (<< (& 0xff (§ ai ßbytes (+ ßoffset 1))) 8) (<< (& 0xff (§ ai ßbytes (+ ßoffset 2))) 16) (<< (& 0xff (§ ai ßbytes (+ ßoffset 3))) 24) (<< (& 0xff (§ ai ßbytes (+ ßoffset 4))) 32) (<< (& 0xff (§ ai ßbytes (+ ßoffset 5))) 40) (<< (& 0xff (§ ai ßbytes (+ ßoffset 6))) 48) (<< (& 0xff (§ ai ßbytes (+ ßoffset 7))) 56))
    )

    ;;; Parse 4 bytes from the byte array (starting at the offset) as unsigned 32-bit integer in big endian format. ;;
    #_public
    #_static
    (§ defn #_"long" (§ fn readUint32BE) [#_"byte[]" (§ name ßbytes), #_"int" (§ name ßoffset)])
    (§ block
        (| (<< (& 0xff (§ ai ßbytes ßoffset)) 24) (<< (& 0xff (§ ai ßbytes (+ ßoffset 1))) 16) (<< (& 0xff (§ ai ßbytes (+ ßoffset 2))) 8) (& 0xff (§ ai ßbytes (+ ßoffset 3))))
    )

    ;;; Parse 2 bytes from the byte array (starting at the offset) as unsigned 16-bit integer in big endian format. ;;
    #_public
    #_static
    (§ defn #_"int" (§ fn readUint16BE) [#_"byte[]" (§ name ßbytes), #_"int" (§ name ßoffset)])
    (§ block
        (| (<< (& 0xff (§ ai ßbytes ßoffset)) 8) (& 0xff (§ ai ßbytes (+ ßoffset 1))))
    )

    ;;;
     ; Calculates RIPEMD160(SHA256(input)).  This is used in Address calculations.
     ;;
    #_public
    #_static
    (§ defn #_"byte[]" (§ fn sha256hash160) [#_"byte[]" (§ name ßinput)])
    (§ block
        (§ let #_"byte[]" (§ name ßsha256) (Sha256Hash/hash ßinput))
        (§ block
            (§ let #_"RIPEMD160Digest" (§ name ßdigest) (§ new #_"RIPEMD160Digest"))
            (§ block
                (.. ßdigest (update ßsha256, 0, (.. ßsha256 length)))
                (§ let #_"byte[]" (§ name ßout) (§ new #_"byte[]" (§ count 20)))
                (§ block
                    (.. ßdigest (doFinal ßout, 0))
                    ßout
                )
            )
        )
    )

    ;;;
     ; MPI encoded numbers are produced by the OpenSSL BN_bn2mpi function.  They consist of
     ; a 4 byte big endian length field, followed by the stated number of bytes representing
     ; the number in big endian format (with a sign bit).
     ; @param hasLength can be set to false if the given array is missing the 4 byte length field.
     ;;
    #_public
    #_static
    (§ defn #_"BigInteger" (§ fn decodeMPI) [#_"byte[]" (§ name ßmpi), #_"boolean" (§ name ßhasLength)])
    (§ block
        (§ let #_"byte[]" (§ name ßbuf))
        (§ block
            (§ if ßhasLength)
            (§ block
                (§ let #_"int" (§ name ßlength) (§ cast #_"int" (readUint32BE ßmpi, 0)))
                (§ block
                    (§ ass (§ name ßbuf) (§ new #_"byte[]" (§ count ßlength)))
                    (System/arraycopy ßmpi, 4, ßbuf, 0, ßlength)
                )
            )
            (§ else )
            (§ block
                (§ ass (§ name ßbuf) ßmpi)
            )
            (§ if (== (.. ßbuf length) 0))
            (§ block
                (§ return BigInteger/ZERO)
            )

            (§ let #_"boolean" (§ name ßisNegative) (== (& (§ ai ßbuf 0) 0x80) 0x80))
            (§ block
                (§ if ßisNegative)
                (§ block
                    (§ ass (§ name (§ ai ßbuf 0)) (& (§ ai ßbuf 0) 0x7f))
                )
                (§ let #_"BigInteger" (§ name ßresult) (§ new #_"BigInteger" ßbuf))
                (§ block
                    (if ßisNegative (.. ßresult (negate)) ßresult)
                )
            )
        )
    )

    ;;;
     ; MPI encoded numbers are produced by the OpenSSL BN_bn2mpi function.  They consist of
     ; a 4 byte big endian length field, followed by the stated number of bytes representing
     ; the number in big endian format (with a sign bit).
     ; @param includeLength indicates whether the 4 byte length field should be included.
     ;;
    #_public
    #_static
    (§ defn #_"byte[]" (§ fn encodeMPI) [#_"BigInteger" (§ name ßvalue), #_"boolean" (§ name ßincludeLength)])
    (§ block
        (§ if (.. ßvalue (equals BigInteger/ZERO)))
        (§ block
            (§ return (if ßincludeLength (§ new #_"byte[]" (§ coll 0x00, 0x00, 0x00, 0x00 )) (§ new #_"byte[]")))
        )

        (§ let #_"boolean" (§ name ßisNegative) (< (.. ßvalue (signum)) 0))
        (§ block
            (§ if ßisNegative)
            (§ block
                (§ ass (§ name ßvalue) (.. ßvalue (negate)))
            )
            (§ let #_"byte[]" (§ name ßarray) (.. ßvalue (toByteArray)))
            (§ block
                (§ let #_"int" (§ name ßlength) (.. ßarray length))
                (§ block
                    (§ if (== (& (§ ai ßarray 0) 0x80) 0x80))
                    (§ block
                        (§ ass (§ name ßlength) (+ ßlength 1))
                    )

                    (§ if ßincludeLength)
                    (§ block
                        (§ let #_"byte[]" (§ name ßresult) (§ new #_"byte[]" (§ count ßlength + 4)))
                        (§ block
                            (System/arraycopy ßarray, 0, ßresult, (+ (- ßlength (.. ßarray ßlength)) 3), (.. ßarray ßlength))
                            (uint32ToByteArrayBE ßlength, ßresult, 0)
                            (§ if ßisNegative)
                            (§ block
                                (§ ass (§ name (§ ai ßresult 4)) (| (§ ai ßresult 4) 0x80))
                            )
                            (§ return ßresult)
                        )
                    )
                    (§ else )
                    (§ block
                        (§ let #_"byte[]" (§ name ßresult))
                        (§ block
                            (§ if (!= ßlength (.. ßarray ßlength)))
                            (§ block
                                (§ ass (§ name ßresult) (§ new #_"byte[]" (§ count ßlength)))
                                (System/arraycopy ßarray, 0, ßresult, 1, (.. ßarray ßlength))
                            )
                            (§ else )
                            (§ block
                                (§ ass (§ name ßresult) ßarray)
                            )
                            (§ if ßisNegative)
                            (§ block
                                (§ ass (§ name (§ ai ßresult 0)) (| (§ ai ßresult 0) 0x80))
                            )
                            (§ return ßresult)
                        )
                    )
                )
            )
        )
    )

    ;;;
     ; <p>The "compact" format is a representation of a whole number N using an unsigned 32 bit number similar to
     ; a floating point format.  The most significant 8 bits are the unsigned exponent of base 256.  This exponent
     ; can be thought of as "number of bytes of N".  The lower 23 bits are the mantissa.  Bit number 24 (0x800000)
     ; represents the sign of N.  Therefore, N = (-1^sign) * mantissa * 256^(exponent-3).</p>
     ;
     ; <p>Satoshi's original implementation used BN_bn2mpi() and BN_mpi2bn().  MPI uses the most significant bit of
     ; the first byte as sign.  Thus 0x1234560000 is compact 0x05123456 and 0xc0de000000 is compact 0x0600c0de.
     ; Compact 0x05c0de00 would be -0x40de000000.</p>
     ;
     ; <p>Bitcoin only uses this "compact" format for encoding difficulty targets, which are unsigned 256bit quantities.
     ; Thus, all the complexities of the sign bit and using base 256 are probably an implementation accident.</p>
     ;;
    #_public
    #_static
    (§ defn #_"BigInteger" (§ fn decodeCompactBits) [#_"long" (§ name ßcompact)])
    (§ block
        (§ let #_"int" (§ name ßsize) (& (§ cast #_"int" (>> ßcompact 24)) 0xff))
        (§ block
            (§ let #_"byte[]" (§ name ßbytes) (§ new #_"byte[]" (§ count 4 + ßsize)))
            (§ block
                (§ ass (§ name (§ ai ßbytes 3)) (§ cast #_"byte" ßsize))
                (§ if (<= 1 ßsize))
                (§ block
                    (§ ass (§ name (§ ai ßbytes 4)) (§ cast #_"byte" (& (>> ßcompact 16) 0xff)))
                )
                (§ if (<= 2 ßsize))
                (§ block
                    (§ ass (§ name (§ ai ßbytes 5)) (§ cast #_"byte" (& (>> ßcompact 8) 0xff)))
                )
                (§ if (<= 3 ßsize))
                (§ block
                    (§ ass (§ name (§ ai ßbytes 6)) (§ cast #_"byte" (& ßcompact 0xff)))
                )
                (decodeMPI ßbytes, true)
            )
        )
    )

    ;;;
     ; @see Utils#decodeCompactBits(long)
     ;;
    #_public
    #_static
    (§ defn #_"long" (§ fn encodeCompactBits) [#_"BigInteger" (§ name ßvalue)])
    (§ block
        (§ let #_"long" (§ name ßresult))
        (§ block
            (§ let #_"int" (§ name ßsize) (.. ßvalue (toByteArray) length))
            (§ block
                (§ if (<= ßsize 3))
                (§ block
                    (§ ass (§ name ßresult) (<< (.. ßvalue (longValue)) (* 8 (- 3 ßsize))))
                )
                (§ else )
                (§ block
                    (§ ass (§ name ßresult) (.. ßvalue (shiftRight (* 8 (- ßsize 3))) (longValue)))
                )
                ;; The 0x00800000 bit denotes the sign.
                ;; Thus, if it is already set, divide the mantissa by 256 and increase the exponent.
                (§ if (!= (& ßresult 0x00800000) 0))
                (§ block
                    (§ ass (§ name ßresult) (>> ßresult 8))
                    (§ ass (§ name ßsize) (+ ßsize 1))
                )
                (§ ass (§ name ßresult) (| ßresult (<< ßsize 24)))
                (§ ass (§ name ßresult) (| ßresult (if (== (.. ßvalue (signum)) -1) 0x00800000 0)))
                ßresult
            )
        )
    )

    ;;;
     ; If non-null, overrides the return value of now().
     ;;
    #_public
    #_static
    #_volatile
    (§ def #_"Date" (§ name mockTime))

    ;;;
     ; Advances (or rewinds) the mock clock by the given number of seconds.
     ;;
    #_public
    #_static
    (§ defn #_"Date" (§ fn rollMockClock) [#_"int" (§ name ßseconds)])
    (§ block
        (rollMockClockMillis (* ßseconds 1000))
    )

    ;;;
     ; Advances (or rewinds) the mock clock by the given number of milliseconds.
     ;;
    #_public
    #_static
    (§ defn #_"Date" (§ fn rollMockClockMillis) [#_"long" (§ name ßmillis)])
    (§ block
        (§ if (nil? mockTime))
        (§ block
            (§ throw (§ new #_"IllegalStateException" "You need to use setMockClock() first."))
        )

        (§ ass (§ name mockTime) (§ new #_"Date" (+ (.. mockTime (getTime)) ßmillis)))
        mockTime
    )

    ;;;
     ; Sets the mock clock to the current time.
     ;;
    #_public
    #_static
    (§ defn #_"void" (§ fn setMockClock) [])
    (§ block
        (§ ass (§ name mockTime) (§ new #_"Date"))
        nil
    )

    ;;;
     ; Sets the mock clock to the given time (in seconds).
     ;;
    #_public
    #_static
    (§ defn #_"void" (§ fn setMockClock) [#_"long" (§ name ßmockClockSeconds)])
    (§ block
        (§ ass (§ name mockTime) (§ new #_"Date" (* ßmockClockSeconds 1000)))
        nil
    )

    ;;;
     ; Returns the current time, or a mocked out equivalent.
     ;;
    #_public
    #_static
    (§ defn #_"Date" (§ fn now) [])
    (§ block
        (or mockTime (§ new #_"Date"))
    )

    ;; TODO: Replace usages of this where the result is / 1000 with currentTimeSeconds.
    ;;; Returns the current time in milliseconds since the epoch, or a mocked out equivalent. ;;
    #_public
    #_static
    (§ defn #_"long" (§ fn currentTimeMillis) [])
    (§ block
        (if (some? mockTime) (.. mockTime (getTime)) (System/currentTimeMillis))
    )

    #_public
    #_static
    (§ defn #_"long" (§ fn currentTimeSeconds) [])
    (§ block
        (/ (currentTimeMillis) 1000)
    )

    #_private
    #_static
