        (§ block
            (§ let #_"ByteArrayOutputStream" (§ name ßbos) (§ new #_"ByteArrayOutputStream"))
            (§ block
                (.. ßbos (write (.. BITCOIN_SIGNED_MESSAGE_HEADER_BYTES length)))
                (.. ßbos (write BITCOIN_SIGNED_MESSAGE_HEADER_BYTES))
                (§ let #_"byte[]" (§ name ßmessageBytes) (.. ßmessage (getBytes Charsets/UTF_8)))
                (§ block
                    (§ let #_"VarInt" (§ name ßsize) (§ new #_"VarInt" (.. ßmessageBytes length)))
