    )

    #_private
    #_static
    (§ class #_"Pair" (§ implements #_"Comparable<Pair>")
        (§ let #_"int" (§ name ßitem))
        (§ block
            (§ let #_"int" (§ name ßcount))
            (§ block

                #_public
                (§ constructor #_"Pair" [#_"int" (§ name ßitem), #_"int" (§ name ßcount)])
                (§ block
                    (§ ass (§ name (.. this ßcount)) ßcount)
                    (§ ass (§ name (.. this ßitem)) ßitem)
                    this
                )

                ;; Note that in this implementation compareTo() is not consistent with equals().
                #_override
                #_public
                (§ method #_"int" (§ fn compareTo) [#_"Pair" (§ name ßo)])
                (§ block
                    (- (Ints/compare ßcount, (.. ßo ßcount)))
                )
            )
        )
    )

    #_public
    #_static
    (§ defn #_"int" (§ fn maxOfMostFreq) [#_"int..." (§ name ßitems)])
    (§ block
        ;; Java 6 sucks.
        (§ let #_"ArrayList<Integer>" (§ name ßlist) (§ new #_"ArrayList<>" (.. ßitems length)))
        (§ block
            (§ for (§ var #_"int" (§ name ßitem)) :for ßitems)
            (§ block
                (.. ßlist (add ßitem))
            )
            (maxOfMostFreq ßlist)
        )
    )

    #_public
    #_static
    (§ defn #_"int" (§ fn maxOfMostFreq) [#_"List<Integer>" (§ name ßitems)])
    (§ block
        (§ if (.. ßitems (isEmpty)))
        (§ block
            (§ return 0)
        )

        ;; This would be much easier in a functional language (or in Java 8).
        (§ ass (§ name ßitems) (.. (Ordering/natural) (reverse) (sortedCopy ßitems)))
        (§ let #_"LinkedList<Pair>" (§ name ßpairs) (Lists/newLinkedList))
        (§ block
            (.. ßpairs (add (§ new #_"Pair" (.. ßitems (get 0)), 0)))
            (§ for (§ var #_"int" (§ name ßitem)) :for ßitems)
            (§ block
                (§ let #_"Pair" (§ name ßpair) (.. ßpairs (getLast)))
                (§ block
                    (§ if (!= (.. ßpair ßitem) ßitem))
                    (§ block
                        (.. ßpairs (add (§ ass (§ name ßpair) (§ new #_"Pair" ßitem, 0))))
                    )
                    (§ ass (§ name (.. ßpair count)) (+ (.. ßpair count) 1))
                )
            )
            ;; pairs now contains a uniqified list of the sorted inputs, with counts for how often that item appeared.
            ;; Now sort by how frequently they occur, and pick the max of the most frequent.
            (Collections/sort ßpairs)
            (§ let #_"int" (§ name ßmaxCount) (.. ßpairs (getFirst) count))
            (§ block
                (§ let #_"int" (§ name ßmaxItem) (.. ßpairs (getFirst) item))
                (§ block
                    (§ for (§ var #_"Pair" (§ name ßpair)) :for ßpairs)
                    (§ block
                        (§ if (!= (.. ßpair count) ßmaxCount))
                        (§ block
                            (§ break )
                        )
                        (§ ass (§ name ßmaxItem) (Math/max ßmaxItem, (.. ßpair item)))
                    )
                    ßmaxItem
                )
            )
        )
    )

    ;;;
     ; Reads and joins together with LF char (\n) all the lines from given file.
     ; It's assumed that file is in UTF-8.
