            (§ let #_"ImmutableList<ChildNumber>" (§ name ßpath) (.. childNumberPath (subList (.. ßcursor (getPath) (size)), (.. childNumberPath (size)))))
            (§ block
                (§ for (§ var #_"ChildNumber" (§ name ßnum)) :for ßpath)
                (§ block
                    (§ ass (§ name ßdownCursor) (HDKeyDerivation/deriveChildKey ßdownCursor, ßnum))
                )
                ;; downCursor is now the same key as us, but with private key bytes.
                ;; If it's not, it means we tried decrypting with an invalid password and earlier checks e.g. for padding didn't
                ;; catch it.
                (§ if (not (.. ßdownCursor pub (equals pub))))
                (§ block
                    (§ throw (§ new #_"KeyCrypterException" "Could not decrypt bytes"))
                )

                (Preconditions/checkNotNull (.. ßdownCursor priv))
            )
        )
    )

    ;;;
     ; Derives a child at the given index using hardened derivation.  Note: <code>index</code>
     ; is not the "i" value.  If you want the softened derivation, then use instead
     ; <code>HDKeyDerivation.deriveChildKey(this, new ChildNumber(child, false))</code>.
     ;;
    #_public
    (§ method #_"DeterministicKey" (§ fn derive) [#_"int" (§ name ßchild)])
    (§ block
        (HDKeyDerivation/deriveChildKey this, (§ new #_"ChildNumber" ßchild, true))
    )

    ;;;
     ; Returns the private key of this deterministic key.  Even if this object isn't storing the private key,
     ; it can be re-derived by walking up to the parents if necessary and this is what will happen.
     ; @throws java.lang.IllegalStateException if the parents are encrypted or a watching chain.
     ;;
    #_override
    #_public
    (§ method #_"BigInteger" (§ fn getPrivKey) [])
    (§ block
        (§ let #_"BigInteger" (§ name ßkey) (findOrDerivePrivateKey))
        (§ block
            (Preconditions/checkState (some? ßkey), "Private ßkey bytes not available")
            ßkey
        )
    )

    #_public
    (§ method #_"byte[]" (§ fn serializePublic) [#_"NetworkParameters" (§ name ßparams)])
    (§ block
        (serialize ßparams, true)
    )

    #_public
    (§ method #_"byte[]" (§ fn serializePrivate) [#_"NetworkParameters" (§ name ßparams)])
    (§ block
        (serialize ßparams, false)
    )

    #_private
    (§ method- #_"byte[]" (§ fn serialize) [#_"NetworkParameters" (§ name ßparams), #_"boolean" (§ name ßpub)])
    (§ block
        (§ let #_"ByteBuffer" (§ name ßser) (ByteBuffer/allocate 78))
        (§ block
            (.. ßser (putInt (if ßpub (.. ßparams (getBip32HeaderPub)) (.. ßparams (getBip32HeaderPriv)))))
            (.. ßser (put (§ cast #_"byte" (getDepth))))
            (.. ßser (putInt (getParentFingerprint)))
            (.. ßser (putInt (.. (getChildNumber) (i))))
            (.. ßser (put (getChainCode)))
            (.. ßser (put (if ßpub (getPubKey) (getPrivKeyBytes33))))
            (Preconditions/checkState (== (.. ßser (position)) 78))
            (.. ßser (array))
        )
    )

    #_public
    (§ method #_"String" (§ fn serializePubB58) [#_"NetworkParameters" (§ name ßparams)])
    (§ block
        (toBase58 (serialize ßparams, true))
    )

    #_public
    (§ method #_"String" (§ fn serializePrivB58) [#_"NetworkParameters" (§ name ßparams)])
    (§ block
        (toBase58 (serialize ßparams, false))
    )

    #_static
    (§ defn #_"String" (§ fn toBase58) [#_"byte[]" (§ name ßser)])
    (§ block
        (Base58/encode (addChecksum ßser))
    )

    ;;; Deserialize a base-58-encoded HD Key with no parent. ;;
    #_public
    #_static
    (§ defn #_"DeterministicKey" (§ fn deserializeB58) [#_"String" (§ name ßbase58), #_"NetworkParameters" (§ name ßparams)])
    (§ block
        (deserializeB58 nil, ßbase58, ßparams)
    )

    ;;;
     ; Deserialize a base-58-encoded HD Key.
     ; @param parent The parent node in the given key's deterministic hierarchy.
     ; @throws IllegalArgumentException if the base58 encoded key could not be parsed.
     ;;
    #_public
    #_static
    (§ defn #_"DeterministicKey" (§ fn deserializeB58) [#_nilable #_"DeterministicKey" (§ name ßparent), #_"String" (§ name ßbase58), #_"NetworkParameters" (§ name ßparams)])
    (§ block
        (deserialize ßparams, (Base58/decodeChecked ßbase58), ßparent)
    )

    ;;;
     ; Deserialize an HD Key with no parent.
     ;;
    #_public
    #_static
    (§ defn #_"DeterministicKey" (§ fn deserialize) [#_"NetworkParameters" (§ name ßparams), #_"byte[]" (§ name ßserializedKey)])
    (§ block
        (deserialize ßparams, ßserializedKey, nil)
    )

    ;;;
     ; Deserialize an HD Key.
     ; @param parent The parent node in the given key's deterministic hierarchy.
     ;;
    #_public
    #_static
    (§ defn #_"DeterministicKey" (§ fn deserialize) [#_"NetworkParameters" (§ name ßparams), #_"byte[]" (§ name ßserializedKey), #_nilable #_"DeterministicKey" (§ name ßparent)])
    (§ block
        (§ let #_"ByteBuffer" (§ name ßbuffer) (ByteBuffer/wrap ßserializedKey))
        (§ block
            (§ let #_"int" (§ name ßheader) (.. ßbuffer (getInt)))
            (§ block
                (§ if (and (!= ßheader (.. ßparams (getBip32HeaderPriv))) (!= ßheader (.. ßparams (getBip32HeaderPub)))))
                (§ block
                    (§ throw (§ new #_"IllegalArgumentException" (str "Unknown header bytes: " (.. (toBase58 ßserializedKey) (substring 0, 4)))))
                )

                (§ let #_"boolean" (§ name ßpub) (== ßheader (.. ßparams (getBip32HeaderPub))))
                (§ block
                    (§ let #_"int" (§ name ßdepth) (& (.. ßbuffer (get)) 0xff)) ;; convert signed byte to positive int since depth cannot be negative
                    (§ block
                        (§ let #_"int" (§ name ßparentFingerprint) (.. ßbuffer (getInt)))
                        (§ block
                            (§ let #_"int" (§ name ßi) (.. ßbuffer (getInt)))
                            (§ block
                                (§ let #_"ChildNumber" (§ name ßchildNumber) (§ new #_"ChildNumber" ßi))
                                (§ block
                                    (§ let #_"ImmutableList<ChildNumber>" (§ name ßpath))
                                    (§ block
                                        (§ if (some? ßparent))
                                        (§ block
                                            (§ if (== ßparentFingerprint 0))
                                            (§ block
                                                (§ throw (§ new #_"IllegalArgumentException" "Parent was provided but this key doesn't have one"))
                                            )
                                            (§ if (!= (.. ßparent (getFingerprint)) ßparentFingerprint))
                                            (§ block
                                                (§ throw (§ new #_"IllegalArgumentException" "Parent fingerprints don't match"))
                                            )
                                            (§ ass (§ name ßpath) (HDUtils/append (.. ßparent (getPath)), ßchildNumber))
                                            (§ if (!= (.. ßpath (size)) ßdepth))
                                            (§ block
                                                (§ throw (§ new #_"IllegalArgumentException" "Depth does not match"))
                                            )
                                        )
                                        (§ else )
                                        (§ block
                                            ;; We have been given a key that is not a root key, yet we lack the object representing the parent.
                                            ;; This can happen when deserializing an account key for a watching wallet.  In this case, we assume that
                                            ;; the client wants to conceal the key's position in the hierarchy.  The path is truncated at the
                                            ;; parent's node.
                                            (§ if (<= 1 ßdepth))
                                            (§ block
                                                (§ ass (§ name ßpath) (ImmutableList/of ßchildNumber))
                                            )
                                            (§ else )
                                            (§ block
                                                (§ ass (§ name ßpath) (ImmutableList/of))
                                            )
                                        )
                                        (§ let #_"byte[]" (§ name ßchainCode) (§ new #_"byte[]" (§ count 32)))
                                        (§ block
                                            (.. ßbuffer (get ßchainCode))
                                            (§ let #_"byte[]" (§ name ßdata) (§ new #_"byte[]" (§ count 33)))
                                            (§ block
                                                (.. ßbuffer (get ßdata))
                                                (Preconditions/checkArgument (not (.. ßbuffer (hasRemaining))), "Found unexpected ßdata in key")
                                                (§ if ßpub)
                                                (§ block
                                                    (§ return (§ new #_"DeterministicKey" ßpath, ßchainCode, (§ new #_"LazyECPoint" (.. ECKey/CURVE (getCurve)), ßdata), ßparent, ßdepth, ßparentFingerprint))
                                                )
                                                (§ else )
                                                (§ block
                                                    (§ return (§ new #_"DeterministicKey" ßpath, ßchainCode, (§ new #_"BigInteger" 1, ßdata), ßparent, ßdepth, ßparentFingerprint))
                                                )
                                            )
                                        )
                                    )
                                )
                            )
                        )
                    )
                )
            )
        )
    )

    ;;;
     ; The creation time of a deterministic key is equal to that of its parent, unless this key is the root of a tree
     ; in which case the time is stored alongside the key as per normal, see {@link org.bitcoinj.core.ECKey#getCreationTimeSeconds()}.
     ;;
    #_override
    #_public
    (§ method #_"long" (§ fn getCreationTimeSeconds) [])
    (§ block
        (§ if (some? parent))
        (§ block
            (§ return (.. parent (getCreationTimeSeconds)))
        )
        (§ else )
        (§ block
            (§ return (.. super (getCreationTimeSeconds)))
        )
    )

    ;;;
     ; The creation time of a deterministic key is equal to that of its parent, unless this key is the root of a tree.
     ; Thus, setting the creation time on a leaf is forbidden.
     ;;
    #_override
    #_public
    (§ method #_"void" (§ fn setCreationTimeSeconds) [#_"long" (§ name ßnewCreationTimeSeconds)])
    (§ block
        (§ if (some? parent))
        (§ block
            (§ throw (§ new #_"IllegalStateException" "Creation time can only be set on root keys."))
        )
        (§ else )
        (§ block
            (.. super (setCreationTimeSeconds ßnewCreationTimeSeconds))
        )
        nil
    )

    ;;;
     ; Verifies equality of all fields but NOT the parent pointer (thus the same key derived in two separate heirarchy
     ; objects will equal each other.
     ;;
    #_override
    #_public
    (§ method #_"boolean" (§ fn equals) [#_"Object" (§ name ßo)])
    (§ block
        (§ if (== this ßo))
        (§ block
            (§ return true)
        )
        (§ if (or (nil? ßo) (!= (getClass) (.. ßo (getClass)))))
        (§ block
            (§ return false)
        )
        (§ let #_"DeterministicKey" (§ name ßother) (§ cast #_"DeterministicKey" ßo))
        (§ block
            (and (.. super (equals ßother)) (Arrays/equals (.. this chainCode), (.. ßother chainCode)) (Objects/equal (.. this childNumberPath), (.. ßother childNumberPath)))
        )
    )

    #_override
    #_public
    (§ method #_"int" (§ fn hashCode) [])
    (§ block
        (Objects/hashCode (.. super (hashCode)), (Arrays/hashCode chainCode), childNumberPath)
    )

    #_override
    #_public
    (§ method #_"String" (§ fn toString) [])
    (§ block
        (§ let #_"MoreObjects.ToStringHelper" (§ name ßhelper) (.. (MoreObjects/toStringHelper this) (omitNullValues)))
        (§ block
            (.. ßhelper (add "pub", (.. Utils/HEX (encode (.. pub (getEncoded))))))
            (.. ßhelper (add "chainCode", (.. HEX (encode chainCode))))
            (.. ßhelper (add "path", (getPathAsString)))
            (§ if (< 0 creationTimeSeconds))
            (§ block
                (.. ßhelper (add "creationTimeSeconds", creationTimeSeconds))
            )
            (.. ßhelper (add "isEncrypted", (isEncrypted)))
            (.. ßhelper (add "isPubKeyOnly", (isPubKeyOnly)))
            (.. ßhelper (toString))
        )
    )

    #_override
    #_public
    (§ method #_"void" (§ fn formatKeyWithAddress) [#_"boolean" (§ name ßincludePrivateKeys), #_"StringBuilder" (§ name ßsb), #_"NetworkParameters" (§ name ßparams)])
    (§ block
        (§ let #_"Address" (§ name ßaddress) (toAddress ßparams))
        (§ block
            (.. ßsb (append "  addr:") (append ßaddress))
            (.. ßsb (append "  hash160:") (append (.. Utils/HEX (encode (getPubKeyHash)))))
            (.. ßsb (append "  (") (append (getPathAsString)) (append ")\n"))
            (§ if ßincludePrivateKeys)
            (§ block
                (.. ßsb (append "  ") (append (toStringWithPrivate ßparams)) (append "\n"))
            )
            nil
        )
    )
)

#_(ns org.bitcoinj.crypto #_"EncryptableItem"
   (:require [org.bitcoinj.wallet Protos]))

;;;
 ; Provides a uniform way to access something that can be optionally encrypted with a
 ; {@link org.bitcoinj.crypto.KeyCrypter}, yielding an {@link org.bitcoinj.crypto.EncryptedData},
 ; and which can have a creation time associated with it.
 ;;
#_public
(§ interface #_"EncryptableItem"
    ;;; Returns whether the item is encrypted or not.  If it is, then {@link #getSecretBytes()} will return null. ;;
    (§ method #_"boolean" (§ fn isEncrypted) [])

    ;;; Returns the raw bytes of the item, if not encrypted, or null if encrypted or the secret is missing. ;;
    #_nilable
    (§ method #_"byte[]" (§ fn getSecretBytes) [])

    ;;; Returns the initialization vector and encrypted secret bytes, or null if not encrypted. ;;
    #_nilable
    (§ method #_"EncryptedData" (§ fn getEncryptedData) [])

    ;;; Returns an enum constant describing what algorithm was used to encrypt the key or UNENCRYPTED. ;;
    (§ method #_"Protos.Wallet.EncryptionType" (§ fn getEncryptionType) [])

    ;;; Returns the time in seconds since the UNIX epoch at which this encryptable item was first created/derived. ;;
    (§ method #_"long" (§ fn getCreationTimeSeconds) [])
)

#_(ns org.bitcoinj.crypto #_"EncryptedData"
    (:import [java.util Arrays])
    (:import [com.google.common.base Objects]))

;;;
 ; <p>An instance of EncryptedData is a holder for an initialization vector and encrypted bytes.
 ; It is typically used to hold encrypted private key bytes.</p>
 ;
 ; <p>The initialisation vector is random data that is used to initialise the AES block cipher when
 ; the private key bytes were encrypted.  You need these for decryption.</p>
 ;;
#_public
(§ class #_"EncryptedData"
    #_public
    (§ field #_"byte[]" (§ name initialisationVector))
    #_public
    (§ field #_"byte[]" (§ name encryptedBytes))

    #_public
    (§ constructor #_"EncryptedData" [#_"byte[]" (§ name ßinitialisationVector), #_"byte[]" (§ name ßencryptedBytes)])
    (§ block
        (§ ass (§ name (.. this ßinitialisationVector)) (Arrays/copyOf ßinitialisationVector, (.. ßinitialisationVector length)))
        (§ ass (§ name (.. this ßencryptedBytes)) (Arrays/copyOf ßencryptedBytes, (.. ßencryptedBytes length)))
        this
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn equals) [#_"Object" (§ name ßo)])
    (§ block
        (§ if (== this ßo))
        (§ block
            (§ return true)
        )
        (§ if (or (nil? ßo) (!= (getClass) (.. ßo (getClass)))))
        (§ block
            (§ return false)
        )
        (§ let #_"EncryptedData" (§ name ßother) (§ cast #_"EncryptedData" ßo))
        (§ block
            (and (Arrays/equals encryptedBytes, (.. ßother encryptedBytes)) (Arrays/equals initialisationVector, (.. ßother initialisationVector)))
        )
    )

    #_override
    #_public
    (§ method #_"int" (§ fn hashCode) [])
    (§ block
        (Objects/hashCode (Arrays/hashCode encryptedBytes), (Arrays/hashCode initialisationVector))
    )

    #_override
    #_public
    (§ method #_"String" (§ fn toString) [])
    (§ block
        (str "EncryptedData [initialisationVector=" (Arrays/toString initialisationVector) ", encryptedPrivateKey=" (Arrays/toString encryptedBytes) "]")
    )
)

#_(ns org.bitcoinj.crypto #_"HDDerivationException")

#_public
(§ class #_"HDDerivationException" (§ extends #_"RuntimeException")
    #_public
    (§ constructor #_"HDDerivationException" [#_"String" (§ name ßmessage)])
    (§ block
        (§ super ßmessage)
        this
    )
)

#_(ns org.bitcoinj.crypto #_"HDKeyDerivation"
    (:import [java.math *]
             [java.nio *]
             [java.security *]
             [java.util *])
    (:import [com.google.common.base Preconditions]
             [com.google.common.collect *]
             [org.spongycastle.math.ec *])
   (:require [org.bitcoinj.core *]))

;;;
 ; Implementation of the <a href="https://github.com/bitcoin/bips/blob/master/bip-0032.mediawiki">BIP 32</a>
 ; deterministic wallet child key generation algorithm.
 ;;
#_public
(§ class #_"HDKeyDerivation"
    #_static
    (§ block
        ;; Init proper random number generator, as some old Android installations have bugs that make it unsecure.
        (§ if (Utils/isAndroidRuntime))
        (§ block
            (§ new #_"LinuxSecureRandom")
        )

        (§ ass (§ name RAND_INT) (§ new #_"BigInteger" 256, (§ new #_"SecureRandom")))
    )

    ;; Some arbitrary random number.  Doesn't matter what it is.
    #_private
    #_static
    (§ def- #_"BigInteger" (§ name RAND_INT))

    #_private
    (§ constructor- #_"HDKeyDerivation" [])
    (§ block
        this
    )

    ;;;
     ; Child derivation may fail (although with extremely low probability); in such case it is re-attempted.
     ; This is the maximum number of re-attempts (to avoid an infinite loop in case of bugs etc.)
     ;;
    #_public
    #_static
    (§ def #_"int" (§ name MAX_CHILD_DERIVATION_ATTEMPTS) 100)

    ;;;
     ; Generates a new deterministic key from the given seed, which can be any arbitrary byte array.
     ; However resist the temptation to use a string as the seed - any key derived from a password
     ; is likely to be weak and easily broken by attackers (this is not theoretical, people have had
     ; money stolen that way).  This method checks that the given seed is at least 64 bits long.
     ;
     ; @throws HDDerivationException if generated master key is invalid (private key 0 or >= n).
     ; @throws IllegalArgumentException if the seed is less than 8 bytes and could be brute forced.
     ;;
    #_public
    #_static
    (§ defn #_"DeterministicKey" (§ fn createMasterPrivateKey) [#_"byte[]" (§ name ßseed)])
        (§ throws #_"HDDerivationException")
    (§ block
        (Preconditions/checkArgument (< 8 (.. ßseed length)), "Seed is too short and could be brute forced")

        ;; Calculate I = HMAC-SHA512(key="Bitcoin seed", msg=S).
        (§ let #_"byte[]" (§ name ßi) (HDUtils/hmacSha512 (HDUtils/createHmacSha512Digest (.. "Bitcoin seed" (getBytes))), ßseed))
        (§ block
            ;; Split I into two 32-byte sequences, Il and Ir.
            ;; Use Il as master secret key, and Ir as master chain code.
            (Preconditions/checkState (== (.. ßi length) 64), (.. ßi length))

            (§ let #_"byte[]" (§ name ßil) (Arrays/copyOfRange ßi, 0, 32))
            (§ block
                (§ let #_"byte[]" (§ name ßir) (Arrays/copyOfRange ßi, 32, 64))
                (§ block
                    (Arrays/fill ßi, (§ cast #_"byte" 0))
                    (§ let #_"DeterministicKey" (§ name ßmasterPrivKey) (createMasterPrivKeyFromBytes ßil, ßir))
                    (§ block
                        (Arrays/fill ßil, (§ cast #_"byte" 0))
                        (Arrays/fill ßir, (§ cast #_"byte" 0))

                        ;; Child deterministic keys will chain up to their parents to find the keys.
                        (.. ßmasterPrivKey (setCreationTimeSeconds (Utils/currentTimeSeconds)))
                        ßmasterPrivKey
                    )
                )
            )
        )
    )

    ;;;
     ; @throws HDDerivationException if privKeyBytes is invalid (0 or >= n).
     ;;
    #_public
    #_static
    (§ defn #_"DeterministicKey" (§ fn createMasterPrivKeyFromBytes) [#_"byte[]" (§ name ßprivKeyBytes), #_"byte[]" (§ name ßchainCode)])
        (§ throws #_"HDDerivationException")
    (§ block
        (§ let #_"BigInteger" (§ name ßpriv) (§ new #_"BigInteger" 1, ßprivKeyBytes))
        (§ block
            (assertNonZero ßpriv, "Generated master key is invalid.")
            (assertLessThanN ßpriv, "Generated master key is invalid.")
            (§ new #_"DeterministicKey" (ImmutableList/of #_"ImmutableList<ChildNumber>"), ßchainCode, ßpriv, nil)
        )
    )

    #_public
    #_static
    (§ defn #_"DeterministicKey" (§ fn createMasterPubKeyFromBytes) [#_"byte[]" (§ name ßpubKeyBytes), #_"byte[]" (§ name ßchainCode)])
    (§ block
        (§ new #_"DeterministicKey" (ImmutableList/of #_"ImmutableList<ChildNumber>"), ßchainCode, (§ new #_"LazyECPoint" (.. ECKey/CURVE (getCurve)), ßpubKeyBytes), nil, nil)
    )

    ;;;
     ; Derives a key given the "extended" child number, i.e. the 0x80000000 bit of the value that you
     ; pass for <code>childNumber</code> will determine whether to use hardened derivation or not.
     ; Consider whether your code would benefit from the clarity of the equivalent, but explicit, form
     ; of this method that takes a <code>ChildNumber</code> rather than an <code>int</code>, for example:
     ; <code>deriveChildKey(parent, new ChildNumber(childNumber, true))</code>
     ; where the value of the hardened bit of <code>childNumber</code> is zero.
     ;;
    #_public
    #_static
    (§ defn #_"DeterministicKey" (§ fn deriveChildKey) [#_"DeterministicKey" (§ name ßparent), #_"int" (§ name ßchildNumber)])
    (§ block
        (deriveChildKey ßparent, (§ new #_"ChildNumber" ßchildNumber))
    )

    ;;;
     ; Derives a key of the "extended" child number, i.e. with the 0x80000000 bit specifying whether
     ; to use hardened derivation or not.  If derivation fails, tries a next child.
     ;;
    #_public
    #_static
    (§ defn #_"DeterministicKey" (§ fn deriveThisOrNextChildKey) [#_"DeterministicKey" (§ name ßparent), #_"int" (§ name ßchildNumber)])
    (§ block
        (§ let #_"ChildNumber" (§ name ßchild) (§ new #_"ChildNumber" ßchildNumber))
        (§ block
            (§ let #_"boolean" (§ name ßisHardened) (.. ßchild (isHardened)))
            (§ block
                (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi MAX_CHILD_DERIVATION_ATTEMPTS) :for (§ recur (+ ßi 1)))
                (§ block
                    (§ try )
                    (§ block
                        (§ ass (§ name ßchild) (§ new #_"ChildNumber" (+ (.. ßchild (num)) ßi), ßisHardened))
                        (§ return (deriveChildKey ßparent, ßchild))
                    )
                    (§ catch #_"HDDerivationException" (§ name _))
                    (§ block
                    )
                )
                (§ throw (§ new #_"HDDerivationException" "Maximum number of child derivation attempts reached, this is probably an indication of a bug."))
            )
        )
    )

    ;;;
     ; @throws HDDerivationException if private derivation is attempted for a public-only parent key, or
     ; if the resulting derived key is invalid (e.g. private key == 0).
     ;;
    #_public
    #_static
    (§ defn #_"DeterministicKey" (§ fn deriveChildKey) [#_"DeterministicKey" (§ name ßparent), #_"ChildNumber" (§ name ßchildNumber)])
        (§ throws #_"HDDerivationException")
    (§ block
        (§ if (not (.. ßparent (hasPrivKey))))
        (§ block
            (§ let #_"RawKeyBytes" (§ name ßrawKey) (deriveChildKeyBytesFromPublic ßparent, ßchildNumber, PublicDeriveMode/NORMAL))
            (§ block
                (§ return (§ new #_"DeterministicKey" (HDUtils/append (.. ßparent (getPath)), ßchildNumber), (.. ßrawKey chainCode), (§ new #_"LazyECPoint" (.. ECKey/CURVE (getCurve)), (.. ßrawKey keyBytes)), nil, ßparent))
            )
        )
        (§ else )
        (§ block
            (§ let #_"RawKeyBytes" (§ name ßrawKey) (deriveChildKeyBytesFromPrivate ßparent, ßchildNumber))
            (§ block
                (§ return (§ new #_"DeterministicKey" (HDUtils/append (.. ßparent (getPath)), ßchildNumber), (.. ßrawKey chainCode), (§ new #_"BigInteger" 1, (.. ßrawKey keyBytes)), ßparent))
            )
        )
    )

    #_public
    #_static
    (§ defn #_"RawKeyBytes" (§ fn deriveChildKeyBytesFromPrivate) [#_"DeterministicKey" (§ name ßparent), #_"ChildNumber" (§ name ßchildNumber)])
        (§ throws #_"HDDerivationException")
    (§ block
        (Preconditions/checkArgument (.. ßparent (hasPrivKey)), "Parent key must have private key bytes for this method.")

        (§ let #_"byte[]" (§ name ßparentPublicKey) (.. ßparent (getPubKeyPoint) (getEncoded true)))
        (§ block
            (Preconditions/checkState (== (.. ßparentPublicKey length) 33), (str "Parent pubkey must be 33 bytes, but is " (.. ßparentPublicKey length)))

            (§ let #_"ByteBuffer" (§ name ßdata) (ByteBuffer/allocate 37))
            (§ block
                (.. ßdata (put (if (.. ßchildNumber (isHardened)) (.. ßparent (getPrivKeyBytes33)) ßparentPublicKey)))
                (.. ßdata (putInt (.. ßchildNumber (i))))

                (§ let #_"byte[]" (§ name ßi) (HDUtils/hmacSha512 (.. ßparent (getChainCode)), (.. ßdata (array))))
                (§ block
                    (Preconditions/checkState (== (.. ßi length) 64), (.. ßi length))

                    (§ let #_"byte[]" (§ name ßil) (Arrays/copyOfRange ßi, 0, 32))
                    (§ block
                        (§ let #_"byte[]" (§ name ßchainCode) (Arrays/copyOfRange ßi, 32, 64))
                        (§ block
                            (§ let #_"BigInteger" (§ name ßilInt) (§ new #_"BigInteger" 1, ßil))
                            (§ block
                                (assertLessThanN ßilInt, "Illegal derived key: I_L >= n")

                                (§ let #_"BigInteger" (§ name ßpriv) (.. ßparent (getPrivKey)))
                                (§ block
                                    (§ let #_"BigInteger" (§ name ßki) (.. ßpriv (add ßilInt) (mod (.. ECKey/CURVE (getN)))))
                                    (§ block
                                        (assertNonZero ßki, "Illegal derived key: derived private key equals 0.")

                                        (§ new #_"RawKeyBytes" (.. ßki (toByteArray)), ßchainCode)
                                    )
                                )
                            )
                        )
                    )
                )
            )
        )
    )

    #_public
    (§ enum #_"PublicDeriveMode"
        (§ item NORMAL)
        (§ item WITH_INVERSION)
    )

    #_public
    #_static
    (§ defn #_"RawKeyBytes" (§ fn deriveChildKeyBytesFromPublic) [#_"DeterministicKey" (§ name ßparent), #_"ChildNumber" (§ name ßchildNumber), #_"PublicDeriveMode" (§ name ßmode)])
        (§ throws #_"HDDerivationException")
    (§ block
        (Preconditions/checkArgument (not (.. ßchildNumber (isHardened))), "Can't use private derivation with public keys only.")

        (§ let #_"byte[]" (§ name ßparentPublicKey) (.. ßparent (getPubKeyPoint) (getEncoded true)))
        (§ block
            (Preconditions/checkState (== (.. ßparentPublicKey length) 33), (str "Parent pubkey must be 33 bytes, but is " (.. ßparentPublicKey length)))

            (§ let #_"ByteBuffer" (§ name ßdata) (ByteBuffer/allocate 37))
            (§ block
                (.. ßdata (put ßparentPublicKey))
                (.. ßdata (putInt (.. ßchildNumber (i))))

                (§ let #_"byte[]" (§ name ßi) (HDUtils/hmacSha512 (.. ßparent (getChainCode)), (.. ßdata (array))))
                (§ block
                    (Preconditions/checkState (== (.. ßi length) 64), (.. ßi length))

                    (§ let #_"byte[]" (§ name ßil) (Arrays/copyOfRange ßi, 0, 32))
                    (§ block
                        (§ let #_"byte[]" (§ name ßchainCode) (Arrays/copyOfRange ßi, 32, 64))
                        (§ block
                            (§ let #_"BigInteger" (§ name ßilInt) (§ new #_"BigInteger" 1, ßil))
                            (§ block
                                (assertLessThanN ßilInt, "Illegal derived key: I_L >= n")

                                (§ let #_"BigInteger" (§ name ßN) (.. ECKey/CURVE (getN)))
                                (§ block
                                    (§ let #_"ECPoint" (§ name ßKi))
                                    (§ block
                                        (§ switch ßmode)
                                        (§ block
                                            (§ case NORMAL)
                                            (§ block
                                                (§ ass (§ name ßKi) (.. (ECKey/publicPointFromPrivate ßilInt) (add (.. ßparent (getPubKeyPoint)))))
                                                (§ break )
                                            )
                                            (§ case WITH_INVERSION)
                                            (§ block
                                                ;; This trick comes from Gregory Maxwell.  Check the homomorphic properties of our curve hold.  The
                                                ;; below calculations should be redundant and give the same result as NORMAL but if the precalculated
                                                ;; tables have taken a bit flip will yield a different answer.  This mode is used when vending a key
                                                ;; to perform a last-ditch sanity check trying to catch bad RAM.
                                                (§ ass (§ name ßKi) (ECKey/publicPointFromPrivate (.. ßilInt (add RAND_INT) (mod ßN))))
                                                (§ let #_"BigInteger" (§ name ßadditiveInverse) (.. RAND_INT (negate) (mod ßN)))
                                                (§ block
                                                    (§ ass (§ name ßKi) (Ki/add (ECKey/publicPointFromPrivate ßadditiveInverse)))
                                                    (§ ass (§ name ßKi) (Ki/add (.. ßparent (getPubKeyPoint))))
                                                    (§ break )
                                                )
                                            )
                                            (§ default )
                                            (§ block
                                                (§ throw (§ new #_"AssertionError"))
                                            )
                                        )

                                        (assertNonInfinity ßKi, "Illegal derived key: derived public key equals infinity.")
                                        (§ new #_"RawKeyBytes" (Ki/getEncoded true), ßchainCode)
                                    )
                                )
                            )
                        )
                    )
                )
            )
        )
    )

    #_private
    #_static
    (§ defn- #_"void" (§ fn assertNonZero) [#_"BigInteger" (§ name ßinteger), #_"String" (§ name ßerrorMessage)])
    (§ block
        (§ if (.. ßinteger (equals BigInteger/ZERO)))
        (§ block
            (§ throw (§ new #_"HDDerivationException" ßerrorMessage))
        )
        nil
    )

    #_private
    #_static
    (§ defn- #_"void" (§ fn assertNonInfinity) [#_"ECPoint" (§ name ßpoint), #_"String" (§ name ßerrorMessage)])
    (§ block
        (§ if (.. ßpoint (equals (.. ECKey/CURVE (getCurve) (getInfinity)))))
        (§ block
            (§ throw (§ new #_"HDDerivationException" ßerrorMessage))
        )
        nil
    )

    #_private
    #_static
    (§ defn- #_"void" (§ fn assertLessThanN) [#_"BigInteger" (§ name ßinteger), #_"String" (§ name ßerrorMessage)])
    (§ block
        (§ if (> (.. ßinteger (compareTo (.. ECKey/CURVE (getN)))) 0))
        (§ block
            (§ throw (§ new #_"HDDerivationException" ßerrorMessage))
        )
        nil
    )

    #_public
    #_static
    (§ class #_"RawKeyBytes"
        #_public
        (§ field #_"byte[]" (§ name keyBytes))
        #_public
        (§ field #_"byte[]" (§ name chainCode))

        #_public
        (§ constructor #_"RawKeyBytes" [#_"byte[]" (§ name ßkeyBytes), #_"byte[]" (§ name ßchainCode)])
        (§ block
            (§ ass (§ name (.. this ßkeyBytes)) ßkeyBytes)
            (§ ass (§ name (.. this ßchainCode)) ßchainCode)
            this
        )
    )
)

#_(ns org.bitcoinj.crypto #_"HDUtils"
    (:import [java.nio ByteBuffer]
             [java.util ArrayList Arrays Collections List])
    (:import [com.google.common.base Joiner]
             [com.google.common.collect ImmutableList Iterables]
             [org.spongycastle.crypto.digests SHA512Digest]
             [org.spongycastle.crypto.macs HMac]
             [org.spongycastle.crypto.params KeyParameter])
   (:require [org.bitcoinj.core ECKey]))

;;;
 ; Static utilities used in BIP 32 Hierarchical Deterministic Wallets (HDW).
 ;;
#_public
(§ class #_"HDUtils"
    #_private
    #_static
    (§ def- #_"Joiner" (§ name PATH_JOINER) (Joiner/on "/"))

    #_static
    (§ defn #_"HMac" (§ fn createHmacSha512Digest) [#_"byte[]" (§ name ßkey)])
    (§ block
        (§ let #_"SHA512Digest" (§ name ßdigest) (§ new #_"SHA512Digest"))
        (§ block
            (§ let #_"HMac" (§ name ßhMac) (§ new #_"HMac" ßdigest))
            (§ block
                (.. ßhMac (init (§ new #_"KeyParameter" ßkey)))
                ßhMac
            )
        )
    )

    #_static
    (§ defn #_"byte[]" (§ fn hmacSha512) [#_"HMac" (§ name ßhmacSha512), #_"byte[]" (§ name ßinput)])
    (§ block
        (.. ßhmacSha512 (reset))
        (.. ßhmacSha512 (update ßinput, 0, (.. ßinput length)))
        (§ let #_"byte[]" (§ name ßout) (§ new #_"byte[]" (§ count 64)))
        (§ block
            (.. ßhmacSha512 (doFinal ßout, 0))
            ßout
        )
    )

    #_public
    #_static
    (§ defn #_"byte[]" (§ fn hmacSha512) [#_"byte[]" (§ name ßkey), #_"byte[]" (§ name ßdata)])
    (§ block
        (hmacSha512 (createHmacSha512Digest ßkey), ßdata)
    )

    #_static
    (§ defn #_"byte[]" (§ fn toCompressed) [#_"byte[]" (§ name ßuncompressedPoint)])
    (§ block
        (.. ECKey/CURVE (getCurve) (decodePoint ßuncompressedPoint) (getEncoded true))
    )

    #_static
    (§ defn #_"byte[]" (§ fn longTo4ByteArray) [#_"long" (§ name ßn)])
    (§ block
        (§ let #_"byte[]" (§ name ßbytes) (Arrays/copyOfRange (.. (ByteBuffer/allocate 8) (putLong ßn) (array)), 4, 8))
        (§ block
            (§ assert (== (.. ßbytes length) 4) :assert (.. ßbytes length))
            ßbytes
        )
    )

    ;;; Append a derivation level to an existing path. ;;
    #_public
    #_static
    (§ defn #_"ImmutableList<ChildNumber>" (§ fn append) [#_"List<ChildNumber>" (§ name ßpath), #_"ChildNumber" (§ name ßchildNumber)])
    (§ block
        (.. (ImmutableList/builder #_"ImmutableList<ChildNumber>") (addAll ßpath) (add ßchildNumber) (build))
    )

    ;;; Concatenate two derivation paths. ;;
    #_public
    #_static
    (§ defn #_"ImmutableList<ChildNumber>" (§ fn concat) [#_"List<ChildNumber>" (§ name ßpath), #_"List<ChildNumber>" (§ name ßpath2)])
    (§ block
        (.. (ImmutableList/builder #_"ImmutableList<ChildNumber>") (addAll ßpath) (addAll ßpath2) (build))
    )

    ;;; Convert to a string path, starting with "M/". ;;
    #_public
    #_static
    (§ defn #_"String" (§ fn formatPath) [#_"List<ChildNumber>" (§ name ßpath)])
    (§ block
        (.. PATH_JOINER (join (Iterables/concat (Collections/singleton "M"), ßpath)))
    )

    ;;;
     ; The path is a human-friendly representation of the deterministic path.  For example:
     ;
     ; "44H / 0H / 0H / 1 / 1"
     ;
     ; Where a letter "H" means hardened key.  Spaces are ignored.
     ;;
    #_public
    #_static
    (§ defn #_"List<ChildNumber>" (§ fn parsePath) [#_non-nil #_"String" (§ name ßpath)])
    (§ block
        (§ let #_"String[]" (§ name ßparsedNodes) (.. ßpath (replace "M", "") (split "/")))
        (§ block
            (§ let #_"List<ChildNumber>" (§ name ßnodes) (§ new #_"ArrayList<>"))
            (§ block

                (§ for (§ var #_"String" (§ name ßn)) :for ßparsedNodes)
                (§ block
                    (§ ass (§ name ßn) (.. ßn (replaceAll " ", "")))
                    (§ if (!= (.. ßn (length)) 0))
                    (§ block
                        (§ let #_"boolean" (§ name ßisHard) (.. ßn (endsWith "H")))
                        (§ block
                            (§ if ßisHard)
                            (§ block
                                (§ ass (§ name ßn) (.. ßn (substring 0, (- (.. ßn (length)) 1))))
                            )
                            (§ let #_"int" (§ name ßnodeNumber) (Integer/parseInt ßn))
                            (§ block
                                (.. ßnodes (add (§ new #_"ChildNumber" ßnodeNumber, ßisHard)))
                            )
                        )
                    )
                )

                ßnodes
            )
        )
    )
)

#_(ns org.bitcoinj.crypto #_"KeyCrypter"
    (:import [java.io Serializable])
    (:import [org.spongycastle.crypto.params KeyParameter])
   (:require [org.bitcoinj.wallet.Protos.Wallet EncryptionType]))

;;;
 ; <p>A KeyCrypter can be used to encrypt and decrypt a message.  The sequence of events to encrypt and then decrypt
 ; a message are as follows:</p>
 ;
 ; <p>(1) Ask the user for a password.  deriveKey() is then called to create an KeyParameter.  This contains the AES
 ; key that will be used for encryption.</p>
 ; <p>(2) Encrypt the message using encrypt(), providing the message bytes and the KeyParameter from (1).  This returns
 ; an EncryptedData which contains the encryptedPrivateKey bytes and an initialisation vector.</p>
 ; <p>(3) To decrypt an EncryptedData, repeat step (1) to get a KeyParameter, then call decrypt().</p>
 ;
 ; <p>There can be different algorithms used for encryption/ decryption so the getUnderstoodEncryptionType is used
 ; to determine whether any given KeyCrypter can understand the type of encrypted data you have.</p>
 ;;
#_public
(§ interface #_"KeyCrypter" (§ extends #_"Serializable")
    ;;;
     ; Return the EncryptionType enum value which denotes the type of encryption/ decryption that this KeyCrypter
     ; can understand.
     ;;
    (§ method #_"EncryptionType" (§ fn getUnderstoodEncryptionType) [])

    ;;;
     ; Create a KeyParameter (which typically contains an AES key).
     ; @param password
     ; @return the KeyParameter which typically contains the AES key to use for encrypting and decrypting.
     ; @throws KeyCrypterException
     ;;
    (§ method #_"KeyParameter" (§ fn deriveKey) [#_"CharSequence" __password])
        (§ throws #_"KeyCrypterException")

    ;;;
     ; Decrypt the provided encrypted bytes, converting them into unencrypted bytes.
     ;
     ; @throws KeyCrypterException if decryption was unsuccessful.
     ;;
    (§ method #_"byte[]" (§ fn decrypt) [#_"EncryptedData" __encryptedBytesToDecode, #_"KeyParameter" __aesKey])
        (§ throws #_"KeyCrypterException")

    ;;;
     ; Encrypt the supplied bytes, converting them into ciphertext.
     ;
     ; @return an encryptedPrivateKey containing the encrypted bytes and an initialisation vector.
     ; @throws KeyCrypterException if encryption was unsuccessful.
     ;;
    (§ method #_"EncryptedData" (§ fn encrypt) [#_"byte[]" __plainBytes, #_"KeyParameter" __aesKey])
        (§ throws #_"KeyCrypterException")
)

#_(ns org.bitcoinj.crypto #_"KeyCrypterException")

;;;
 ; <p>Exception to provide the following:</p>
 ; <ul>
 ; <li>Provision of encryption / decryption exception.</li>
 ; </ul>
 ; <p>This base exception acts as a general failure mode not attributable to a specific cause (other than
 ; that reported in the exception message).  Since this is in English, it may not be worth reporting directly
 ; to the user other than as part of a "general failure to parse" response.</p>
 ;;
#_public
(§ class #_"KeyCrypterException" (§ extends #_"RuntimeException")
    #_public
    (§ constructor #_"KeyCrypterException" [#_"String" (§ name ßs)])
    (§ block
        (§ super ßs)
        this
    )

    #_public
    (§ constructor #_"KeyCrypterException" [#_"String" (§ name ßs), #_"Throwable" (§ name ßthrowable)])
    (§ block
        (§ super ßs, ßthrowable)
        this
    )
)

#_(ns org.bitcoinj.crypto #_"KeyCrypterScrypt"
    (:import [java.security SecureRandom]
             [java.util Arrays])
    (:import [com.google.common.base Objects Preconditions Stopwatch]
             [com.google.protobuf ByteString]
             [com.lambdaworks.crypto SCrypt]
             [org.slf4j Logger LoggerFactory]
             [org.spongycastle.crypto BufferedBlockCipher]
             [org.spongycastle.crypto.engines AESFastEngine]
             [org.spongycastle.crypto.modes CBCBlockCipher]
             [org.spongycastle.crypto.paddings PaddedBufferedBlockCipher]
             [org.spongycastle.crypto.params KeyParameter ParametersWithIV])
   (:require [org.bitcoinj.core Utils]
             [org.bitcoinj.wallet Protos]
             [org.bitcoinj.wallet.Protos ScryptParameters]
             [org.bitcoinj.wallet.Protos.Wallet EncryptionType]))

;;;
 ; <p>This class encrypts and decrypts byte arrays and strings using scrypt as the
 ; key derivation function and AES for the encryption.</p>
 ;
 ; <p>You can use this class to:</p>
 ;
 ; <p>1. Using a user password, create an AES key that can encrypt and decrypt your private keys.
 ; To convert the password to the AES key, scrypt is used.  This is an algorithm resistant
 ; to brute force attacks.  You can use the ScryptParameters to tune how difficult you
 ; want this to be generation to be.</p>
 ;
 ; <p>2. Using the AES Key generated above, you then can encrypt and decrypt any bytes using
 ; the AES symmetric cipher.  Eight bytes of salt is used to prevent dictionary attacks.</p>
 ;;
#_public
(§ class #_"KeyCrypterScrypt" (§ implements #_"KeyCrypter")
    #_private
    #_static
    (§ def- #_"Logger" (§ name log) (LoggerFactory/getLogger (§ klass #_"KeyCrypterScrypt")))

    ;;;
     ; Key length in bytes.
     ;;
    #_public
    #_static
    (§ def #_"int" (§ name KEY_LENGTH) 32) ;; = 256 bits.

    ;;;
     ; The size of an AES block in bytes.
     ; This is also the length of the initialisation vector.
     ;;
    #_public
    #_static
    (§ def #_"int" (§ name BLOCK_LENGTH) 16) ;; = 128 bits.

    ;;;
     ; The length of the salt used.
     ;;
    #_public
    #_static
    (§ def #_"int" (§ name SALT_LENGTH) 8)

    #_static
    (§ block
        ;; Init proper random number generator, as some old Android installations have bugs that make it unsecure.
        (§ if (Utils/isAndroidRuntime))
        (§ block
            (§ new #_"LinuxSecureRandom")
        )

        (§ ass (§ name secureRandom) (§ new #_"SecureRandom"))
    )

    #_private
    #_static
    (§ def- #_"SecureRandom" (§ name secureRandom))

    ;;; Returns SALT_LENGTH (8) bytes of random data. ;;
    #_public
    #_static
    (§ defn #_"byte[]" (§ fn randomSalt) [])
    (§ block
        (§ let #_"byte[]" (§ name ßsalt) (§ new #_"byte[]" (§ count SALT_LENGTH)))
        (§ block
            (.. secureRandom (nextBytes ßsalt))
            ßsalt
        )
    )

    ;; Scrypt parameters.
    #_private
    (§ field- #_"ScryptParameters" (§ name scryptParameters))

    ;;;
     ; Encryption/Decryption using default parameters and a random salt.
     ;;
    #_public
    (§ constructor #_"KeyCrypterScrypt" [])
    (§ block
        (§ let #_"Protos.ScryptParameters.Builder" (§ name ßbuilder) (.. (Protos/ScryptParameters/newBuilder) (setSalt (ByteString/copyFrom (randomSalt)))))
        (§ block
            (§ ass (§ name (.. this scryptParameters)) (.. ßbuilder (build)))
            this
        )
    )

    ;;;
     ; Encryption/Decryption using custom number of iterations parameters and a random salt.
     ; As of August 2016, a useful value for mobile devices is 4096 (derivation takes about 1 second).
     ;
     ; @param iterations Number of scrypt iterations.
     ;;
    #_public
    (§ constructor #_"KeyCrypterScrypt" [#_"int" (§ name ßiterations)])
    (§ block
        (§ let #_"Protos.ScryptParameters.Builder" (§ name ßbuilder) (.. (Protos/ScryptParameters/newBuilder) (setSalt (ByteString/copyFrom (randomSalt))) (setN ßiterations)))
        (§ block
            (§ ass (§ name (.. this scryptParameters)) (.. ßbuilder (build)))
            this
        )
    )

    ;;;
     ; Encryption/ Decryption using specified Scrypt parameters.
     ;
     ; @param scryptParameters ScryptParameters to use.
     ; @throws NullPointerException if the scryptParameters or any of its N, R or P is null.
     ;;
    #_public
    (§ constructor #_"KeyCrypterScrypt" [#_"ScryptParameters" (§ name ßscryptParameters)])
    (§ block
        (§ ass (§ name (.. this ßscryptParameters)) (Preconditions/checkNotNull ßscryptParameters))

        ;; Check there is a non-empty salt.  Some early MultiBit wallets has a missing salt, so it is not a hard fail.
        (§ if (or (nil? (.. ßscryptParameters (getSalt))) (nil? (.. ßscryptParameters (getSalt) (toByteArray))) (== (.. ßscryptParameters (getSalt) (toByteArray) length) 0)))
        (§ block
            (.. log (warn "You are using a ScryptParameters with no salt. Your encryption may be vulnerable to a dictionary attack."))
        )
        this
    )

    ;;;
     ; Generate AES key.
     ;
     ; This is a very slow operation compared to encrypt/ decrypt so it is normally worth caching the result.
     ;
     ; @param password The password to use in key generation.
     ; @return the KeyParameter containing the created AES key.
     ; @throws KeyCrypterException
     ;;
    #_override
    #_public
    (§ method #_"KeyParameter" (§ fn deriveKey) [#_"CharSequence" (§ name ßpassword)])
        (§ throws #_"KeyCrypterException")
    (§ block
        (§ let #_"byte[]" (§ name ßpasswordBytes) nil)
        (§ block
            (§ try )
            (§ block
                (§ ass (§ name ßpasswordBytes) (convertToByteArray ßpassword))
                (§ let #_"byte[]" (§ name ßsalt) (§ new #_"byte[]" (§ count 0)))
                (§ block
                    (§ if (!= (.. scryptParameters (getSalt)) nil))
                    (§ block
                        (§ ass (§ name ßsalt) (.. scryptParameters (getSalt) (toByteArray)))
                    )
                    (§ else )
                    (§ block
                        ;; Warn the user that they are not using a salt.  Some early MultiBit wallets had a blank salt.
                        (.. log (warn "You are using a ScryptParameters with no ßsalt. Your encryption may be vulnerable to a dictionary attack."))
                    )

                    (§ let #_"Stopwatch" (§ name ßwatch) (Stopwatch/createStarted))
                    (§ block
                        (§ let #_"byte[]" (§ name ßkeyBytes) (SCrypt/scrypt ßpasswordBytes, ßsalt, (§ cast #_"int" (.. scryptParameters (getN))), (.. scryptParameters (getR)), (.. scryptParameters (getP)), KEY_LENGTH))
                        (§ block
                            (.. ßwatch (stop))
                            (.. log (info "Deriving key took {} for {} scrypt iterations.", ßwatch, (.. scryptParameters (getN))))
                            (§ return (§ new #_"KeyParameter" ßkeyBytes))
                        )
                    )
                )
            )
            (§ catch #_"Exception" (§ name ße))
            (§ block
                (§ throw (§ new #_"KeyCrypterException" "Could not generate key from password and salt.", ße))
            )
            (§ finally )
            (§ block
                ;; Zero the password bytes.
                (§ if (some? ßpasswordBytes))
                (§ block
                    (java.util.Arrays/fill ßpasswordBytes, (§ cast #_"byte" 0))
                )
            )
        )
    )

    ;;;
     ; Password based encryption using AES - CBC 256 bits.
     ;;
    #_override
    #_public
    (§ method #_"EncryptedData" (§ fn encrypt) [#_"byte[]" (§ name ßplainBytes), #_"KeyParameter" (§ name ßaesKey)])
        (§ throws #_"KeyCrypterException")
    (§ block
        (Preconditions/checkNotNull ßplainBytes)
        (Preconditions/checkNotNull ßaesKey)

        (§ try )
        (§ block
            ;; Generate iv - each encryption call has a different iv.
            (§ let #_"byte[]" (§ name ßiv) (§ new #_"byte[]" (§ count BLOCK_LENGTH)))
            (§ block
                (.. secureRandom (nextBytes ßiv))

                (§ let #_"ParametersWithIV" (§ name ßkeyWithIv) (§ new #_"ParametersWithIV" ßaesKey, ßiv))
                (§ block

                    ;; Encrypt using AES.
                    (§ let #_"BufferedBlockCipher" (§ name ßcipher) (§ new #_"PaddedBufferedBlockCipher" (§ new #_"CBCBlockCipher" (§ new #_"AESFastEngine"))))
                    (§ block
                        (.. ßcipher (init true, ßkeyWithIv))
                        (§ let #_"byte[]" (§ name ßencryptedBytes) (§ new #_"byte[]" (§ count (.. ßcipher (getOutputSize (.. ßplainBytes length))))))
                        (§ block
                            (§ let #_"int" (§ name ßlength1) (.. ßcipher (processBytes ßplainBytes, 0, (.. ßplainBytes length), ßencryptedBytes, 0)))
                            (§ block
                                (§ let #_"int" (§ name ßlength2) (.. ßcipher (doFinal ßencryptedBytes, ßlength1)))
                                (§ block

                                    (§ return (§ new #_"EncryptedData" ßiv, (Arrays/copyOf ßencryptedBytes, (+ ßlength1 ßlength2))))
                                )
                            )
                        )
                    )
                )
            )
        )
        (§ catch #_"Exception" (§ name ße))
        (§ block
            (§ throw (§ new #_"KeyCrypterException" "Could not encrypt bytes.", ße))
        )
    )

    ;;;
     ; Decrypt bytes previously encrypted with this class.
     ;
     ; @param dataToDecrypt The data to decrypt.
     ; @param aesKey The AES key to use for decryption.
     ; @return the decrypted bytes.
     ; @throws KeyCrypterException if bytes could not be decrypted.
     ;;
    #_override
    #_public
    (§ method #_"byte[]" (§ fn decrypt) [#_"EncryptedData" (§ name ßdataToDecrypt), #_"KeyParameter" (§ name ßaesKey)])
        (§ throws #_"KeyCrypterException")
    (§ block
        (Preconditions/checkNotNull ßdataToDecrypt)
        (Preconditions/checkNotNull ßaesKey)

        (§ try )
        (§ block
            (§ let #_"ParametersWithIV" (§ name ßkeyWithIv) (§ new #_"ParametersWithIV" (§ new #_"KeyParameter" (.. ßaesKey (getKey))), (.. ßdataToDecrypt initialisationVector)))
            (§ block

                ;; Decrypt the message.
                (§ let #_"BufferedBlockCipher" (§ name ßcipher) (§ new #_"PaddedBufferedBlockCipher" (§ new #_"CBCBlockCipher" (§ new #_"AESFastEngine"))))
                (§ block
                    (.. ßcipher (init false, ßkeyWithIv))

                    (§ let #_"byte[]" (§ name ßcipherBytes) (.. ßdataToDecrypt encryptedBytes))
                    (§ block
                        (§ let #_"byte[]" (§ name ßdecryptedBytes) (§ new #_"byte[]" (§ count (.. ßcipher (getOutputSize (.. ßcipherBytes length))))))
                        (§ block
                            (§ let #_"int" (§ name ßlength1) (.. ßcipher (processBytes ßcipherBytes, 0, (.. ßcipherBytes length), ßdecryptedBytes, 0)))
                            (§ block
                                (§ let #_"int" (§ name ßlength2) (.. ßcipher (doFinal ßdecryptedBytes, ßlength1)))
                                (§ block

                                    (§ return (Arrays/copyOf ßdecryptedBytes, (+ ßlength1 ßlength2)))
                                )
                            )
                        )
                    )
                )
            )
        )
        (§ catch #_"Exception" (§ name ße))
        (§ block
            (§ throw (§ new #_"KeyCrypterException" "Could not decrypt bytes", ße))
        )
    )

    ;;;
     ; Convert a CharSequence (which are UTF16) into a byte array.
     ;
     ; Note: a String.getBytes() is not used to avoid creating a String of the password in the JVM.
     ;;
    #_private
    #_static
    (§ defn- #_"byte[]" (§ fn convertToByteArray) [#_"CharSequence" (§ name ßcharSequence)])
    (§ block
        (Preconditions/checkNotNull ßcharSequence)

        (§ let #_"byte[]" (§ name ßbyteArray) (§ new #_"byte[]" (§ count (.. ßcharSequence (length)) << 1)))
        (§ block
            (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi (.. ßcharSequence (length))) :for (§ recur (+ ßi 1)))
            (§ block
                (§ let #_"int" (§ name ßbytePosition) (<< ßi 1))
                (§ block
                    (§ ass (§ name (§ ai ßbyteArray ßbytePosition)) (§ cast #_"byte" (>> (& (.. ßcharSequence (charAt ßi)) 0xff00) 8)))
                    (§ ass (§ name (§ ai ßbyteArray (+ ßbytePosition 1))) (§ cast #_"byte" (& (.. ßcharSequence (charAt ßi)) 0x00ff)))
                )
            )
            ßbyteArray
        )
    )

    #_public
    (§ method #_"ScryptParameters" (§ fn getScryptParameters) [])
    (§ block
        scryptParameters
    )

    ;;;
     ; Return the EncryptionType enum value which denotes the type of encryption/ decryption that this KeyCrypter
     ; can understand.
     ;;
    #_override
    #_public
    (§ method #_"EncryptionType" (§ fn getUnderstoodEncryptionType) [])
    (§ block
        EncryptionType/ENCRYPTED_SCRYPT_AES
    )

    #_override
    #_public
    (§ method #_"String" (§ fn toString) [])
    (§ block
        (str "AES-" (* KEY_LENGTH 8) "-CBC, Scrypt (N: " (.. scryptParameters (getN)) ")")
    )

    #_override
    #_public
    (§ method #_"int" (§ fn hashCode) [])
    (§ block
        (Objects/hashCode scryptParameters)
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn equals) [#_"Object" (§ name ßo)])
    (§ block
        (§ if (== this ßo))
        (§ block
            (§ return true)
        )
        (§ if (or (nil? ßo) (!= (getClass) (.. ßo (getClass)))))
        (§ block
            (§ return false)
        )
        (Objects/equal scryptParameters, (.. (§ cast #_"KeyCrypterScrypt" ßo) scryptParameters))
    )
)

#_(ns org.bitcoinj.crypto #_"LazyECPoint"
    (:import [java.math BigInteger]
             [java.util Arrays])
    (:import [com.google.common.base Preconditions]
             [org.spongycastle.math.ec ECCurve ECFieldElement ECPoint]))

;;;
 ; A wrapper around ECPoint that delays decoding of the point for as long as possible.  This is useful because point
 ; encode/decode in Bouncy Castle is quite slow especially on Dalvik, as it often involves decompression/recompression.
 ;;
#_public
(§ class #_"LazyECPoint"
    ;; If curve is set, bits is also set.  If curve is unset, point is set and bits is unset.  Point can be set along
    ;; with curve and bits when the cached form has been accessed and thus must have been converted.
    #_private
    (§ field- #_"ECCurve" (§ name curve))
    #_private
    (§ field- #_"byte[]" (§ name bits))

    ;; This field is effectively final - once set it won't change again.  However it can be set after construction.
    #_nilable
    #_private
    (§ field- #_"ECPoint" (§ name point))

    #_public
    (§ constructor #_"LazyECPoint" [#_"ECCurve" (§ name ßcurve), #_"byte[]" (§ name ßbits)])
    (§ block
        (§ ass (§ name (.. this ßcurve)) ßcurve)
        (§ ass (§ name (.. this ßbits)) ßbits)
        this
    )

    #_public
    (§ constructor #_"LazyECPoint" [#_"ECPoint" (§ name ßpoint)])
    (§ block
        (§ ass (§ name (.. this ßpoint)) (Preconditions/checkNotNull ßpoint))
        (§ ass (§ name (.. this curve)) nil)
        (§ ass (§ name (.. this bits)) nil)
        this
    )

    #_public
    (§ method #_"ECPoint" (§ fn get) [])
    (§ block
        (§ if (nil? point))
        (§ block
            (§ ass (§ name point) (.. curve (decodePoint bits)))
        )
        point
    )

    ;; Delegated methods.

    #_public
    (§ method #_"ECPoint" (§ fn getDetachedPoint) [])
    (§ block
        (.. (get) (getDetachedPoint))
    )

    #_public
    (§ method #_"byte[]" (§ fn getEncoded) [])
    (§ block
        (§ if (some? bits))
        (§ block
            (§ return (Arrays/copyOf bits, (.. bits length)))
        )
        (§ else )
        (§ block
            (§ return (.. (get) (getEncoded)))
        )
    )

    #_public
    (§ method #_"boolean" (§ fn isInfinity) [])
    (§ block
        (.. (get) (isInfinity))
    )

    #_public
    (§ method #_"ECPoint" (§ fn timesPow2) [#_"int" (§ name ße)])
    (§ block
        (.. (get) (timesPow2 ße))
    )

    #_public
    (§ method #_"ECFieldElement" (§ fn getYCoord) [])
    (§ block
        (.. (get) (getYCoord))
    )

    #_public
    (§ method #_"ECFieldElement[]" (§ fn getZCoords) [])
    (§ block
        (.. (get) (getZCoords))
    )

    #_public
    (§ method #_"boolean" (§ fn isNormalized) [])
    (§ block
        (.. (get) (isNormalized))
    )

    #_public
    (§ method #_"boolean" (§ fn isCompressed) [])
    (§ block
        (§ if (some? bits))
        (§ block
            (§ return (or (== (§ ai bits 0) 2) (== (§ ai bits 0) 3)))
        )
        (§ else )
        (§ block
            (§ return (.. (get) (isCompressed)))
        )
    )

    #_public
    (§ method #_"ECPoint" (§ fn multiply) [#_"BigInteger" (§ name ßk)])
    (§ block
        (.. (get) (multiply ßk))
    )

    #_public
    (§ method #_"ECPoint" (§ fn subtract) [#_"ECPoint" (§ name ßb)])
    (§ block
        (.. (get) (subtract ßb))
    )

    #_public
    (§ method #_"boolean" (§ fn isValid) [])
    (§ block
        (.. (get) (isValid))
    )

    #_public
    (§ method #_"ECPoint" (§ fn scaleY) [#_"ECFieldElement" (§ name ßscale)])
    (§ block
        (.. (get) (scaleY ßscale))
    )

    #_public
    (§ method #_"ECFieldElement" (§ fn getXCoord) [])
    (§ block
        (.. (get) (getXCoord))
    )

    #_public
    (§ method #_"ECPoint" (§ fn scaleX) [#_"ECFieldElement" (§ name ßscale)])
    (§ block
        (.. (get) (scaleX ßscale))
    )

    #_public
    (§ method #_"boolean" (§ fn equals) [#_"ECPoint" (§ name ßother)])
    (§ block
        (.. (get) (equals ßother))
    )

    #_public
    (§ method #_"ECPoint" (§ fn negate) [])
    (§ block
        (.. (get) (negate))
    )

    #_public
    (§ method #_"ECPoint" (§ fn threeTimes) [])
    (§ block
        (.. (get) (threeTimes))
    )

    #_public
    (§ method #_"ECFieldElement" (§ fn getZCoord) [#_"int" (§ name ßindex)])
    (§ block
        (.. (get) (getZCoord ßindex))
    )

    #_public
    (§ method #_"byte[]" (§ fn getEncoded) [#_"boolean" (§ name ßcompressed)])
    (§ block
        (§ if (and (== ßcompressed (isCompressed)) (some? bits)))
        (§ block
            (§ return (Arrays/copyOf bits, (.. bits length)))
        )
        (§ else )
        (§ block
            (§ return (.. (get) (getEncoded ßcompressed)))
        )
    )

    #_public
    (§ method #_"ECPoint" (§ fn add) [#_"ECPoint" (§ name ßb)])
    (§ block
        (.. (get) (add ßb))
    )

    #_public
    (§ method #_"ECPoint" (§ fn twicePlus) [#_"ECPoint" (§ name ßb)])
    (§ block
        (.. (get) (twicePlus ßb))
    )

    #_public
    (§ method #_"ECCurve" (§ fn getCurve) [])
    (§ block
        (.. (get) (getCurve))
    )

    #_public
    (§ method #_"ECPoint" (§ fn normalize) [])
    (§ block
        (.. (get) (normalize))
    )

    #_public
    (§ method #_"ECFieldElement" (§ fn getY) [])
    (§ block
        (.. this (normalize) (getYCoord))
    )

    #_public
    (§ method #_"ECPoint" (§ fn twice) [])
    (§ block
        (.. (get) (twice))
    )

    #_public
    (§ method #_"ECFieldElement" (§ fn getAffineYCoord) [])
    (§ block
        (.. (get) (getAffineYCoord))
    )

    #_public
    (§ method #_"ECFieldElement" (§ fn getAffineXCoord) [])
    (§ block
        (.. (get) (getAffineXCoord))
    )

    #_public
    (§ method #_"ECFieldElement" (§ fn getX) [])
    (§ block
        (.. this (normalize) (getXCoord))
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn equals) [#_"Object" (§ name ßo)])
    (§ block
        (§ if (== this ßo))
        (§ block
            (§ return true)
        )
        (§ if (or (nil? ßo) (!= (getClass) (.. ßo (getClass)))))
        (§ block
            (§ return false)
        )
        (Arrays/equals (getCanonicalEncoding), (.. (§ cast #_"LazyECPoint" ßo) (getCanonicalEncoding)))
    )

    #_override
    #_public
    (§ method #_"int" (§ fn hashCode) [])
    (§ block
        (Arrays/hashCode (getCanonicalEncoding))
    )

    #_private
    (§ method- #_"byte[]" (§ fn getCanonicalEncoding) [])
    (§ block
        (getEncoded true)
    )
)

#_(ns org.bitcoinj.crypto #_"LinuxSecureRandom"
    (:import [java.io *]
             [java.security *])
    (:import [org.slf4j *]))

;;;
 ; A SecureRandom implementation that is able to override the standard JVM provided implementation, and which simply
 ; serves random numbers by reading /dev/urandom.  That is, it delegates to the kernel on UNIX systems and is unusable
 ; on other platforms.  Attempts to manually set the seed are ignored.  There is no difference between seed bytes and
 ; non-seed bytes, they are all from the same source.
 ;;
#_public
(§ class #_"LinuxSecureRandom" (§ extends #_"SecureRandomSpi")
    #_private
    #_static
    (§ def- #_"FileInputStream" (§ name urandom))

    #_private
    #_static
    (§ class #_"LinuxSecureRandomProvider" (§ extends #_"Provider")
        #_public
        (§ constructor #_"LinuxSecureRandomProvider" [])
        (§ block
            (§ super "LinuxSecureRandom", 1.0, "A Linux specific random number provider that uses /dev/urandom")

            (put "SecureRandom.LinuxSecureRandom", (.. (§ klass #_"LinuxSecureRandom") (getName)))
            this
        )
    )

    #_private
    #_static
    (§ def- #_"Logger" (§ name log) (LoggerFactory/getLogger (§ klass #_"LinuxSecureRandom")))

    #_static
    (§ block
        (§ try )
        (§ block
            (§ let #_"File" (§ name ßfile) (§ new #_"File" "/dev/urandom"))
            (§ block
                ;; This stream is deliberately leaked.
                (§ ass (§ name urandom) (§ new #_"FileInputStream" ßfile))
                (§ if (== (.. urandom (read)) -1))
                (§ block
                    (§ throw (§ new #_"RuntimeException" "/dev/urandom not readable?"))
                )

                ;; Now override the default SecureRandom implementation with this one.
                (§ let #_"int" (§ name ßposition) (Security/insertProviderAt (§ new #_"LinuxSecureRandomProvider"), 1))
                (§ block

                    (§ if (!= ßposition -1))
                    (§ block
                        (.. log (info "Secure randomness will be read from {} only.", ßfile))
                    )
                    (§ else )
                    (§ block
                        (.. log (info "Randomness is already secure."))
                    )
                )
            )
        )
        (§ catch #_"FileNotFoundException" (§ name ße))
        (§ block
            ;; Should never happen.
            (.. log (error "/dev/urandom does not appear to exist or is not openable"))
            (§ throw (§ new #_"RuntimeException" ße))
        )
        (§ catch #_"IOException" (§ name ße))
        (§ block
            (.. log (error "/dev/urandom does not appear to be readable"))
            (§ throw (§ new #_"RuntimeException" ße))
        )
    )

    #_private
    (§ field- #_"DataInputStream" (§ name dis))

    #_public
    (§ constructor #_"LinuxSecureRandom" [])
    (§ block
        ;; DataInputStream is not thread safe, so each random object has its own.
        (§ ass (§ name dis) (§ new #_"DataInputStream" urandom))
        this
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn engineSetSeed) [#_"byte[]" (§ name ßbytes)])
    (§ block
        ;; Ignore.
        nil
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn engineNextBytes) [#_"byte[]" (§ name ßbytes)])
    (§ block
        (§ try )
        (§ block
            (.. dis (readFully ßbytes)) ;; This will block until all the bytes can be read.
        )
        (§ catch #_"IOException" (§ name ße))
        (§ block
            (§ throw (§ new #_"RuntimeException" ße)) ;; Fatal error.  Do not attempt to recover from this.
        )
        nil
    )

    #_override
    #_protected
    (§ method #_"byte[]" (§ fn engineGenerateSeed) [#_"int" (§ name ßi)])
    (§ block
        (§ let #_"byte[]" (§ name ßbits) (§ new #_"byte[]" (§ count ßi)))
        (§ block
            (engineNextBytes ßbits)
            ßbits
        )
    )
)

#_(ns org.bitcoinj.crypto #_"MnemonicCode"
    (:import [java.io BufferedReader FileNotFoundException IOException InputStream InputStreamReader]
             [java.security MessageDigest]
             [java.util ArrayList Collections List])
    (:import [com.google.common.base Stopwatch]
             [org.slf4j Logger LoggerFactory])
   (:require [org.bitcoinj.core Sha256Hash Utils]
             #_static [org.bitcoinj.core.Utils HEX]))

;;;
 ; A MnemonicCode object may be used to convert between binary seed values and lists of words per
 ; <a href="https://github.com/bitcoin/bips/blob/master/bip-0039.mediawiki">the BIP 39 specification</a>.
 ;;

#_public
(§ class #_"MnemonicCode"
    #_private
    #_static
    (§ def- #_"Logger" (§ name log) (LoggerFactory/getLogger (§ klass #_"MnemonicCode")))

    #_private
    (§ field- #_"ArrayList<String>" (§ name wordList))

    #_private
    #_static
    (§ def- #_"String" (§ name BIP39_ENGLISH_RESOURCE_NAME) "mnemonic/wordlist/english.txt")
    #_private
    #_static
    (§ def- #_"String" (§ name BIP39_ENGLISH_SHA256) "ad90bf3beb7b0eb7e5acd74727dc0da96e0a280a258354e7293fb7e211ac03db")

    ;;; UNIX time for when the BIP39 standard was finalised.  This can be used as a default seed birthday. ;;
    #_public
    #_static
    (§ def #_"long" (§ name BIP39_STANDARDISATION_TIME_SECS) 1381276800)

    #_private
    #_static
    (§ def- #_"int" (§ name PBKDF2_ROUNDS) 2048)

    #_public
    #_static
    (§ def #_"MnemonicCode" (§ name INSTANCE))

    #_static
    (§ block
        (§ try )
        (§ block
            (§ ass (§ name INSTANCE) (§ new #_"MnemonicCode"))
        )
        (§ catch #_"FileNotFoundException" (§ name ße))
        (§ block
            ;; We expect failure on Android.  The developer has to set INSTANCE themselves.
            (§ if (not (Utils/isAndroidRuntime)))
            (§ block
                (.. log (error "Could not find word list", ße))
            )
        )
        (§ catch #_"IOException" (§ name ße))
        (§ block
            (.. log (error "Failed to load word list", ße))
        )
    )

    ;;; Initialise from the included word list.  Won't work on Android. ;;
    #_public
    (§ constructor #_"MnemonicCode" [])
        (§ throws #_"IOException")
    (§ block
        (§ this (openDefaultWords), BIP39_ENGLISH_SHA256)
        this
    )

    #_private
    #_static
    (§ defn- #_"InputStream" (§ fn openDefaultWords) [])
        (§ throws #_"IOException")
    (§ block
        (§ let #_"InputStream" (§ name ßstream) (.. (§ klass #_"MnemonicCode") (getResourceAsStream BIP39_ENGLISH_RESOURCE_NAME)))
        (§ block
            (§ if (nil? ßstream))
            (§ block
                (§ throw (§ new #_"FileNotFoundException" BIP39_ENGLISH_RESOURCE_NAME))
            )
            ßstream
        )
    )

    ;;;
     ; Creates an MnemonicCode object, initializing with words read from the supplied input stream.
     ; If a wordListDigest is supplied, the digest of the words will be checked.
     ;;
    #_public
    (§ constructor #_"MnemonicCode" [#_"InputStream" (§ name ßwordstream), #_"String" (§ name ßwordListDigest)])
        (§ throws #_"IOException", #_"IllegalArgumentException")
    (§ block
        (§ let #_"BufferedReader" (§ name ßbr) (§ new #_"BufferedReader" (§ new #_"InputStreamReader" ßwordstream, "UTF-8")))
        (§ block
            (§ ass (§ name (.. this wordList)) (§ new #_"ArrayList<>" 2048))
            (§ let #_"MessageDigest" (§ name ßmd) (Sha256Hash/newDigest))
            (§ block
                (§ let #_"String" (§ name ßword))
                (§ block
                    (§ while (!= (§ ass (§ name ßword) (.. ßbr (readLine))) nil))
                    (§ block
                        (.. ßmd (update (.. ßword (getBytes))))
                        (.. this wordList (add ßword))
                    )
                    (.. ßbr (close))

                    (§ if (!= (.. this wordList (size)) 2048))
                    (§ block
                        (§ throw (§ new #_"IllegalArgumentException" "input stream did not contain 2048 words"))
                    )

                    ;; If a wordListDigest is supplied, check to make sure it matches.
                    (§ if (some? ßwordListDigest))
                    (§ block
                        (§ let #_"byte[]" (§ name ßdigest) (.. ßmd (digest)))
                        (§ block
                            (§ let #_"String" (§ name ßhexdigest) (.. HEX (encode ßdigest)))
                            (§ block
                                (§ if (not (.. ßhexdigest (equals ßwordListDigest))))
                                (§ block
                                    (§ throw (§ new #_"IllegalArgumentException" "wordlist digest mismatch"))
                                )
                            )
                        )
                    )
                    this
                )
            )
        )
    )

    ;;;
     ; Gets the word list this code uses.
     ;;
    #_public
    (§ method #_"List<String>" (§ fn getWordList) [])
    (§ block
        wordList
    )

    ;;;
     ; Convert mnemonic word list to seed.
     ;;
    #_public
    #_static
    (§ defn #_"byte[]" (§ fn toSeed) [#_"List<String>" (§ name ßwords), #_"String" (§ name ßpassphrase)])
    (§ block
        ;; To create binary seed from mnemonic, we use PBKDF2 function with mnemonic sentence (in UTF-8) used as a password
        ;; and string "mnemonic" + passphrase (again in UTF-8) used as a salt.  Iteration count is set to 4096 and HMAC-SHA512
        ;; is used as a pseudo-random function.  Desired length of the derived key is 512 bits (= 64 bytes).

        (§ let #_"String" (§ name ßpass) (.. Utils/SPACE_JOINER (join ßwords)))
        (§ block
            (§ let #_"String" (§ name ßsalt) (str "mnemonic" ßpassphrase))
            (§ block

                (§ let #_"Stopwatch" (§ name ßwatch) (Stopwatch/createStarted))
                (§ block
                    (§ let #_"byte[]" (§ name ßseed) (PBKDF2SHA512/derive ßpass, ßsalt, PBKDF2_ROUNDS, 64))
                    (§ block
                        (.. ßwatch (stop))
                        (.. log (info "PBKDF2 took {}", ßwatch))
                        ßseed
                    )
                )
            )
        )
    )

    ;;;
     ; Convert mnemonic word list to original entropy value.
     ;;
    #_public
    (§ method #_"byte[]" (§ fn toEntropy) [#_"List<String>" (§ name ßwords)])
        (§ throws #_"MnemonicException.MnemonicLengthException", #_"MnemonicException.MnemonicWordException", #_"MnemonicException.MnemonicChecksumException")
    (§ block
        (§ if (< 0 (% (.. ßwords (size)) 3)))
        (§ block
            (§ throw (§ new #_"MnemonicException.MnemonicLengthException" "Word list size must be multiple of three words."))
        )

        (§ if (== (.. ßwords (size)) 0))
        (§ block
            (§ throw (§ new #_"MnemonicException.MnemonicLengthException" "Word list is empty."))
        )

        ;; Look up all the words in the list and construct the concatenation of the original entropy and the checksum.

        (§ let #_"int" (§ name ßconcatLenBits) (* (.. ßwords (size)) 11))
        (§ block
            (§ let #_"boolean[]" (§ name ßconcatBits) (§ new #_"boolean[]" (§ count ßconcatLenBits)))
            (§ block
                (§ let #_"int" (§ name ßwordindex) 0)
                (§ block
                    (§ for (§ var #_"String" (§ name ßword)) :for ßwords)
                    (§ block
                        ;; Find the words index in the wordlist.
                        (§ let #_"int" (§ name ßndx) (Collections/binarySearch (.. this wordList), ßword))
                        (§ block
                            (§ if (< ßndx 0))
                            (§ block
                                (§ throw (§ new #_"MnemonicException.MnemonicWordException" ßword))
                            )

                            ;; Set the next 11 bits to the value of the index.
                            (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi 11) :for (§ recur (+ ßi 1)))
                            (§ block
                                (§ ass (§ name (§ ai ßconcatBits (+ (* ßwordindex 11) ßi))) (!= (& ßndx (<< 1 (- 10 ßi))) 0))
                            )
                            (§ ass (§ name ßwordindex) (+ ßwordindex 1))
                        )
                    )

                    (§ let #_"int" (§ name ßchecksumLengthBits) (/ ßconcatLenBits 33))
                    (§ block
                        (§ let #_"int" (§ name ßentropyLengthBits) (- ßconcatLenBits ßchecksumLengthBits))
                        (§ block

                            ;; Extract original entropy as bytes.
                            (§ let #_"byte[]" (§ name ßentropy) (§ new #_"byte[]" (§ count ßentropyLengthBits / 8)))
                            (§ block
                                (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi (.. ßentropy length)) :for (§ recur (+ ßi 1)))
                                (§ block
                                    (§ for (§ var #_"int" (§ name ßj) 0) :for (< ßj 8) :for (§ recur (+ ßj 1)))
                                    (§ block
                                        (§ if (§ ai ßconcatBits (+ (* ßi 8) ßj)))
                                        (§ block
                                            (§ ass (§ name (§ ai ßentropy ßi)) (| (§ ai ßentropy ßi) (<< 1 (- 7 ßj))))
                                        )
                                    )
                                )

                                ;; Take the digest of the entropy.
                                (§ let #_"byte[]" (§ name ßhash) (Sha256Hash/hash ßentropy))
                                (§ block
                                    (§ let #_"boolean[]" (§ name ßhashBits) (bytesToBits ßhash))
                                    (§ block

                                        ;; Check all the checksum bits.
                                        (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi ßchecksumLengthBits) :for (§ recur (+ ßi 1)))
                                        (§ block
                                            (§ if (!= (§ ai ßconcatBits (+ ßentropyLengthBits ßi)) (§ ai ßhashBits ßi)))
                                            (§ block
                                                (§ throw (§ new #_"MnemonicException.MnemonicChecksumException"))
                                            )
                                        )

                                        ßentropy
                                    )
                                )
                            )
                        )
                    )
                )
            )
        )
    )

    ;;;
     ; Convert entropy data to mnemonic word list.
     ;;
    #_public
    (§ method #_"List<String>" (§ fn toMnemonic) [#_"byte[]" (§ name ßentropy)])
        (§ throws #_"MnemonicException.MnemonicLengthException")
    (§ block
        (§ if (< 0 (% (.. ßentropy length) 4)))
        (§ block
            (§ throw (§ new #_"MnemonicException.MnemonicLengthException" "Entropy length not multiple of 32 bits."))
        )

        (§ if (== (.. ßentropy length) 0))
        (§ block
            (§ throw (§ new #_"MnemonicException.MnemonicLengthException" "Entropy is empty."))
        )

        ;; We take initial entropy of ENT bits and compute its checksum by taking first ENT / 32 bits of its SHA256 hash.

        (§ let #_"byte[]" (§ name ßhash) (Sha256Hash/hash ßentropy))
        (§ block
            (§ let #_"boolean[]" (§ name ßhashBits) (bytesToBits ßhash))
            (§ block

                (§ let #_"boolean[]" (§ name ßentropyBits) (bytesToBits ßentropy))
                (§ block
                    (§ let #_"int" (§ name ßchecksumLengthBits) (/ (.. ßentropyBits length) 32))
                    (§ block

                        ;; We append these bits to the end of the initial entropy.
                        (§ let #_"boolean[]" (§ name ßconcatBits) (§ new #_"boolean[]" (§ count (.. ßentropyBits length) + ßchecksumLengthBits)))
                        (§ block
                            (System/arraycopy ßentropyBits, 0, ßconcatBits, 0, (.. ßentropyBits length))
                            (System/arraycopy ßhashBits, 0, ßconcatBits, (.. ßentropyBits length), ßchecksumLengthBits)

                            ;; Next we take these concatenated bits and split them into groups of 11 bits.  Each group encodes number from 0-2047
                            ;; which is a position in a wordlist.  We convert numbers into words and use joined words as mnemonic sentence.

                            (§ let #_"ArrayList<String>" (§ name ßwords) (§ new #_"ArrayList<>"))
                            (§ block
                                (§ let #_"int" (§ name ßnwords) (/ (.. ßconcatBits length) 11))
                                (§ block
                                    (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi ßnwords) :for (§ recur (+ ßi 1)))
                                    (§ block
                                        (§ let #_"int" (§ name ßindex) 0)
                                        (§ block
                                            (§ for (§ var #_"int" (§ name ßj) 0) :for (< ßj 11) :for (§ recur (+ ßj 1)))
                                            (§ block
                                                (§ ass (§ name ßindex) (<< ßindex 1))
                                                (§ if (§ ai ßconcatBits (+ (* ßi 11) ßj)))
                                                (§ block
                                                    (§ ass (§ name ßindex) (| ßindex 0x1))
                                                )
                                            )
                                            (.. ßwords (add (.. this wordList (get ßindex))))
                                        )
                                    )

                                    ßwords
                                )
                            )
                        )
                    )
                )
            )
        )
    )

    ;;;
     ; Check to see if a mnemonic word list is valid.
     ;;
    #_public
    (§ method #_"void" (§ fn check) [#_"List<String>" (§ name ßwords)])
        (§ throws #_"MnemonicException")
    (§ block
        (toEntropy ßwords)
        nil
    )

    #_private
    #_static
    (§ defn- #_"boolean[]" (§ fn bytesToBits) [#_"byte[]" (§ name ßdata)])
    (§ block
        (§ let #_"boolean[]" (§ name ßbits) (§ new #_"boolean[]" (§ count (.. ßdata length) * 8)))
        (§ block
            (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi (.. ßdata length)) :for (§ recur (+ ßi 1)))
            (§ block
                (§ for (§ var #_"int" (§ name ßj) 0) :for (< ßj 8) :for (§ recur (+ ßj 1)))
                (§ block
                    (§ ass (§ name (§ ai ßbits (+ (* ßi 8) ßj))) (!= (& (§ ai ßdata ßi) (<< 1 (- 7 ßj))) 0))
                )
            )
            ßbits
        )
    )
)

