    #_public
    (§ constructor #_"WalletAppKit" [#_"NetworkParameters" (§ name ßparams), #_"File" (§ name ßdirectory), #_"String" (§ name ßfilePrefix)])
    (§ block
        (§ this (§ new #_"Context" ßparams), ßdirectory, ßfilePrefix)
        this
    )

    ;;;
     ; Creates a new WalletAppKit, with the given {@link Context}.  Files will be stored in the given directory.
     ;;
    #_public
    (§ constructor #_"WalletAppKit" [#_"Context" (§ name ßcontext), #_"File" (§ name ßdirectory), #_"String" (§ name ßfilePrefix)])
    (§ block
        (§ ass (§ name (.. this ßcontext)) ßcontext)
        (§ ass (§ name (.. this params)) (Preconditions/checkNotNull (.. ßcontext (getParams))))
        (§ ass (§ name (.. this ßdirectory)) (Preconditions/checkNotNull ßdirectory))
