    )

    ;;; Will only connect to localhost.  Cannot be called after startup. ;;
    #_public
    (§ method #_"WalletAppKit" (§ fn connectToLocalHost) [])
    (§ block
        (§ try )
        (§ block
            (§ let #_"InetAddress" (§ name ßlocalHost) (InetAddress/getLocalHost))
            (§ block
                (§ return (setPeerNodes (§ new #_"PeerAddress" params, ßlocalHost, (.. params (getPort)))))
            )
        )
        (§ catch #_"UnknownHostException" (§ name ße))
        (§ block
            ;; Borked machine with no loopback adapter configured properly.
            (§ throw (§ new #_"RuntimeException" ße))
        )
    )

    ;;; If true, the wallet will save itself to disk automatically whenever it changes. ;;
    #_public
    (§ method #_"WalletAppKit" (§ fn setAutoSave) [#_"boolean" (§ name ßvalue)])
    (§ block
        (Preconditions/checkState (== (state) State/NEW), "Cannot call after startup")
        (§ ass (§ name useAutoSave) ßvalue)
        this
    )

    ;;;
     ; If you want to learn about the sync process, you can provide a listener here.  For instance,
     ; a {@link org.bitcoinj.core.DownloadProgressTracker} is a good choice.  This has no effect unless
     ; setBlockingStartup(false) has been called too, due to some missing implementation code.
     ;;
    #_public
    (§ method #_"WalletAppKit" (§ fn setDownloadListener) [#_"DownloadProgressTracker" (§ name ßlistener)])
    (§ block
        (§ ass (§ name (.. this downloadListener)) ßlistener)
        this
    )

    ;;; If true, will register a shutdown hook to stop the library.  Defaults to true. ;;
    #_public
    (§ method #_"WalletAppKit" (§ fn setAutoStop) [#_"boolean" (§ name ßautoStop)])
    (§ block
        (§ ass (§ name (.. this ßautoStop)) ßautoStop)
        this
    )

    ;;;
     ; If set, the file is expected to contain a checkpoints file calculated with BuildCheckpoints.
     ; It makes initial block sync faster for new users - please refer to the documentation on the
     ; bitcoinj website (https://bitcoinj.github.io/speeding-up-chain-sync) for further details.
     ;;
    #_public
    (§ method #_"WalletAppKit" (§ fn setCheckpoints) [#_"InputStream" (§ name ßcheckpoints)])
    (§ block
        (§ if (!= (.. this ßcheckpoints) nil))
        (§ block
            (Utils/closeUnchecked (.. this ßcheckpoints))
        )
        (§ ass (§ name (.. this ßcheckpoints)) (Preconditions/checkNotNull ßcheckpoints))
        this
    )

    ;;;
     ; If true (the default) then the startup of this service won't be considered complete until the network has been
     ; brought up, peer connections established and the block chain synchronised.  Therefore {@link #awaitRunning()} can
     ; potentially take a very long time.  If false, then startup is considered complete once the network activity
     ; begins and peer connections/block chain sync will continue in the background.
     ;;
    #_public
    (§ method #_"WalletAppKit" (§ fn setBlockingStartup) [#_"boolean" (§ name ßblockingStartup)])
    (§ block
        (§ ass (§ name (.. this ßblockingStartup)) ßblockingStartup)
        this
    )

    ;;;
     ; Sets the string that will appear in the subver field of the version message.
     ; @param userAgent A short string that should be the name of your app, e.g. "My Wallet".
     ; @param version A short string that contains the version number, e.g. "1.0-BETA".
     ;;
    #_public
    (§ method #_"WalletAppKit" (§ fn setUserAgent) [#_"String" (§ name ßuserAgent), #_"String" (§ name ßversion)])
    (§ block
        (§ ass (§ name (.. this ßuserAgent)) (Preconditions/checkNotNull ßuserAgent))
        (§ ass (§ name (.. this ßversion)) (Preconditions/checkNotNull ßversion))
        this
    )

    ;;;
     ; Sets a wallet factory which will be used when the kit creates a new wallet.
     ;;
    #_public
    (§ method #_"WalletAppKit" (§ fn setWalletFactory) [#_"WalletProtobufSerializer.WalletFactory" (§ name ßwalletFactory)])
    (§ block
        (§ ass (§ name (.. this ßwalletFactory)) ßwalletFactory)
        this
    )

    ;;;
     ; If a seed is set here then any existing wallet that matches the file name will be renamed to a backup name,
     ; the chain file will be deleted, and the wallet object will be instantiated with the given seed instead of
     ; a fresh one being created.  This is intended for restoring a wallet from the original seed.  To implement
     ; restore, you would shut down the existing appkit, if any, then recreate it with the seed given by the user,
     ; then start up the new kit.  The next time your app starts it should work as normal (that is, don't keep
     ; calling this each time).
     ;;
    #_public
    (§ method #_"WalletAppKit" (§ fn restoreWalletFromSeed) [#_"DeterministicSeed" (§ name ßseed)])
    (§ block
        (§ ass (§ name (.. this restoreFromSeed)) ßseed)
        this
    )

    ;;;
     ; Sets the peer discovery class to use.  If none is provided then DNS is used, which is a reasonable default.
     ;;
    #_public
    (§ method #_"WalletAppKit" (§ fn setDiscovery) [#_nilable #_"PeerDiscovery" (§ name ßdiscovery)])
    (§ block
        (§ ass (§ name (.. this ßdiscovery)) ßdiscovery)
        this
    )

    ;;;
     ; Override this to use a {@link BlockStore} that isn't the default of {@link SPVBlockStore}.
     ;;
    #_protected
    (§ method #_"BlockStore" (§ fn provideBlockStore) [#_"File" (§ name ßfile)])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ new #_"SPVBlockStore" params, ßfile)
    )

    ;;;
     ; This method is invoked on a background thread after all objects are initialised, but before the peer group
     ; or block chain download is started.  You can tweak the objects configuration here.
     ;;
    #_protected
    (§ method #_"void" (§ fn onSetupCompleted) [])
    (§ block
        nil
    )

    ;;;
     ; Tests to see if the spvchain file has an operating system file lock on it.  Useful for checking if your app
     ; is already running.  If another copy of your app is running and you start the appkit anyway, an exception will
     ; be thrown during the startup process.  Returns false if the chain file does not exist or is a directory.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isChainFileLocked) [])
        (§ throws #_"IOException")
    (§ block
        (§ let #_"RandomAccessFile" (§ name ßfile2) nil)
        (§ block
            (§ try )
            (§ block
                (§ let #_"File" (§ name ßfile) (§ new #_"File" directory, (str filePrefix ".spvchain")))
                (§ block
                    (§ if (not (.. ßfile (exists))))
                    (§ block
                        (§ return false)
                    )
                    (§ if (.. ßfile (isDirectory)))
                    (§ block
                        (§ return false)
                    )
                    (§ ass (§ name ßfile2) (§ new #_"RandomAccessFile" ßfile, "rw"))
                    (§ let #_"FileLock" (§ name ßlock) (.. ßfile2 (getChannel) (tryLock)))
                    (§ block
                        (§ if (nil? ßlock))
                        (§ block
                            (§ return true)
                        )
                        (.. ßlock (release))
                        (§ return false)
                    )
                )
            )
            (§ finally )
            (§ block
                (§ if (some? ßfile2))
                (§ block
                    (.. ßfile2 (close))
                )
            )
        )
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn startUp) [])
        (§ throws #_"Exception")
    (§ block
        ;; Runs in a separate thread.
        (Context/propagate context)
        (§ if (and (not (.. directory (exists))) (not (.. directory (mkdirs)))))
        (§ block
            (§ throw (§ new #_"IOException" (str "Could not create directory " (.. directory (getAbsolutePath)))))
        )

        (.. log (info "Starting up with directory = {}", directory))
        (§ try )
        (§ block
            (§ let #_"File" (§ name ßchainFile) (§ new #_"File" directory, (str filePrefix ".spvchain")))
            (§ block
                (§ let #_"boolean" (§ name ßchainFileExists) (.. ßchainFile (exists)))
                (§ block
                    (§ ass (§ name vWalletFile) (§ new #_"File" directory, (str filePrefix ".wallet")))
                    (§ let #_"boolean" (§ name ßshouldReplayWallet) (or (and (.. vWalletFile (exists)) (not ßchainFileExists)) (some? restoreFromSeed)))
                    (§ block
                        (§ ass (§ name vWallet) (createOrLoadWallet ßshouldReplayWallet))

                        ;; Initiate Bitcoin network objects (block store, blockchain and peer group).
                        (§ ass (§ name vStore) (provideBlockStore ßchainFile))
                        (§ if (or (not ßchainFileExists) (some? restoreFromSeed)))
                        (§ block
                            (§ if (and (nil? checkpoints) (not (Utils/isAndroidRuntime))))
                            (§ block
                                (§ ass (§ name checkpoints) (CheckpointManager/openStream params))
                            )

                            (§ if (some? checkpoints))
                            (§ block
                                ;; Initialize the chain file with a checkpoint to speed up first-run sync.
                                (§ let #_"long" (§ name ßtime))
                                (§ block
                                    (§ if (some? restoreFromSeed))
                                    (§ block
                                        (§ ass (§ name ßtime) (.. restoreFromSeed (getCreationTimeSeconds)))
                                        (§ if ßchainFileExists)
                                        (§ block
                                            (.. log (info "Deleting the chain file in preparation from restore."))
                                            (.. vStore (close))
                                            (§ if (not (.. ßchainFile (delete))))
                                            (§ block
                                                (§ throw (§ new #_"IOException" "Failed to delete chain file in preparation for restore."))
                                            )

                                            (§ ass (§ name vStore) (§ new #_"SPVBlockStore" params, ßchainFile))
                                        )
                                    )
                                    (§ else )
                                    (§ block
                                        (§ ass (§ name ßtime) (.. vWallet (getEarliestKeyCreationTime)))
                                    )
                                    (§ if (< 0 ßtime))
                                    (§ block
                                        (CheckpointManager/checkpoint params, checkpoints, vStore, ßtime)
                                    )
                                    (§ else )
                                    (§ block
                                        (.. log (warn "Creating a new uncheckpointed block store due to a wallet with a creation ßtime of zero: this will result in a very slow chain sync"))
                                    )
                                )
                            )
                            (§ elseif ßchainFileExists)
                            (§ block
                                (.. log (info "Deleting the chain file in preparation from restore."))
                                (.. vStore (close))
                                (§ if (not (.. ßchainFile (delete))))
                                (§ block
                                    (§ throw (§ new #_"IOException" "Failed to delete chain file in preparation for restore."))
                                )

                                (§ ass (§ name vStore) (§ new #_"SPVBlockStore" params, ßchainFile))
                            )
                        )
                        (§ ass (§ name vChain) (§ new #_"BlockChain" params, vStore))
                        (§ ass (§ name vPeerGroup) (createPeerGroup))
                        (§ if (!= (.. this userAgent) nil))
                        (§ block
                            (.. vPeerGroup (setUserAgent userAgent, version))
                        )

                        ;; Set up peer addresses or discovery first, so if wallet extensions try to broadcast a transaction
                        ;; before we're actually connected the broadcast waits for an appropriate number of connections.
                        (§ if (some? peerAddresses))
                        (§ block
                            (§ for (§ var #_"PeerAddress" (§ name ßaddr)) :for peerAddresses)
                            (§ block
                                (.. vPeerGroup (addAddress ßaddr))
                            )
                            (.. vPeerGroup (setMaxConnections (.. peerAddresses length)))
                            (§ ass (§ name peerAddresses) nil)
                        )
                        (§ else )
                        (§ block
                            (.. vPeerGroup (addPeerDiscovery (or discovery (§ new #_"DnsDiscovery" params))))
                        )
                        (.. vChain (addWallet vWallet))
                        (.. vPeerGroup (addWallet vWallet))
                        (onSetupCompleted)

                        (§ if blockingStartup)
                        (§ block
                            (.. vPeerGroup (start))
                            ;; Make sure we shut down cleanly.
                            (installShutdownHook)

                            ;; TODO: Be able to use the provided download listener when doing a blocking startup.
                            (§ let #_"DownloadProgressTracker" (§ name ßlistener) (§ new #_"DownloadProgressTracker"))
                            (§ block
                                (.. vPeerGroup (startBlockChainDownload ßlistener))
                                (.. ßlistener (await))
                            )
                        )
                        (§ else )
                        (§ block
                            (Futures/addCallback (.. vPeerGroup (startAsync)), (§ new #_"FutureCallback")
                            (§ anon
                                #_override
                                #_public
                                (§ method #_"void" (§ fn onSuccess) [#_nilable #_"Object" (§ name ßresult)])
                                (§ block
                                    (§ let #_"DownloadProgressTracker" (§ name ßl) (or downloadListener (§ new #_"DownloadProgressTracker")))
                                    (§ block
                                        (.. vPeerGroup (startBlockChainDownload ßl))
                                        nil
                                    )
                                )

                                #_override
                                #_public
                                (§ method #_"void" (§ fn onFailure) [#_"Throwable" (§ name ßt)])
                                (§ block
                                    (§ throw (§ new #_"RuntimeException" ßt))
                                )
                            ))
                        )
                    )
                )
            )
        )
        (§ catch #_"BlockStoreException" (§ name ße))
        (§ block
            (§ throw (§ new #_"IOException" ße))
        )
        nil
    )

    #_private
    (§ method- #_"Wallet" (§ fn createOrLoadWallet) [#_"boolean" (§ name ßshouldReplayWallet)])
        (§ throws #_"Exception")
    (§ block
        (§ let #_"Wallet" (§ name ßwallet))
        (§ block

            (maybeMoveOldWalletOutOfTheWay)

            (§ if (.. vWalletFile (exists)))
            (§ block
                (§ ass (§ name ßwallet) (loadWallet ßshouldReplayWallet))
            )
            (§ else )
            (§ block
                (§ ass (§ name ßwallet) (createWallet))
                (.. ßwallet (freshReceiveKey))

                ;; Currently the only way we can be sure that an extension is aware of its containing wallet is
                ;; by deserializing the extension.
                ;; Hence, we first save and then load wallet to ensure any extensions are correctly initialized.
                (.. ßwallet (saveToFile vWalletFile))
                (§ ass (§ name ßwallet) (loadWallet false))
            )

            (§ if useAutoSave)
            (§ block
                (.. this (setupAutoSave ßwallet))
            )

            ßwallet
        )
    )

    #_protected
    (§ method #_"void" (§ fn setupAutoSave) [#_"Wallet" (§ name ßwallet)])
    (§ block
        (.. ßwallet (autosaveToFile vWalletFile, 5, TimeUnit/SECONDS, nil))
        nil
    )

    #_private
    (§ method- #_"Wallet" (§ fn loadWallet) [#_"boolean" (§ name ßshouldReplayWallet)])
        (§ throws #_"Exception")
    (§ block
        (§ let #_"Wallet" (§ name ßwallet))
        (§ block
            (§ let #_"FileInputStream" (§ name ßwalletStream) (§ new #_"FileInputStream" vWalletFile))
            (§ block
                (§ try )
                (§ block
                    (§ let #_"Protos.Wallet" (§ name ßproto) (WalletProtobufSerializer/parseToProto ßwalletStream))
                    (§ block
                        (§ let #_"WalletProtobufSerializer" (§ name ßserializer))
                        (§ block
                            (§ if (some? walletFactory))
                            (§ block
                                (§ ass (§ name ßserializer) (§ new #_"WalletProtobufSerializer" walletFactory))
                            )
                            (§ else )
                            (§ block
                                (§ ass (§ name ßserializer) (§ new #_"WalletProtobufSerializer"))
                            )
                            (§ ass (§ name ßwallet) (.. ßserializer (readWallet params, ßproto)))
                            (§ if ßshouldReplayWallet)
                            (§ block
                                (.. ßwallet (reset))
                            )
                        )
                    )
                )
                (§ finally )
                (§ block
                    (.. ßwalletStream (close))
                )
                ßwallet
            )
        )
    )

    #_protected
    (§ method #_"Wallet" (§ fn createWallet) [])
    (§ block
        (§ let #_"KeyChainGroup" (§ name ßkcg))
        (§ block
            (§ if (some? restoreFromSeed))
            (§ block
                (§ ass (§ name ßkcg) (§ new #_"KeyChainGroup" params, restoreFromSeed))
            )
            (§ else )
            (§ block
                (§ ass (§ name ßkcg) (§ new #_"KeyChainGroup" params))
            )
            (if (some? walletFactory) (.. walletFactory (create params, ßkcg)) (§ new #_"Wallet" params, ßkcg)) ;; default
        )
    )

    #_private
    (§ method- #_"void" (§ fn maybeMoveOldWalletOutOfTheWay) [])
    (§ block
        (§ if (nil? restoreFromSeed))
        (§ block
            (§ return nil)
        )
        (§ if (not (.. vWalletFile (exists))))
        (§ block
            (§ return nil)
        )

        (§ let #_"int" (§ name ßcounter) 1)
        (§ block
            (§ let #_"File" (§ name ßnewName))
            (§ block
                (§ do )
                (§ block
                    (§ ass (§ name ßnewName) (§ new #_"File" (.. vWalletFile (getParent)), (str "Backup " ßcounter " for " (.. vWalletFile (getName)))))
                    (§ ass (§ name ßcounter) (+ ßcounter 1))
                )
                (§ again (.. ßnewName (exists)))

                (.. log (info "Renaming old wallet file {} to {}", vWalletFile, ßnewName))

                ;; This should not happen unless something is really messed up.
                (§ if (not (.. vWalletFile (renameTo ßnewName))))
                (§ block
                    (§ throw (§ new #_"RuntimeException" "Failed to rename wallet for restore"))
                )
                nil
            )
        )
    )

    #_protected
    (§ method #_"PeerGroup" (§ fn createPeerGroup) [])
        (§ throws #_"TimeoutException")
    (§ block
        (§ new #_"PeerGroup" params, vChain)
    )

    #_private
    (§ method- #_"void" (§ fn installShutdownHook) [])
    (§ block
        (§ if autoStop)
        (§ block
            (.. (Runtime/getRuntime) (addShutdownHook (§ new #_"Thread")
            (§ anon
                #_override
                #_public
                (§ method #_"void" (§ fn run) [])
                (§ block
                    (§ try )
                    (§ block
                        (.. (§ dhis WalletAppKit) (stopAsync))
                        (.. (§ dhis WalletAppKit) (awaitTerminated))
                    )
                    (§ catch #_"Exception" (§ name ße))
                    (§ block
                        (§ throw (§ new #_"RuntimeException" ße))
                    )
                    nil
                )
            )))
        )
        nil
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn shutDown) [])
        (§ throws #_"Exception")
    (§ block
        ;; Runs in a separate thread.
        (§ try )
        (§ block
            (Context/propagate context)
            (.. vPeerGroup (stop))
            (.. vWallet (saveToFile vWalletFile))
            (.. vStore (close))

            (§ ass (§ name vPeerGroup) nil)
            (§ ass (§ name vWallet) nil)
            (§ ass (§ name vStore) nil)
            (§ ass (§ name vChain) nil)
        )
        (§ catch #_"BlockStoreException" (§ name ße))
        (§ block
            (§ throw (§ new #_"IOException" ße))
        )
        nil
    )

    #_public
    (§ method #_"NetworkParameters" (§ fn params) [])
    (§ block
        params
    )

    #_public
    (§ method #_"BlockChain" (§ fn chain) [])
    (§ block
        (Preconditions/checkState (or (== (state) State/STARTING) (== (state) State/RUNNING)), "Cannot call until startup is complete")
        vChain
    )

    #_public
    (§ method #_"BlockStore" (§ fn store) [])
    (§ block
        (Preconditions/checkState (or (== (state) State/STARTING) (== (state) State/RUNNING)), "Cannot call until startup is complete")
        vStore
    )

    #_public
    (§ method #_"Wallet" (§ fn wallet) [])
    (§ block
        (Preconditions/checkState (or (== (state) State/STARTING) (== (state) State/RUNNING)), "Cannot call until startup is complete")
        vWallet
    )

    #_public
    (§ method #_"PeerGroup" (§ fn peerGroup) [])
    (§ block
        (Preconditions/checkState (or (== (state) State/STARTING) (== (state) State/RUNNING)), "Cannot call until startup is complete")
        vPeerGroup
    )

    #_public
    (§ method #_"File" (§ fn directory) [])
    (§ block
        directory
    )
)

#_(ns org.bitcoinj.net #_"AbstractTimeoutHandler"
    (:import [java.util Timer TimerTask]))

;;;
 ; <p>A base class which provides basic support for socket timeouts.  It is used instead of integrating timeouts into the
 ; NIO select thread both for simplicity and to keep code shared between NIO and blocking sockets as much as possible.
 ; </p>
 ;;
#_public
#_abstract
(§ class #_"AbstractTimeoutHandler"
    ;; TimerTask and timeout value which are added to a timer to kill the connection on timeout.
    #_private
    (§ field- #_"TimerTask" (§ name timeoutTask))
    #_private
    (§ field- #_"long" (§ name timeoutMillis) 0)
    #_private
    (§ field- #_"boolean" (§ name timeoutEnabled) true)

    ;; A timer which manages expiring channels as their timeouts occur (if configured).
    #_private
    #_static
    (§ def- #_"Timer" (§ name timeoutTimer) (§ new #_"Timer" "AbstractTimeoutHandler timeouts", true))

    ;;;
     ; <p>Enables or disables the timeout entirely.  This may be useful if you want to store the timeout value
     ; but wish to temporarily disable/enable timeouts.</p>
     ;
     ; <p>The default is for timeoutEnabled to be true but timeoutMillis to be set to 0 (i.e. disabled).</p>
     ;
     ; <p>This call will reset the current progress towards the timeout.</p>
     ;;
    #_public
    #_synchronized
    (§ method #_"void" (§ fn setTimeoutEnabled) [#_"boolean" (§ name ßtimeoutEnabled)])
    (§ block
        (§ ass (§ name (.. this ßtimeoutEnabled)) ßtimeoutEnabled)
        (resetTimeout)
        nil
    )

    ;;;
     ; <p>Sets the receive timeout to the given number of milliseconds, automatically killing the connection
     ; if no messages are received for this long.</p>
     ;
     ; <p>A timeout of 0 is interpreted as no timeout.</p>
     ;
     ; <p>The default is for timeoutEnabled to be true but timeoutMillis to be set to 0 (i.e. disabled).</p>
     ;
     ; <p>This call will reset the current progress towards the timeout.</p>
     ;;
    #_public
    #_synchronized
    (§ method #_"void" (§ fn setSocketTimeout) [#_"int" (§ name ßtimeoutMillis)])
    (§ block
        (§ ass (§ name (.. this ßtimeoutMillis)) ßtimeoutMillis)
        (resetTimeout)
        nil
    )

    ;;;
     ; Resets the current progress towards timeout to 0.
     ;;
    #_protected
    #_synchronized
    (§ method #_"void" (§ fn resetTimeout) [])
    (§ block
        (§ if (some? timeoutTask))
        (§ block
            (.. timeoutTask (cancel))
        )
