    #_public
    (§ constructor #_"NioClient" [#_"SocketAddress" (§ name ßserverAddress), #_"StreamConnection" (§ name ßparser), #_"int" (§ name ßconnectTimeoutMillis)])
        (§ throws #_"IOException")
