    )

    #_override
    #_public
    (§ method #_"void" (§ fn closeConnection) [])
    (§ block
        (.. handler writeTarget (closeConnection))
        nil
    )

    #_override
    #_public
    #_synchronized
    (§ method #_"void" (§ fn writeBytes) [#_"byte[]" (§ name ßmessage)])
        (§ throws #_"IOException")
    (§ block
