;;;
 ; A class which manages a set of client connections.  Uses Java NIO to select network events and processes them
 ; in a single network processing thread.
 ;;
#_public
(§ class #_"NioClientManager" (§ extends #_"AbstractExecutionThreadService") (§ implements #_"ClientConnectionManager")
    #_private
    #_static
    (§ def- #_"Logger" (§ name log) (LoggerFactory/getLogger (§ klass #_"NioClientManager")))

    #_private
    (§ field- #_"Selector" (§ name selector))

    (§ class #_"PendingConnect"
        (§ let #_"SocketChannel" (§ name ßsc))
        (§ block
            (§ let #_"StreamConnection" (§ name ßconnection))
            (§ block
                (§ let #_"SocketAddress" (§ name ßaddress))
                (§ block
                    (§ let #_"SettableFuture<SocketAddress>" (§ name ßfuture) (SettableFuture/create))
                    (§ block

                        (§ constructor #_"PendingConnect" [#_"SocketChannel" (§ name ßsc), #_"StreamConnection" (§ name ßconnection), #_"SocketAddress" (§ name ßaddress)])
                        (§ block
                            (§ ass (§ name (.. this ßsc)) ßsc)
                            (§ ass (§ name (.. this ßconnection)) ßconnection)
                            (§ ass (§ name (.. this ßaddress)) ßaddress)
                            this
                        )
                    )
                )
            )
        )
    )
    (§ field #_"Queue<PendingConnect>" (§ name newConnectionChannels) (§ new #_"LinkedBlockingQueue<>"))

    ;; Added to/removed from by the individual ConnectionHandler's, thus must by synchronized on its own.
    #_private
    (§ field- #_"Set<ConnectionHandler>" (§ name connectedHandlers) (Collections/synchronizedSet (§ new #_"HashSet<ConnectionHandler>")))

    ;; Handle a SelectionKey which was selected.
    #_private
    (§ method- #_"void" (§ fn handleKey) [#_"SelectionKey" (§ name ßkey)])
        (§ throws #_"IOException")
    (§ block
        ;; We could have a !isValid() key here if the connection is already closed at this point,
        ;; i.e. a client connection which has finished the initial connect process.
        (§ if (and (.. ßkey (isValid)) (.. ßkey (isConnectable))))
        (§ block
            ;; Create a ConnectionHandler and hook everything together.
            (§ let #_"PendingConnect" (§ name ßdata) (§ cast #_"PendingConnect" (.. ßkey (attachment))))
            (§ block
                (§ let #_"StreamConnection" (§ name ßconnection) (.. ßdata connection))
                (§ block
                    (§ let #_"SocketChannel" (§ name ßsc) (§ cast #_"SocketChannel" (.. ßkey (channel))))
                    (§ block
                        (§ let #_"ConnectionHandler" (§ name ßhandler) (§ new #_"ConnectionHandler" ßconnection, ßkey, connectedHandlers))
                        (§ block
                            (§ try )
                            (§ block
                                (§ if (.. ßsc (finishConnect)))
                                (§ block
                                    (.. log (info "Connected to {}", (.. ßsc (socket) (getRemoteSocketAddress))))
                                    (.. ßkey (interestOps (& (| (.. ßkey (interestOps)) SelectionKey/OP_READ) (bit-not SelectionKey/OP_CONNECT))) (attach ßhandler))
                                    (.. ßconnection (connectionOpened))
                                    (.. ßdata future (set (.. ßdata address)))
                                )
                                (§ else )
                                (§ block
                                    (.. log (warn "Failed to connect to {}", (.. ßsc (socket) (getRemoteSocketAddress))))
                                    (.. ßhandler (closeConnection)) ;; Failed to connect for some reason.
                                    (.. ßdata future (setException (§ new #_"ConnectException" "Unknown reason")))
                                    (§ ass (§ name (.. ßdata future)) nil)
                                )
                            )
                            (§ catch #_"Exception" (§ name ße))
                            (§ block
                                ;; If e is a CancelledKeyException, there is a race to get to interestOps after finishConnect() which
                                ;; may cause this.  Otherwise it may be any arbitrary kind of connection failure.
                                ;; Calling sc.socket().getRemoteSocketAddress() here throws an exception, so we can only log the error itself.
                                (§ let #_"Throwable" (§ name ßcause) (Throwables/getRootCause ße))
                                (§ block
                                    (.. log (warn "Failed to connect with exception: {}: {}", (.. ßcause (getClass) (getName)), (.. ßcause (getMessage)), ße))
                                    (.. ßhandler (closeConnection))
                                    (.. ßdata future (setException ßcause))
                                    (§ ass (§ name (.. ßdata future)) nil)
                                )
                            )
                        )
                    )
                )
            )
        )
        (§ else ) ;; Process bytes read.
        (§ block
            (ConnectionHandler/handleKey ßkey)
        )
