    )

    ;;;
     ; Creates a new client manager which uses Java NIO for socket management.
     ; Uses a single thread to handle all select calls.
     ;;
    #_public
    (§ constructor #_"NioClientManager" [])
    (§ block
        (§ try )
        (§ block
            (§ ass (§ name selector) (.. (SelectorProvider/provider) (openSelector)))
        )
        (§ catch #_"IOException" (§ name ße))
        (§ block
            (§ throw (§ new #_"RuntimeException" ße)) ;; Shouldn't ever happen.
        )
        this
    )

    #_override
    #_public
    (§ method #_"void" (§ fn run) [])
    (§ block
        (§ try )
        (§ block
            (.. (Thread/currentThread) (setPriority Thread/MIN_PRIORITY))
            (§ while (isRunning))
            (§ block
                (§ let #_"PendingConnect" (§ name ßconn))
                (§ block
                    (§ while (!= (§ ass (§ name ßconn) (.. newConnectionChannels (poll))) nil))
                    (§ block
                        (§ try )
                        (§ block
                            (§ let #_"SelectionKey" (§ name ßkey) (.. ßconn sc (register selector, SelectionKey/OP_CONNECT)))
                            (§ block
                                (.. ßkey (attach ßconn))
                            )
                        )
                        (§ catch #_"ClosedChannelException" (§ name _))
                        (§ block
                            (.. log (warn "SocketChannel was closed before it could be registered"))
                        )
                    )

                    (.. selector (select))

                    (§ let #_"Iterator<SelectionKey>" (§ name ßkeyIterator) (.. selector (selectedKeys) (iterator)))
                    (§ block
                        (§ while (.. ßkeyIterator (hasNext)))
                        (§ block
                            (§ let #_"SelectionKey" (§ name ßkey) (.. ßkeyIterator (next)))
                            (§ block
                                (.. ßkeyIterator (remove))
                                (handleKey ßkey)
                            )
                        )
                    )
                )
            )
        )
        (§ catch #_"Exception" (§ name ße))
        (§ block
            (.. log (warn "Error trying to open/read from connection: ", ße))
        )
        (§ finally )
        (§ block
            ;; Go through and close everything, without letting IOExceptions get in our way.
            (§ for (§ var #_"SelectionKey" (§ name ßkey)) :for (.. selector (keys)))
            (§ block
                (§ try )
                (§ block
                    (.. ßkey (channel) (close))
                )
                (§ catch #_"IOException" (§ name ße))
                (§ block
                    (.. log (warn "Error closing channel", ße))
                )
                (.. ßkey (cancel))
                (§ if (§ insta (.. ßkey (attachment)) #_"ConnectionHandler"))
                (§ block
                    (ConnectionHandler/handleKey ßkey) ;; Close connection if relevant.
                )
            )
            (§ try )
            (§ block
                (.. selector (close))
            )
            (§ catch #_"IOException" (§ name ße))
            (§ block
                (.. log (warn "Error closing client manager selector", ße))
            )
        )
        nil
    )

    #_override
    #_public
    (§ method #_"ListenableFuture<SocketAddress>" (§ fn openConnection) [#_"SocketAddress" (§ name ßserverAddress), #_"StreamConnection" (§ name ßconnection)])
    (§ block
        (§ if (not (isRunning)))
        (§ block
            (§ throw (§ new #_"IllegalStateException"))
        )

        ;; Create a new connection, give it a connection as an attachment.
        (§ try )
        (§ block
            (§ let #_"SocketChannel" (§ name ßsc) (SocketChannel/open))
            (§ block
                (.. ßsc (configureBlocking false))
                (.. ßsc (connect ßserverAddress))
                (§ let #_"PendingConnect" (§ name ßdata) (§ new #_"PendingConnect" ßsc, ßconnection, ßserverAddress))
                (§ block
                    (.. newConnectionChannels (offer ßdata))
                    (.. selector (wakeup))
                    (§ return (.. ßdata future))
                )
            )
        )
        (§ catch #_"Throwable" (§ name ße))
        (§ block
            (§ return (Futures/immediateFailedFuture ße))
        )
    )

    #_override
    #_public
    (§ method #_"void" (§ fn triggerShutdown) [])
    (§ block
        (.. selector (wakeup))
        nil
    )

    #_override
    #_public
    (§ method #_"int" (§ fn getConnectedClientCount) [])
    (§ block
        (.. connectedHandlers (size))
    )

    #_override
    #_public
    (§ method #_"void" (§ fn closeConnections) [#_"int" (§ name ßn)])
    (§ block
        (§ for  :for (< 0 ßn) :for (§ ass (§ name ßn) (- ßn 1)))
        (§ block
            (§ let #_"ConnectionHandler" (§ name ßhandler))
            (§ block
                (§ sync connectedHandlers)
                (§ block
                    (§ ass (§ name ßhandler) (.. connectedHandlers (iterator) (next)))
                )
                (§ if (some? ßhandler))
                (§ block
                    (.. ßhandler (closeConnection)) ;; Removes handler from connectedHandlers before returning.
                )
            )
        )
        nil
    )

    #_override
    #_protected
    (§ method #_"Executor" (§ fn executor) [])
    (§ block
        (§ return (§ new #_"Executor")
        (§ anon
            #_override
            #_public
            (§ method #_"void" (§ fn execute) [#_"Runnable" (§ name ßcommand)])
            (§ block
                (.. (§ new #_"ContextPropagatingThreadFactory" "NioClientManager") (newThread ßcommand) (start))
                nil
            )
        ))
    )
)

