    #_testing
    (§ field #_"Selector" (§ name selector))

    ;; Handle a SelectionKey which was selected.
    #_private
    (§ method- #_"void" (§ fn handleKey) [#_"Selector" (§ name ßselector), #_"SelectionKey" (§ name ßkey)])
        (§ throws #_"IOException")
    (§ block
        (§ if (and (.. ßkey (isValid)) (.. ßkey (isAcceptable))))
