                (.. ßnewChannel (configureBlocking false))
                (§ let #_"SelectionKey" (§ name ßnewKey) (.. ßnewChannel (register ßselector, SelectionKey/OP_READ)))
                (§ block
                    (§ try )
                    (§ block
                        (§ let #_"ConnectionHandler" (§ name ßhandler) (§ new #_"ConnectionHandler" connectionFactory, ßnewKey))
                        (§ block
                            (.. ßnewKey (attach ßhandler))
