        (§ ass (§ name (.. this ßconnectionFactory)) ßconnectionFactory)

        (§ ass (§ name sc) (ServerSocketChannel/open))
        (.. sc (configureBlocking false))
        (.. sc (socket) (bind ßbindAddress))
        (§ ass (§ name selector) (.. (SelectorProvider/provider) (openSelector)))
        (.. sc (register selector, SelectionKey/OP_ACCEPT))
        this
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn run) [])
        (§ throws #_"Exception")
    (§ block
        (§ try )
        (§ block
            (§ while (isRunning))
            (§ block
                (.. selector (select))

                (§ let #_"Iterator<SelectionKey>" (§ name ßkeyIterator) (.. selector (selectedKeys) (iterator)))
                (§ block
                    (§ while (.. ßkeyIterator (hasNext)))
                    (§ block
                        (§ let #_"SelectionKey" (§ name ßkey) (.. ßkeyIterator (next)))
                        (§ block
                            (.. ßkeyIterator (remove))

                            (handleKey selector, ßkey)
                        )
                    )
                )
            )
        )
        (§ catch #_"Exception" (§ name ße))
        (§ block
            (.. log (error "Error trying to open/read from connection: {}", ße))
        )
        (§ finally )
        (§ block
            ;; Go through and close everything, without letting IOExceptions get in our way.
            (§ for (§ var #_"SelectionKey" (§ name ßkey)) :for (.. selector (keys)))
            (§ block
                (§ try )
                (§ block
                    (.. ßkey (channel) (close))
                )
                (§ catch #_"IOException" (§ name ße))
                (§ block
                    (.. log (error "Error closing channel", ße))
                )
                (§ try )
                (§ block
                    (.. ßkey (cancel))
                    (handleKey selector, ßkey)
                )
                (§ catch #_"IOException" (§ name ße))
                (§ block
                    (.. log (error "Error closing selection ßkey", ße))
                )
            )
            (§ try )
            (§ block
                (.. selector (close))
            )
            (§ catch #_"IOException" (§ name ße))
            (§ block
                (.. log (error "Error closing server selector", ße))
            )
            (§ try )
            (§ block
                (.. sc (close))
            )
            (§ catch #_"IOException" (§ name ße))
            (§ block
                (.. log (error "Error closing server channel", ße))
            )
        )
        nil
    )

    ;;;
     ; Invoked by the Execution service when it's time to stop.
     ; Calling this method directly will NOT stop the service, call
     ; {@link com.google.common.util.concurrent.AbstractExecutionThreadService#stop()} instead.
     ;;
    #_override
    #_public
    (§ method #_"void" (§ fn triggerShutdown) [])
    (§ block
        ;; Wake up the selector and let the selection thread break its loop as the ExecutionService !isRunning().
        (.. selector (wakeup))
        nil
    )
)

#_(ns org.bitcoinj.net #_"ProtobufConnection"
    (:import [java.io IOException]
             [java.nio ByteBuffer ByteOrder]
             [java.util.concurrent.atomic AtomicReference]
             [java.util.concurrent.locks ReentrantLock]
