#_(ns org.bitcoinj.net.discovery #_"SeedPeers"
    (:import [java.net InetAddress InetSocketAddress UnknownHostException]
             [java.util.concurrent TimeUnit])
   (:require [org.bitcoinj.core NetworkParameters]))

;;;
 ; SeedPeers stores a pre-determined list of Bitcoin node addresses.  These nodes are selected based on
 ; being active on the network for a long period of time.  The intention is to be a last resort way of finding
 ; a connection to the network, in case IRC and DNS fail.  The list comes from the Bitcoin C++ source code.
 ;;
#_public
(§ class #_"SeedPeers" (§ implements #_"PeerDiscovery")
    #_private
    (§ field- #_"NetworkParameters" (§ name params))
    #_private
    (§ field- #_"int[]" (§ name seedAddrs))
    #_private
    (§ field- #_"int" (§ name pnseedIndex))

    ;;;
     ; Supports finding peers by IP addresses.
     ;
     ; @param params Network parameters to be used for port information.
     ;;
    #_public
    (§ constructor #_"SeedPeers" [#_"NetworkParameters" (§ name ßparams)])
    (§ block
        (§ this (.. ßparams (getAddrSeeds)), ßparams)
        this
    )

    ;;;
     ; Supports finding peers by IP addresses.
     ;
     ; @param seedAddrs IP addresses for seed addresses.
     ; @param params Network parameters to be used for port information.
     ;;
    #_public
    (§ constructor #_"SeedPeers" [#_"int[]" (§ name ßseedAddrs), #_"NetworkParameters" (§ name ßparams)])
    (§ block
        (§ ass (§ name (.. this ßseedAddrs)) ßseedAddrs)
        (§ ass (§ name (.. this ßparams)) ßparams)
        this
    )

    ;;;
     ; Acts as an iterator, returning the address of each node in the list sequentially.
     ; Once all the list has been iterated, null will be returned for each subsequent query.
     ;
     ; @return InetSocketAddress - the address/port of the next node.
     ; @throws PeerDiscoveryException
     ;;
    #_nilable
    #_public
    (§ method #_"InetSocketAddress" (§ fn getPeer) [])
        (§ throws #_"PeerDiscoveryException")
    (§ block
        (§ try )
        (§ block
            (§ return (nextPeer))
        )
        (§ catch #_"UnknownHostException" (§ name ße))
        (§ block
            (§ throw (§ new #_"PeerDiscoveryException" ße))
        )
    )

    #_nilable
    #_private
    (§ method- #_"InetSocketAddress" (§ fn nextPeer) [])
        (§ throws #_"UnknownHostException", #_"PeerDiscoveryException")
    (§ block
        (§ if (or (nil? seedAddrs) (== (.. seedAddrs length) 0)))
        (§ block
            (§ throw (§ new #_"PeerDiscoveryException" "No IP address seeds configured; unable to find any peers"))
        )

        (§ if (<= (.. seedAddrs length) pnseedIndex))
        (§ block
            (§ return nil)
        )

        (§ let #_"int" (§ name ßi) pnseedIndex)
        (§ block
            (§ ass (§ name pnseedIndex) (+ pnseedIndex 1))
            (§ new #_"InetSocketAddress" (convertAddress (§ ai seedAddrs ßi)), (.. params (getPort)))
        )
    )

    ;;;
     ; Returns an array containing all the Bitcoin nodes within the list.
     ;;
    #_override
    #_public
    (§ method #_"InetSocketAddress[]" (§ fn getPeers) [#_"long" (§ name ßservices), #_"long" (§ name ßtimeoutValue), #_"TimeUnit" (§ name ßtimeoutUnit)])
        (§ throws #_"PeerDiscoveryException")
    (§ block
        (§ if (!= ßservices 0))
        (§ block
            (§ throw (§ new #_"PeerDiscoveryException" (str "Pre-determined peers cannot be filtered by services: " ßservices)))
        )

        (§ try )
        (§ block
            (§ return (allPeers))
        )
        (§ catch #_"UnknownHostException" (§ name ße))
        (§ block
            (§ throw (§ new #_"PeerDiscoveryException" ße))
        )
    )

    #_private
    (§ method- #_"InetSocketAddress[]" (§ fn allPeers) [])
        (§ throws #_"UnknownHostException")
    (§ block
        (§ let #_"InetSocketAddress[]" (§ name ßaddresses) (§ new #_"InetSocketAddress[]" (§ count (.. seedAddrs length))))
        (§ block
            (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi (.. seedAddrs length)) :for (§ recur (+ ßi 1)))
            (§ block
                (§ ass (§ name (§ ai ßaddresses ßi)) (§ new #_"InetSocketAddress" (convertAddress (§ ai seedAddrs ßi)), (.. params (getPort))))
            )
            ßaddresses
        )
    )

    #_private
    (§ method- #_"InetAddress" (§ fn convertAddress) [#_"int" (§ name ßseed)])
        (§ throws #_"UnknownHostException")
    (§ block
        (§ let #_"byte[]" (§ name ßv4addr) (§ new #_"byte[]" (§ count 4)))
        (§ block
            (§ ass (§ name (§ ai ßv4addr 0)) (§ cast #_"byte" (& 0xff ßseed)))
            (§ ass (§ name (§ ai ßv4addr 1)) (§ cast #_"byte" (& 0xff (>> ßseed 8))))
            (§ ass (§ name (§ ai ßv4addr 2)) (§ cast #_"byte" (& 0xff (>> ßseed 16))))
            (§ ass (§ name (§ ai ßv4addr 3)) (§ cast #_"byte" (& 0xff (>> ßseed 24))))
            (InetAddress/getByAddress ßv4addr)
        )
    )

    #_override
    #_public
    (§ method #_"void" (§ fn shutdown) [])
    (§ block
        nil
    )
)

#_(ns org.bitcoinj.params #_"AbstractBitcoinNetParams"
    (:import [java.math BigInteger]
             [java.util.concurrent TimeUnit])
    (:import [com.google.common.base Preconditions Stopwatch]
             [org.slf4j Logger LoggerFactory])
   (:require [org.bitcoinj.core BitcoinSerializer Block Coin NetworkParameters Sha256Hash StoredBlock Transaction Utils VerificationException]
             [org.bitcoinj.utils MonetaryFormat]
             [org.bitcoinj.store BlockStore BlockStoreException]))

;;;
 ; Parameters for Bitcoin-like networks.
 ;;
#_public
#_abstract
(§ class #_"AbstractBitcoinNetParams" (§ extends #_"NetworkParameters")
    ;;;
     ; Scheme part for Bitcoin URIs.
     ;;
    #_public
    #_static
    (§ def #_"String" (§ name BITCOIN_SCHEME) "bitcoin")
    #_public
    #_static
    (§ def #_"int" (§ name REWARD_HALVING_INTERVAL) 210000)

    #_private
    #_static
    (§ def- #_"Logger" (§ name log) (LoggerFactory/getLogger (§ klass #_"AbstractBitcoinNetParams")))

    #_public
    (§ constructor #_"AbstractBitcoinNetParams" [])
    (§ block
        (§ super )
        this
    )

    ;;;
     ; Checks if we are at a reward halving point.
     ; @param height The height of the previous stored block.
     ; @return if this is a reward halving point.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isRewardHalvingPoint) [#_"int" (§ name ßheight)])
    (§ block
        (== (% (+ ßheight 1) REWARD_HALVING_INTERVAL) 0)
    )

    ;;;
     ; Checks if we are at a difficulty transition point.
     ; @param height The height of the previous stored block.
     ; @return if this is a difficulty transition point.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isDifficultyTransitionPoint) [#_"int" (§ name ßheight)])
    (§ block
        (== (% (+ ßheight 1) (.. this (getInterval))) 0)
    )

    #_override
    #_public
    (§ method #_"void" (§ fn checkDifficultyTransitions) [#_"StoredBlock" (§ name ßstoredPrev), #_"Block" (§ name ßnextBlock), #_"BlockStore" (§ name ßblockStore)])
        (§ throws #_"VerificationException", #_"BlockStoreException")
    (§ block
        (§ let #_"Block" (§ name ßprev) (.. ßstoredPrev (getHeader)))
        (§ block

            ;; Is this supposed to be a difficulty transition point?
            (§ if (not (isDifficultyTransitionPoint (.. ßstoredPrev (getHeight)))))
            (§ block
                ;; No ... so check the difficulty didn't actually change.
                (§ if (!= (.. ßnextBlock (getDifficultyTarget)) (.. ßprev (getDifficultyTarget))))
                (§ block
                    (§ throw (§ new #_"VerificationException" (str "Unexpected change in difficulty at height " (.. ßstoredPrev (getHeight)) ": " (Long/toHexString (.. ßnextBlock (getDifficultyTarget))) " vs " (Long/toHexString (.. ßprev (getDifficultyTarget))))))
                )
                (§ return nil)
            )

            ;; We need to find a block far back in the chain.  It's OK that this is expensive because it only occurs every
            ;; two weeks after the initial block chain download.
            (§ let #_"Stopwatch" (§ name ßwatch) (Stopwatch/createStarted))
            (§ block

                (§ let #_"Sha256Hash" (§ name ßhash) (.. ßprev (getHash)))
                (§ block
                    (§ let #_"StoredBlock" (§ name ßcursor) nil)
                    (§ block
                        (§ let #_"int" (§ name ßinterval) (.. this (getInterval)))
                        (§ block
                            (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi ßinterval) :for (§ recur (+ ßi 1)))
                            (§ block
                                (§ ass (§ name ßcursor) (.. ßblockStore (get ßhash)))
                                (§ if (nil? ßcursor))
                                (§ block
                                    ;; This should never happen.  If it does, it means we are following an incorrect or busted chain.
                                    (§ throw (§ new #_"VerificationException" (str "Difficulty transition point but we did not find a way back to the last transition point. Not found: " ßhash)))
                                )
                                (§ ass (§ name ßhash) (.. ßcursor (getHeader) (getPrevBlockHash)))
                            )
                            (Preconditions/checkState (and (some? ßcursor) (isDifficultyTransitionPoint (- (.. ßcursor (getHeight)) 1))), "Didn't arrive at a transition point.")

                            (.. ßwatch (stop))
                            (§ if (< 50 (.. ßwatch (elapsed TimeUnit/MILLISECONDS))))
                            (§ block
                                (.. log (info "Difficulty transition traversal took {}", ßwatch))
                            )

                            (§ let #_"Block" (§ name ßblockIntervalAgo) (.. ßcursor (getHeader)))
                            (§ block
                                (§ let #_"int" (§ name ßtimespan) (§ cast #_"int" (- (.. ßprev (getTimeSeconds)) (.. ßblockIntervalAgo (getTimeSeconds)))))
                                (§ block
                                    ;; Limit the adjustment step.
                                    (§ let #_"int" (§ name ßtargetTimespan) (.. this (getTargetTimespan)))
                                    (§ block
                                        (§ if (< ßtimespan (/ ßtargetTimespan 4)))
                                        (§ block
                                            (§ ass (§ name ßtimespan) (/ ßtargetTimespan 4))
                                        )
                                        (§ if (> ßtimespan (* ßtargetTimespan 4)))
                                        (§ block
                                            (§ ass (§ name ßtimespan) (* ßtargetTimespan 4))
                                        )

                                        (§ let #_"BigInteger" (§ name ßnewTarget) (Utils/decodeCompactBits (.. ßprev (getDifficultyTarget))))
                                        (§ block
                                            (§ ass (§ name ßnewTarget) (.. ßnewTarget (multiply (BigInteger/valueOf ßtimespan))))
                                            (§ ass (§ name ßnewTarget) (.. ßnewTarget (divide (BigInteger/valueOf ßtargetTimespan))))

                                            (§ if (< 0 (.. ßnewTarget (compareTo (.. this (getMaxTarget))))))
                                            (§ block
                                                (.. log (info "Difficulty hit proof of work limit: {}", (.. ßnewTarget (toString 16))))
                                                (§ ass (§ name ßnewTarget) (.. this (getMaxTarget)))
                                            )

                                            (§ let #_"int" (§ name ßaccuracyBytes) (- (§ cast #_"int" (>>> (.. ßnextBlock (getDifficultyTarget)) 24)) 3))
                                            (§ block
                                                (§ let #_"long" (§ name ßreceivedTargetCompact) (.. ßnextBlock (getDifficultyTarget)))
                                                (§ block

                                                    ;; The calculated difficulty is to a higher precision than received, so reduce here.
                                                    (§ let #_"BigInteger" (§ name ßmask) (.. (BigInteger/valueOf 0xffffff) (shiftLeft (* ßaccuracyBytes 8))))
                                                    (§ block
                                                        (§ ass (§ name ßnewTarget) (.. ßnewTarget (and ßmask)))
                                                        (§ let #_"long" (§ name ßnewTargetCompact) (Utils/encodeCompactBits ßnewTarget))
                                                        (§ block

                                                            (§ if (!= ßnewTargetCompact ßreceivedTargetCompact))
                                                            (§ block
                                                                (§ throw (§ new #_"VerificationException" (str "Network provided difficulty bits do not match what was calculated: " (Long/toHexString ßnewTargetCompact) " vs " (Long/toHexString ßreceivedTargetCompact))))
                                                            )
                                                            nil
                                                        )
                                                    )
                                                )
                                            )
                                        )
                                    )
                                )
                            )
                        )
                    )
                )
            )
        )
    )

    #_override
    #_public
    (§ method #_"Coin" (§ fn getMaxMoney) [])
    (§ block
        MAX_MONEY
    )

    #_override
    #_public
    (§ method #_"Coin" (§ fn getMinNonDustOutput) [])
    (§ block
        Transaction/MIN_NONDUST_OUTPUT
    )

    #_override
    #_public
    (§ method #_"MonetaryFormat" (§ fn getMonetaryFormat) [])
    (§ block
        (§ new #_"MonetaryFormat")
    )

    #_override
    #_public
    (§ method #_"int" (§ fn getProtocolVersionNum) [#_"ProtocolVersion" (§ name ßversion)])
    (§ block
        (.. ßversion (getBitcoinProtocolVersion))
    )

    #_override
    #_public
    (§ method #_"BitcoinSerializer" (§ fn getSerializer) [#_"boolean" (§ name ßparseRetain)])
    (§ block
        (§ new #_"BitcoinSerializer" this, ßparseRetain)
    )

    #_override
    #_public
    (§ method #_"String" (§ fn getUriScheme) [])
    (§ block
        BITCOIN_SCHEME
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn hasMaxMoney) [])
    (§ block
        true
    )
)

