#_(ns org.bitcoinj.params #_"Networks"
    (:import [java.util Collection Set])
    (:import [com.google.common.collect ImmutableSet Lists])
   (:require [org.bitcoinj.core NetworkParameters]))

;;;
 ; Utility class that holds all the registered NetworkParameters types used for Address auto discovery.
 ; By default only MainNetParams and TestNet3Params are used.  If you want to use UnitTestParams, use
 ; the register and unregister the TestNet3Params as they don't have their own address version/type code.
 ;;
#_public
(§ class #_"Networks"
    ;;; Registered networks. ;;
    #_private
    #_static
    (§ def- #_"Set<? extends NetworkParameters>" (§ name networks) (ImmutableSet/of (TestNet3Params/get), (MainNetParams/get)))

    #_public
    #_static
    (§ defn #_"Set<? extends NetworkParameters>" (§ fn get) [])
    (§ block
        networks
    )

    #_public
    #_static
    (§ defn #_"void" (§ fn register) [#_"NetworkParameters" (§ name ßnetwork)])
    (§ block
        (register (Lists/newArrayList ßnetwork))
        nil
    )

    #_public
    #_static
    (§ defn #_"void" (§ fn register) [#_"Collection<? extends NetworkParameters>" (§ name ßnetworks)])
    (§ block
        (§ let #_"ImmutableSet.Builder<NetworkParameters>" (§ name ßbuilder) (ImmutableSet/builder))
        (§ block
            (.. ßbuilder (addAll Networks/networks))
            (.. ßbuilder (addAll ßnetworks))
            (§ ass (§ name (.. Networks ßnetworks)) (.. ßbuilder (build)))
            nil
        )
    )

    #_public
    #_static
    (§ defn #_"void" (§ fn unregister) [#_"NetworkParameters" (§ name ßnetwork)])
    (§ block
        (§ if (.. networks (contains ßnetwork)))
        (§ block
            (§ let #_"ImmutableSet.Builder<NetworkParameters>" (§ name ßbuilder) (ImmutableSet/builder))
            (§ block

                (§ for (§ var #_"NetworkParameters" (§ name ßparameters)) :for networks)
                (§ block
                    (§ if (not (.. ßparameters (equals ßnetwork))))
                    (§ block
                        (.. ßbuilder (add ßparameters))
                    )
                )

                (§ ass (§ name networks) (.. ßbuilder (build)))
            )
        )
        nil
    )
)

#_(ns org.bitcoinj.params #_"TestNet3Params"
    (:import [java.math BigInteger]
             [java.util Date])
    (:import [com.google.common.base Preconditions])
   (:require [org.bitcoinj.core Block NetworkParameters StoredBlock Utils VerificationException]
             [org.bitcoinj.store BlockStore BlockStoreException]))

;;;
 ; Parameters for the testnet, a separate public instance of Bitcoin that has relaxed rules suitable for development
 ; and testing of applications and new Bitcoin versions.
 ;;
#_public
(§ class #_"TestNet3Params" (§ extends #_"AbstractBitcoinNetParams")
    #_public
    #_static
    (§ def #_"int" (§ name TESTNET_MAJORITY_WINDOW) 100)
    #_public
    #_static
    (§ def #_"int" (§ name TESTNET_MAJORITY_REJECT_BLOCK_OUTDATED) 75)
    #_public
    #_static
    (§ def #_"int" (§ name TESTNET_MAJORITY_ENFORCE_BLOCK_UPGRADE) 51)

    #_public
    (§ constructor #_"TestNet3Params" [])
    (§ block
        (§ super )

        (§ ass (§ name id) ID_TESTNET)
        (§ ass (§ name packetMagic) 0x0b110907)
        (§ ass (§ name interval) INTERVAL)
        (§ ass (§ name targetTimespan) TARGET_TIMESPAN)
        (§ ass (§ name maxTarget) (Utils/decodeCompactBits 0x1d00ffff))
        (§ ass (§ name port) 18333)
        (§ ass (§ name addressHeader) 111)
        (§ ass (§ name p2shHeader) 196)
        (§ ass (§ name acceptableAddressCodes) (§ new #_"int[]" (§ coll addressHeader, p2shHeader )))
        (.. genesisBlock (setTime 1296688602))
        (.. genesisBlock (setDifficultyTarget 0x1d00ffff))
        (.. genesisBlock (setNonce 414098458))
        (§ ass (§ name spendableCoinbaseDepth) 100)
        (§ ass (§ name subsidyDecreaseBlockCount) 210000)
        (§ let #_"String" (§ name ßgenesisHash) (.. genesisBlock (getHashAsString)))
        (§ block
            (Preconditions/checkState (.. ßgenesisHash (equals "000000000933ea01ad0ee984209779baaec3ced90fa3f408719526f8d77f4943")))
            (§ ass (§ name alertSigningKey) (.. Utils/HEX (decode "04302390343f91cc401d56d68b123028bf52e5fca1939df127f63c6467cdf9c8e2c14b61104cf817d0b780da337893ecc4aaff1309e536162dabbdb45200ca2b0a")))

            (§ ass (§ name dnsSeeds) (§ new #_"String[]"
            (§ coll
                "testnet-seed.bitcoin.jonasschnelli.ch", ;; Jonas Schnelli
                "testnet-seed.bluematt.me",              ;; Matt Corallo
                "testnet-seed.bitcoin.petertodd.org",    ;; Peter Todd
                "testnet-seed.bitcoin.schildbach.de",    ;; Andreas Schildbach
            )))
            (§ ass (§ name addrSeeds) nil)
            (§ ass (§ name bip32HeaderPub) 0x043587cf)
            (§ ass (§ name bip32HeaderPriv) 0x04358394)

            (§ ass (§ name majorityEnforceBlockUpgrade) TESTNET_MAJORITY_ENFORCE_BLOCK_UPGRADE)
            (§ ass (§ name majorityRejectBlockOutdated) TESTNET_MAJORITY_REJECT_BLOCK_OUTDATED)
            (§ ass (§ name majorityWindow) TESTNET_MAJORITY_WINDOW)
            this
        )
    )

    #_private
    #_static
    (§ def- #_"TestNet3Params" (§ name instance))

    #_public
    #_static
    #_synchronized
    (§ defn #_"TestNet3Params" (§ fn get) [])
    (§ block
        (§ if (nil? instance))
        (§ block
            (§ ass (§ name instance) (§ new #_"TestNet3Params"))
        )
        instance
    )

    #_override
    #_public
    (§ method #_"String" (§ fn getPaymentProtocolId) [])
    (§ block
        PAYMENT_PROTOCOL_ID_TESTNET
    )

    ;; February 16th 2012
    #_private
    #_static
    (§ def- #_"Date" (§ name testnetDiffDate) (§ new #_"Date" 1329264000000))

    #_override
    #_public
    (§ method #_"void" (§ fn checkDifficultyTransitions) [#_"StoredBlock" (§ name ßstoredPrev), #_"Block" (§ name ßnextBlock), #_"BlockStore" (§ name ßblockStore)])
        (§ throws #_"VerificationException", #_"BlockStoreException")
    (§ block
        (§ if (and (not (isDifficultyTransitionPoint (.. ßstoredPrev (getHeight)))) (.. ßnextBlock (getTime) (after testnetDiffDate))))
        (§ block
            (§ let #_"Block" (§ name ßprev) (.. ßstoredPrev (getHeader)))
            (§ block

                ;; After 15th February 2012 the rules on the testnet change to avoid people running up the difficulty
                ;; and then leaving, making it too hard to mine a block.  On non-difficulty transition points, easy
                ;; blocks are allowed if there has been a span of 20 minutes without one.
                (§ let #_"long" (§ name ßtimeDelta) (- (.. ßnextBlock (getTimeSeconds)) (.. ßprev (getTimeSeconds))))
                (§ block
                    ;; There is an integer underflow bug in bitcoin-qt that means mindiff blocks are accepted when time
                    ;; goes backwards.
                    (§ if (and (<= 0 ßtimeDelta) (<= ßtimeDelta (* 2 NetworkParameters/TARGET_SPACING))))
                    (§ block
                        ;; Walk backwards until we find a block that doesn't have the easiest proof of work, then check
                        ;; that difficulty is equal to that one.
                        (§ let #_"StoredBlock" (§ name ßcursor) ßstoredPrev)
                        (§ block
                            (§ while (and (not (.. ßcursor (getHeader) (equals (getGenesisBlock)))) (!= (% (.. ßcursor (getHeight)) (getInterval)) 0) (.. ßcursor (getHeader) (getDifficultyTargetAsInteger) (equals (getMaxTarget)))))
                            (§ block
                                (§ ass (§ name ßcursor) (.. ßcursor (getPrev ßblockStore)))
                            )
                            (§ let #_"BigInteger" (§ name ßcursorTarget) (.. ßcursor (getHeader) (getDifficultyTargetAsInteger)))
                            (§ block
                                (§ let #_"BigInteger" (§ name ßnewTarget) (.. ßnextBlock (getDifficultyTargetAsInteger)))
                                (§ block
                                    (§ if (not (.. ßcursorTarget (equals ßnewTarget))))
                                    (§ block
                                        (§ throw (§ new #_"VerificationException" (str "Testnet block transition that is not allowed: " (Long/toHexString (.. ßcursor (getHeader) (getDifficultyTarget))) " vs " (Long/toHexString (.. ßnextBlock (getDifficultyTarget))))))
                                    )
                                )
                            )
                        )
                    )
                )
            )
        )
        (§ else )
        (§ block
            (.. super (checkDifficultyTransitions ßstoredPrev, ßnextBlock, ßblockStore))
        )
        nil
    )
)

