#_(ns org.bitcoinj.script #_"Script"
    (:import [java.io ByteArrayInputStream ByteArrayOutputStream IOException OutputStream]
             [java.math BigInteger]
             [java.security MessageDigest NoSuchAlgorithmException]
             [java.util *])
    (:import [com.google.common.base Preconditions]
             [com.google.common.collect Lists]
             [org.slf4j Logger LoggerFactory]
             [org.spongycastle.crypto.digests RIPEMD160Digest])
   (:require [org.bitcoinj.core *]
             [org.bitcoinj.crypto TransactionSignature]
             #_static [org.bitcoinj.script.ScriptOpCodes *]))

;; TODO: Redesign this entire API to be more type safe and organised.

;;;
 ; <p>Programs embedded inside transactions that control redemption of payments.</p>
 ;
 ; <p>Bitcoin transactions don't specify what they do directly.  Instead
 ; <a href="https://en.bitcoin.it/wiki/Script">a small binary stack language</a> is used to define programs that
 ; when evaluated, return whether the transaction "accepts" or rejects the other transactions connected to it.</p>
 ;
 ; <p>In SPV mode, scripts are not run, because that would require all transactions to be available and lightweight
 ; clients don't have that data.  In full mode, this class is used to run the interpreted language.  It also has
 ; static methods for building scripts.</p>
 ;;
#_public
(§ class #_"Script"
    ;;; Enumeration to encapsulate the type of this script. ;;
    #_public
    (§ enum #_"ScriptType"
        ;; Do NOT change the ordering of the following definitions because their ordinals are stored in databases.
        (§ item NO_TYPE)
        (§ item P2PKH)
        (§ item PUB_KEY)
        (§ item P2SH)
    )

    ;;; Flags to pass to {@link Script#correctlySpends(Transaction, long, Script, Set)}.
     ; Note currently only P2SH, DERSIG and NULLDUMMY are actually supported.
     ;;
    #_public
    (§ enum #_"VerifyFlag"
        (§ item P2SH) ;; Enable BIP16-style subscript evaluation.
        (§ item STRICTENC) ;; Passing a non-strict-DER signature or one with undefined hashtype to a checksig operation causes script failure.
        (§ item DERSIG) ;; Passing a non-strict-DER signature to a checksig operation causes script failure (softfork safe, BIP66 rule 1).
        (§ item LOW_S) ;; Passing a non-strict-DER signature or one with S > order/2 to a checksig operation causes script failure.
        (§ item NULLDUMMY) ;; Verify dummy stack item consumed by CHECKMULTISIG is of zero-length.
        (§ item SIGPUSHONLY) ;; Using a non-push operator in the scriptSig causes script failure (softfork safe, BIP62 rule 2).
        (§ item MINIMALDATA) ;; Require minimal encodings for all push operations.
        (§ item DISCOURAGE_UPGRADABLE_NOPS) ;; Discourage use of NOPs reserved for upgrades (NOP1-10).
        (§ item CLEANSTACK) ;; Require that only a single stack element remains after evaluation.
        (§ item CHECKLOCKTIMEVERIFY) ;; Enable CHECKLOCKTIMEVERIFY operation.
        (§ item CHECKSEQUENCEVERIFY) ;; Enable CHECKSEQUENCEVERIFY operation.
    )
    #_public
    #_static
    (§ def #_"EnumSet<VerifyFlag>" (§ name ALL_VERIFY_FLAGS) (EnumSet/allOf (§ klass #_"VerifyFlag")))

    #_private
    #_static
    (§ def- #_"Logger" (§ name log) (LoggerFactory/getLogger (§ klass #_"Script")))

    #_public
    #_static
    (§ def #_"long" (§ name MAX_SCRIPT_ELEMENT_SIZE) 520) ;; bytes
    #_private
    #_static
    (§ def- #_"int" (§ name MAX_OPS_PER_SCRIPT) 201)
    #_private
    #_static
    (§ def- #_"int" (§ name MAX_STACK_SIZE) 1000)
    #_private
    #_static
    (§ def- #_"int" (§ name MAX_PUBKEYS_PER_MULTISIG) 20)
    #_private
    #_static
    (§ def- #_"int" (§ name MAX_SCRIPT_SIZE) 10000)
    #_public
    #_static
    (§ def #_"int" (§ name SIG_SIZE) 75)
    ;;; Max number of sigops allowed in a standard p2sh redeem script. ;;
    #_public
    #_static
    (§ def #_"int" (§ name MAX_P2SH_SIGOPS) 15)

    ;; The program is a set of chunks where each element is either [opcode] or [data, data, data ...].
    #_protected
    (§ field #_"List<ScriptChunk>" (§ name chunks))
    ;; Unfortunately, scripts are not ever re-serialized or canonicalized when used in signature hashing.
    ;; Thus we must preserve the exact bytes that we read off the wire, along with the parsed form.
    #_protected
    (§ field #_"byte[]" (§ name program))

    ;; Creation time of the associated keys in seconds since the epoch.
    #_private
    (§ field- #_"long" (§ name creationTimeSeconds))

    ;;; Creates an empty script that serializes to nothing. ;;
    #_private
    (§ constructor- #_"Script" [])
    (§ block
        (§ ass (§ name chunks) (Lists/newArrayList))
        this
    )

    ;; Used from ScriptBuilder.
    (§ constructor #_"Script" [#_"List<ScriptChunk>" (§ name ßchunks)])
    (§ block
        (§ ass (§ name (.. this ßchunks)) (Collections/unmodifiableList (§ new #_"ArrayList<>" ßchunks)))
        (§ ass (§ name creationTimeSeconds) (Utils/currentTimeSeconds))
        this
    )

    ;;;
     ; Construct a Script that copies and wraps the programBytes array.
     ; The array is parsed and checked for syntactic validity.
     ; @param programBytes Array of program bytes from a transaction.
     ;;
    #_public
    (§ constructor #_"Script" [#_"byte[]" (§ name ßprogramBytes)])
        (§ throws #_"ScriptException")
    (§ block
        (§ ass (§ name program) ßprogramBytes)
        (parse ßprogramBytes)
        (§ ass (§ name creationTimeSeconds) 0)
        this
    )

    #_public
    (§ constructor #_"Script" [#_"byte[]" (§ name ßprogramBytes), #_"long" (§ name ßcreationTimeSeconds)])
        (§ throws #_"ScriptException")
    (§ block
        (§ ass (§ name program) ßprogramBytes)
        (parse ßprogramBytes)
        (§ ass (§ name (.. this ßcreationTimeSeconds)) ßcreationTimeSeconds)
        this
    )

    #_public
    (§ method #_"long" (§ fn getCreationTimeSeconds) [])
    (§ block
        creationTimeSeconds
    )

    #_public
    (§ method #_"void" (§ fn setCreationTimeSeconds) [#_"long" (§ name ßcreationTimeSeconds)])
    (§ block
        (§ ass (§ name (.. this ßcreationTimeSeconds)) ßcreationTimeSeconds)
        nil
    )

    ;;;
     ; Returns the program opcodes as a string, for example "[1234] DUP HASH160".
     ;;
    #_override
    #_public
    (§ method #_"String" (§ fn toString) [])
    (§ block
        (.. Utils/SPACE_JOINER (join chunks))
    )

    ;;; Returns the serialized program as a newly created byte array. ;;
    #_public
    (§ method #_"byte[]" (§ fn getProgram) [])
    (§ block
        (§ try )
        (§ block
            ;; Don't round-trip as Bitcoin Core doesn't and it would introduce a mismatch.
            (§ if (some? program))
            (§ block
                (§ return (Arrays/copyOf program, (.. program length)))
            )

            (§ let #_"ByteArrayOutputStream" (§ name ßbos) (§ new #_"ByteArrayOutputStream"))
            (§ block
                (§ for (§ var #_"ScriptChunk" (§ name ßchunk)) :for chunks)
                (§ block
                    (.. ßchunk (write ßbos))
                )
                (§ ass (§ name program) (.. ßbos (toByteArray)))
                (§ return program)
            )
        )
        (§ catch #_"IOException" (§ name ße))
        (§ block
            (§ throw (§ new #_"RuntimeException" ße)) ;; Cannot happen.
        )
    )

    ;;; Returns an immutable list of the scripts parsed form.  Each chunk is either an opcode or data element. ;;
    #_public
    (§ method #_"List<ScriptChunk>" (§ fn getChunks) [])
    (§ block
        (Collections/unmodifiableList chunks)
    )

    #_private
    #_static
    (§ def- #_"ScriptChunk[]" (§ name STANDARD_TRANSACTION_SCRIPT_CHUNKS)
    (§ coll
        (§ new #_"ScriptChunk" ScriptOpCodes/OP_DUP, nil, 0)
        (§ new #_"ScriptChunk" ScriptOpCodes/OP_HASH160, nil, 1)
        (§ new #_"ScriptChunk" ScriptOpCodes/OP_EQUALVERIFY, nil, 23)
        (§ new #_"ScriptChunk" ScriptOpCodes/OP_CHECKSIG, nil, 24)
    ))

    ;;;
     ; <p>To run a script, first we parse it which breaks it up into chunks representing pushes of data
     ; or logical opcodes.  Then we can run the parsed chunks.</p>
     ;
     ; <p>The reason for this split, instead of just interpreting directly, is to make it easier
     ; to reach into a programs structure and pull out bits of data without having to run it.
     ; This is necessary to render the to/from addresses of transactions in a user interface.
     ; Bitcoin Core does something similar.</p>
     ;;
    #_private
    (§ method- #_"void" (§ fn parse) [#_"byte[]" (§ name ßprogram)])
        (§ throws #_"ScriptException")
    (§ block
        (§ ass (§ name chunks) (§ new #_"ArrayList<>" 5)) ;; Common size.
        (§ let #_"ByteArrayInputStream" (§ name ßbis) (§ new #_"ByteArrayInputStream" ßprogram))
        (§ block
            (§ let #_"int" (§ name ßinitialSize) (.. ßbis (available)))
            (§ block
                (§ while (< 0 (.. ßbis (available))))
                (§ block
                    (§ let #_"int" (§ name ßstartLocationInProgram) (- ßinitialSize (.. ßbis (available))))
                    (§ block
                        (§ let #_"int" (§ name ßopcode) (.. ßbis (read)))
                        (§ block

                            (§ let #_"long" (§ name ßdataToRead) -1)
                            (§ block
                                (§ if (< -1 ßopcode OP_PUSHDATA1))
                                (§ block
                                    ;; Read some bytes of data, where how many is the opcode value itself.
                                    (§ ass (§ name ßdataToRead) ßopcode)
                                )
                                (§ elseif (== ßopcode OP_PUSHDATA1))
                                (§ block
                                    (§ if (< (.. ßbis (available)) 1))
                                    (§ block
                                        (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_UNKNOWN_ERROR, "Unexpected end of script"))
                                    )
                                    (§ ass (§ name ßdataToRead) (.. ßbis (read)))
                                )
                                (§ elseif (== ßopcode OP_PUSHDATA2))
                                (§ block
                                    ;; Read a short, then read that many bytes of data.
                                    (§ if (< (.. ßbis (available)) 2))
                                    (§ block
                                        (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_UNKNOWN_ERROR, "Unexpected end of script"))
                                    )
                                    (§ ass (§ name ßdataToRead) (| (.. ßbis (read)) (<< (.. ßbis (read)) 8)))
                                )
                                (§ elseif (== ßopcode OP_PUSHDATA4))
                                (§ block
                                    ;; Read a uint32, then read that many bytes of data.
                                    ;; Though this is allowed, because its value cannot be > 520, it should never actually be used.
                                    (§ if (< (.. ßbis (available)) 4))
                                    (§ block
                                        (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_UNKNOWN_ERROR, "Unexpected end of script"))
                                    )
                                    (§ ass (§ name ßdataToRead) (| (§ cast #_"long" (.. ßbis (read))) (<< (§ cast #_"long" (.. ßbis (read))) 8) (<< (§ cast #_"long" (.. ßbis (read))) 16) (<< (§ cast #_"long" (.. ßbis (read))) 24)))
                                )

                                (§ let #_"ScriptChunk" (§ name ßchunk))
                                (§ block
                                    (§ if (== ßdataToRead -1))
                                    (§ block
                                        (§ ass (§ name ßchunk) (§ new #_"ScriptChunk" ßopcode, nil, ßstartLocationInProgram))
                                    )
                                    (§ else )
                                    (§ block
                                        (§ if (< (.. ßbis (available)) ßdataToRead))
                                        (§ block
                                            (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_BAD_OPCODE, "Push of data element that is larger than remaining data"))
                                        )

                                        (§ let #_"byte[]" (§ name ßdata) (§ new #_"byte[]" (§ count (§ cast #_"int" ßdataToRead))))
                                        (§ block
                                            (Preconditions/checkState (or (== ßdataToRead 0) (== (.. ßbis (read ßdata, 0, (§ cast #_"int" ßdataToRead))) ßdataToRead)))
                                            (§ ass (§ name ßchunk) (§ new #_"ScriptChunk" ßopcode, ßdata, ßstartLocationInProgram))
                                        )
                                    )
                                    ;; Save some memory by eliminating redundant copies of the same chunk objects.
                                    (§ for (§ var #_"ScriptChunk" (§ name ßc)) :for STANDARD_TRANSACTION_SCRIPT_CHUNKS)
                                    (§ block
                                        (§ if (.. ßc (equals ßchunk)))
                                        (§ block
                                            (§ ass (§ name ßchunk) ßc)
                                        )
                                    )
                                    (.. chunks (add ßchunk))
                                )
                            )
                        )
                    )
                )
                nil
            )
        )
    )

    ;;;
     ; Returns true if this script is of the form <pubkey> OP_CHECKSIG.  This form was originally intended for
     ; transactions where the peers talked to each other directly via TCP/IP, but has fallen out of favor with time
     ; due to that mode of operation being susceptible to man-in-the-middle attacks.  It is still used in coinbase
     ; outputs and can be useful more exotic types of transaction, but today most payments are to addresses.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isSentToRawPubKey) [])
    (§ block
        (and (== (.. chunks (size)) 2) (.. chunks (get 1) (equalsOpCode OP_CHECKSIG)) (not (.. chunks (get 0) (isOpCode))) (< 1 (.. chunks (get 0) data length)))
    )

    ;;;
     ; Returns true if this script is of the form DUP HASH160 <pubkey hash> EQUALVERIFY CHECKSIG, i.e. payment to an
     ; address like 1VayNert3x1KzbpzMGt2qdqrAThiRovi8.  This form was originally intended for the case where you wish
     ; to send somebody money with a written code because their node is offline, but over time has become the standard
     ; way to make payments due to the short and recognizable base58 form addresses come in.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isSentToAddress) [])
    (§ block
        (and (== (.. chunks (size)) 5) (.. chunks (get 0) (equalsOpCode OP_DUP)) (.. chunks (get 1) (equalsOpCode OP_HASH160)) (== (.. chunks (get 2) data length) Address/LENGTH) (.. chunks (get 3) (equalsOpCode OP_EQUALVERIFY)) (.. chunks (get 4) (equalsOpCode OP_CHECKSIG)))
    )

    ;;;
     ; <p>If a program matches the standard template DUP HASH160 &lt;pubkey hash&gt; EQUALVERIFY CHECKSIG,
     ; then this function retrieves the third element.
     ; In this case, this is useful for fetching the destination address of a transaction.</p>
     ;
     ; <p>If a program matches the standard template HASH160 &lt;script hash&gt; EQUAL,
     ; then this function retrieves the second element.
     ; In this case, this is useful for fetching the hash of the redeem script of a transaction.</p>
     ;
     ; <p>Otherwise it throws a ScriptException.</p>
     ;
     ;;
    #_public
    (§ method #_"byte[]" (§ fn getPubKeyHash) [])
        (§ throws #_"ScriptException")
    (§ block
        (§ if (isSentToAddress))
        (§ block
            (§ return (.. chunks (get 2) data))
        )
        (§ if (isPayToScriptHash))
        (§ block
            (§ return (.. chunks (get 1) data))
        )

        (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_UNKNOWN_ERROR, "Script not in the standard scriptPubKey form"))
    )

    ;;;
     ; Returns the public key in this script.  If a script contains two constants and nothing else, it is assumed
     ; to be a scriptSig (input) for a pay-to-address output and the second constant is returned (the first is the
     ; signature).  If a script contains a constant and an OP_CHECKSIG opcode, the constant is returned as it is
     ; assumed to be a direct pay-to-key scriptPubKey (output) and the first constant is the public key.
     ;
     ; @throws ScriptException if the script is none of the named forms.
     ;;
    #_public
    (§ method #_"byte[]" (§ fn getPubKey) [])
        (§ throws #_"ScriptException")
    (§ block
        (§ if (!= (.. chunks (size)) 2))
        (§ block
            (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_UNKNOWN_ERROR, (str "Script not of right size, expecting 2 but got " (.. chunks (size)))))
        )

        (§ let #_"ScriptChunk" (§ name ßchunk0) (.. chunks (get 0)))
        (§ block
            (§ let #_"byte[]" (§ name ßchunk0data) (.. ßchunk0 data))
            (§ block
                (§ let #_"ScriptChunk" (§ name ßchunk1) (.. chunks (get 1)))
                (§ block
                    (§ let #_"byte[]" (§ name ßchunk1data) (.. ßchunk1 data))
                    (§ block

                        ;; If we have two large constants assume the input to a pay-to-address output.
                        (§ if (and (some? ßchunk0data) (< 2 (.. ßchunk0data length)) (some? ßchunk1data) (< 2 (.. ßchunk1data length))))
                        (§ block
                            (§ return ßchunk1data)
                        )

                        ;; A large constant followed by an OP_CHECKSIG is the key.
                        (§ if (and (.. ßchunk1 (equalsOpCode OP_CHECKSIG)) (some? ßchunk0data) (< 2 (.. ßchunk0data length))))
                        (§ block
                            (§ return ßchunk0data)
                        )

                        (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_UNKNOWN_ERROR, (str "Script did not match expected form: " this)))
                    )
                )
            )
        )
    )

    ;;;
     ; Retrieves the sender public key from a LOCKTIMEVERIFY transaction.
     ;
     ; @throws ScriptException
     ;;
    #_public
    (§ method #_"byte[]" (§ fn getCLTVPaymentChannelSenderPubKey) [])
        (§ throws #_"ScriptException")
    (§ block
        (§ if (isSentToCLTVPaymentChannel))
        (§ block
            (§ return (.. chunks (get 8) data))
        )

        (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_UNKNOWN_ERROR, (str "Script not a standard CHECKLOCKTIMVERIFY transaction: " this)))
    )

    ;;;
     ; Retrieves the recipient public key from a LOCKTIMEVERIFY transaction.
     ;
     ; @throws ScriptException
     ;;
    #_public
    (§ method #_"byte[]" (§ fn getCLTVPaymentChannelRecipientPubKey) [])
        (§ throws #_"ScriptException")
    (§ block
        (§ if (isSentToCLTVPaymentChannel))
        (§ block
            (§ return (.. chunks (get 1) data))
        )

        (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_UNKNOWN_ERROR, (str "Script not a standard CHECKLOCKTIMVERIFY transaction: " this)))
    )

    #_public
    (§ method #_"BigInteger" (§ fn getCLTVPaymentChannelExpiry) [])
    (§ block
        (§ if (isSentToCLTVPaymentChannel))
        (§ block
            (§ return (castToBigInteger (.. chunks (get 4) data), 5, false))
        )

        (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_UNKNOWN_ERROR, (str "Script not a standard CHECKLOCKTIMEVERIFY transaction: " this)))
    )

    ;;;
     ; For 2-element [input] scripts assumes that the paid-to-address can be derived from the public key.
     ; The concept of a "from address" isn't well defined in Bitcoin and you should not assume the sender
     ; of a transaction can actually receive coins on it. This method may be removed in future.
     ;;
    #_deprecated
    #_public
    (§ method #_"Address" (§ fn getFromAddress) [#_"NetworkParameters" (§ name ßparams)])
        (§ throws #_"ScriptException")
    (§ block
        (§ new #_"Address" ßparams, (Utils/sha256hash160 (getPubKey)))
    )

    ;;;
     ; Gets the destination address from this script, if it's in the required form (see getPubKey).
     ;;
    #_public
    (§ method #_"Address" (§ fn getToAddress) [#_"NetworkParameters" (§ name ßparams)])
        (§ throws #_"ScriptException")
    (§ block
        (getToAddress ßparams, false)
    )

    ;;;
     ; Gets the destination address from this script, if it's in the required form (see getPubKey).
     ;
     ; @param forcePayToPubKey If true, allow payToPubKey to be casted to the corresponding address.
     ;                         This is useful if you prefer showing addresses rather than pubkeys.
     ;;
    #_public
    (§ method #_"Address" (§ fn getToAddress) [#_"NetworkParameters" (§ name ßparams), #_"boolean" (§ name ßforcePayToPubKey)])
        (§ throws #_"ScriptException")
    (§ block
        (§ if (isSentToAddress))
        (§ block
            (§ return (§ new #_"Address" ßparams, (getPubKeyHash)))
        )
        (§ if (isPayToScriptHash))
        (§ block
            (§ return (Address/fromP2SHScript ßparams, this))
        )
        (§ if (and ßforcePayToPubKey (isSentToRawPubKey)))
        (§ block
            (§ return (.. (ECKey/fromPublicOnly (getPubKey)) (toAddress ßparams)))
        )

        (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_UNKNOWN_ERROR, "Cannot cast this script to a pay-to-address type"))
    )

    ;;;
     ; Writes out the given byte buffer to the output stream with the correct opcode prefix.
     ; To write an integer call writeBytes(out, Utils.reverseBytes(Utils.encodeMPI(val, false))).
     ;;
    #_public
    #_static
    (§ defn #_"void" (§ fn writeBytes) [#_"OutputStream" (§ name ßos), #_"byte[]" (§ name ßbuf)])
        (§ throws #_"IOException")
    (§ block
        (§ if (< (.. ßbuf length) OP_PUSHDATA1))
        (§ block
            (.. ßos (write (.. ßbuf length)))
            (.. ßos (write ßbuf))
        )
        (§ elseif (< (.. ßbuf length) 256))
        (§ block
            (.. ßos (write OP_PUSHDATA1))
            (.. ßos (write (.. ßbuf length)))
            (.. ßos (write ßbuf))
        )
        (§ elseif (< (.. ßbuf length) 65536))
        (§ block
            (.. ßos (write OP_PUSHDATA2))
            (.. ßos (write (& 0xff (.. ßbuf length))))
            (.. ßos (write (& 0xff (>> (.. ßbuf length) 8))))
            (.. ßos (write ßbuf))
        )
        (§ else )
        (§ block
            (§ throw (§ new #_"RuntimeException" "Unimplemented"))
        )
        nil
    )

    ;;; Creates a program that requires at least N of the given keys to sign, using OP_CHECKMULTISIG. ;;
    #_public
    #_static
    (§ defn #_"byte[]" (§ fn createMultiSigOutputScript) [#_"int" (§ name ßthreshold), #_"List<ECKey>" (§ name ßpubkeys)])
    (§ block
        (Preconditions/checkArgument (< 0 ßthreshold))
        (Preconditions/checkArgument (<= ßthreshold (.. ßpubkeys (size))))
        (Preconditions/checkArgument (<= (.. ßpubkeys (size)) 16)) ;; That's the max we can represent with a single opcode.

        (§ if (< 3 (.. ßpubkeys (size))))
        (§ block
            (.. log (warn "Creating a multi-signature output that is non-standard: {} ßpubkeys, should be <= 3", (.. ßpubkeys (size))))
        )

        (§ try )
        (§ block
            (§ let #_"ByteArrayOutputStream" (§ name ßbits) (§ new #_"ByteArrayOutputStream"))
            (§ block
                (.. ßbits (write (encodeToOpN ßthreshold)))
                (§ for (§ var #_"ECKey" (§ name ßkey)) :for ßpubkeys)
                (§ block
                    (writeBytes ßbits, (.. ßkey (getPubKey)))
                )
                (.. ßbits (write (encodeToOpN (.. ßpubkeys (size)))))
                (.. ßbits (write OP_CHECKMULTISIG))
                (§ return (.. ßbits (toByteArray)))
            )
        )
        (§ catch #_"IOException" (§ name ße))
        (§ block
            (§ throw (§ new #_"RuntimeException" ße)) ;; Cannot happen.
        )
    )

    #_public
    #_static
    (§ defn #_"byte[]" (§ fn createInputScript) [#_"byte[]" (§ name ßsignature), #_"byte[]" (§ name ßpubkey)])
    (§ block
        (§ try )
        (§ block
            ;; TODO: Do this by creating a Script *first* then having the script reassemble itself into bytes.
            (§ let #_"ByteArrayOutputStream" (§ name ßbits) (§ new #_"UnsafeByteArrayOutputStream" (+ (.. ßsignature length) (.. ßpubkey length) 2)))
            (§ block
                (writeBytes ßbits, ßsignature)
                (writeBytes ßbits, ßpubkey)
                (§ return (.. ßbits (toByteArray)))
            )
        )
        (§ catch #_"IOException" (§ name ße))
        (§ block
            (§ throw (§ new #_"RuntimeException" ße))
        )
    )

    #_public
    #_static
    (§ defn #_"byte[]" (§ fn createInputScript) [#_"byte[]" (§ name ßsignature)])
    (§ block
        (§ try )
        (§ block
            ;; TODO: Do this by creating a Script *first* then having the script reassemble itself into bytes.
            (§ let #_"ByteArrayOutputStream" (§ name ßbits) (§ new #_"UnsafeByteArrayOutputStream" (+ (.. ßsignature length) 2)))
            (§ block
                (writeBytes ßbits, ßsignature)
                (§ return (.. ßbits (toByteArray)))
            )
        )
        (§ catch #_"IOException" (§ name ße))
        (§ block
            (§ throw (§ new #_"RuntimeException" ße))
        )
    )

    ;;;
     ; Creates an incomplete scriptSig that, once filled with signatures, can redeem output containing this scriptPubKey.
     ; Instead of the signatures resulting script has OP_0.
     ; Having incomplete input script allows to pass around partially signed tx.
     ; It is expected that this program later on will be updated with proper signatures.
     ;;
    #_public
    (§ method #_"Script" (§ fn createEmptyInputScript) [#_nilable #_"ECKey" (§ name ßkey), #_nilable #_"Script" (§ name ßredeemScript)])
    (§ block
        (§ if (isSentToAddress))
        (§ block
            (Preconditions/checkArgument (some? ßkey), "Key required to create pay-to-address input script")
            (§ return (ScriptBuilder/createInputScript nil, ßkey))
        )

        (§ if (isSentToRawPubKey))
        (§ block
            (§ return (ScriptBuilder/createInputScript nil))
        )

        (§ if (isPayToScriptHash))
        (§ block
            (Preconditions/checkArgument (some? ßredeemScript), "Redeem script required to create P2SH input script")
            (§ return (ScriptBuilder/createP2SHMultiSigInputScript nil, ßredeemScript))
        )

        (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_UNKNOWN_ERROR, (str "Do not understand script type: " this)))
    )

    ;;;
     ; Returns a copy of the given scriptSig with the signature inserted in the given position.
     ;;
    #_public
    (§ method #_"Script" (§ fn getScriptSigWithSignature) [#_"Script" (§ name ßscriptSig), #_"byte[]" (§ name ßsigBytes), #_"int" (§ name ßindex)])
    (§ block
        (§ let #_"int" (§ name ßsigsPrefixCount) 0)
        (§ block
            (§ let #_"int" (§ name ßsigsSuffixCount) 0)
            (§ block
                (§ if (isPayToScriptHash))
                (§ block
                    (§ ass (§ name ßsigsPrefixCount) 1) ;; OP_0 <sig>* <redeemScript>
                    (§ ass (§ name ßsigsSuffixCount) 1)
                )
                (§ elseif (isSentToMultiSig))
                (§ block
                    (§ ass (§ name ßsigsPrefixCount) 1) ;; OP_0 <sig>*
                )
                (§ elseif (isSentToAddress))
                (§ block
                    (§ ass (§ name ßsigsSuffixCount) 1) ;; <sig> <pubkey>
                )
                (ScriptBuilder/updateScriptWithSignature ßscriptSig, ßsigBytes, ßindex, ßsigsPrefixCount, ßsigsSuffixCount)
            )
        )
    )

    ;;;
     ; Returns the index where a signature by the key should be inserted.
     ; Only applicable to a P2SH scriptSig.
     ;;
    #_public
    (§ method #_"int" (§ fn getSigInsertionIndex) [#_"Sha256Hash" (§ name ßhash), #_"ECKey" (§ name ßsigningKey)])
    (§ block
        ;; Iterate over existing signatures, skipping the initial OP_0, the final redeem script
        ;; and any placeholder OP_0 sigs.
        (§ let #_"List<ScriptChunk>" (§ name ßexistingChunks) (.. chunks (subList 1, (- (.. chunks (size)) 1))))
        (§ block
            (§ let #_"ScriptChunk" (§ name ßredeemScriptChunk) (.. chunks (get (- (.. chunks (size)) 1))))
            (§ block
                (Preconditions/checkNotNull (.. ßredeemScriptChunk data))
                (§ let #_"Script" (§ name ßredeemScript) (§ new #_"Script" (.. ßredeemScriptChunk data)))
                (§ block

                    (§ let #_"int" (§ name ßsigCount) 0)
                    (§ block
                        (§ let #_"int" (§ name ßmyIndex) (.. ßredeemScript (findKeyInRedeem ßsigningKey)))
                        (§ block
                            (§ for (§ var #_"ScriptChunk" (§ name ßchunk)) :for ßexistingChunks)
                            (§ block
                                (§ if (== (.. ßchunk opcode) OP_0))
                                (§ block
                                    ;; OP_0, skip
                                )
                                (§ else )
                                (§ block
                                    (Preconditions/checkNotNull (.. ßchunk data))
                                    (§ if (< ßmyIndex (.. ßredeemScript (findSigInRedeem (.. ßchunk data), ßhash))))
                                    (§ block
                                        (§ return ßsigCount)
                                    )
                                    (§ ass (§ name ßsigCount) (+ ßsigCount 1))
                                )
                            )
                            ßsigCount
                        )
                    )
                )
            )
        )
    )

    #_private
    (§ method- #_"int" (§ fn findKeyInRedeem) [#_"ECKey" (§ name ßkey)])
    (§ block
        (Preconditions/checkArgument (.. chunks (get 0) (isOpCode))) ;; P2SH scriptSig

        (§ let #_"int" (§ name ßnumKeys) (Script/decodeFromOpN (.. chunks (get (- (.. chunks (size)) 2)) opcode)))
        (§ block
            (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi ßnumKeys) :for (§ recur (+ ßi 1)))
            (§ block
                (§ if (Arrays/equals (.. chunks (get (+ 1 ßi)) data), (.. ßkey (getPubKey))))
                (§ block
                    (§ return ßi)
                )
            )

            (§ throw (§ new #_"IllegalStateException" (str "Could not find matching key " ßkey " in script " this)))
        )
    )

    ;;;
     ; Returns a list of the keys required by this script, assuming a multi-sig script.
     ;
     ; @throws ScriptException if the script type is not understood or is pay to address or is P2SH (run this method on the "Redeem script" instead).
     ;;
    #_public
    (§ method #_"List<ECKey>" (§ fn getPubKeys) [])
    (§ block
        (§ if (not (isSentToMultiSig)))
        (§ block
            (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_UNKNOWN_ERROR, "Only usable for multisig scripts."))
        )

        (§ let #_"ArrayList<ECKey>" (§ name ßresult) (Lists/newArrayList))
        (§ block
            (§ let #_"int" (§ name ßnumKeys) (Script/decodeFromOpN (.. chunks (get (- (.. chunks (size)) 2)) opcode)))
            (§ block
                (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi ßnumKeys) :for (§ recur (+ ßi 1)))
                (§ block
                    (.. ßresult (add (ECKey/fromPublicOnly (.. chunks (get (+ 1 ßi)) data))))
                )
                ßresult
            )
        )
    )

    #_private
    (§ method- #_"int" (§ fn findSigInRedeem) [#_"byte[]" (§ name ßsignatureBytes), #_"Sha256Hash" (§ name ßhash)])
    (§ block
        (Preconditions/checkArgument (.. chunks (get 0) (isOpCode))) ;; P2SH scriptSig

        (§ let #_"int" (§ name ßnumKeys) (Script/decodeFromOpN (.. chunks (get (- (.. chunks (size)) 2)) opcode)))
        (§ block
            (§ let #_"TransactionSignature" (§ name ßsignature) (TransactionSignature/decodeFromBitcoin ßsignatureBytes, true))
            (§ block
                (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi ßnumKeys) :for (§ recur (+ ßi 1)))
                (§ block
                    (§ if (.. (ECKey/fromPublicOnly (.. chunks (get (+ ßi 1)) data)) (verify ßhash, ßsignature)))
                    (§ block
                        (§ return ßi)
                    )
                )

                (§ throw (§ new #_"IllegalStateException" (str "Could not find matching key for signature on " ßhash " sig " (.. Utils/HEX (encode ßsignatureBytes)))))
            )
        )
    )

    #_private
    #_static
    (§ defn- #_"int" (§ fn getSigOpCount) [#_"List<ScriptChunk>" (§ name ßchunks), #_"boolean" (§ name ßaccurate)])
        (§ throws #_"ScriptException")
    (§ block
        (§ let #_"int" (§ name ßsigOps) 0)
        (§ block
            (§ let #_"int" (§ name ßlastOpCode) OP_INVALIDOPCODE)
            (§ block
                (§ for (§ var #_"ScriptChunk" (§ name ßchunk)) :for ßchunks)
                (§ block
                    (§ if (.. ßchunk (isOpCode)))
                    (§ block
                        (§ switch (.. ßchunk opcode))
                        (§ block
                            (§ case OP_CHECKSIG)
                            (§ case OP_CHECKSIGVERIFY)
                            (§ block
                                (§ ass (§ name ßsigOps) (+ ßsigOps 1))
                                (§ break )
                            )
                            (§ case OP_CHECKMULTISIG)
                            (§ case OP_CHECKMULTISIGVERIFY)
                            (§ block
                                (§ if (and ßaccurate (<= OP_1 ßlastOpCode OP_16)))
                                (§ block
                                    (§ ass (§ name ßsigOps) (+ ßsigOps (decodeFromOpN ßlastOpCode)))
                                )
                                (§ else )
                                (§ block
                                    (§ ass (§ name ßsigOps) (+ ßsigOps 20))
                                )
                                (§ break )
                            )
                            (§ default )
                            (§ block
                                (§ break )
                            )
                        )
                        (§ ass (§ name ßlastOpCode) (.. ßchunk opcode))
                    )
                )
                ßsigOps
            )
        )
    )

    #_static
    (§ defn #_"int" (§ fn decodeFromOpN) [#_"int" (§ name ßopcode)])
    (§ block
        (Preconditions/checkArgument (or (== ßopcode OP_0) (== ßopcode OP_1NEGATE) (<= OP_1 ßopcode OP_16)), "decodeFromOpN called on non OP_N ßopcode")

        (§ if (== ßopcode OP_0))
        (§ block
            (§ return 0)
        )
        (§ if (== ßopcode OP_1NEGATE))
        (§ block
            (§ return -1)
        )

        (- (+ ßopcode 1) OP_1)
    )

    #_static
    (§ defn #_"int" (§ fn encodeToOpN) [#_"int" (§ name ßvalue)])
    (§ block
        (Preconditions/checkArgument (<= -1 ßvalue 16), (str "encodeToOpN called for " ßvalue " which we cannot encode in an opcode."))

        (§ if (== ßvalue 0))
        (§ block
            (§ return OP_0)
        )
        (§ if (== ßvalue -1))
        (§ block
            (§ return OP_1NEGATE)
        )

        (+ (- ßvalue 1) OP_1)
    )

    ;;;
     ; Gets the count of regular SigOps in the script program (counting multisig ops as 20).
     ;;
    #_public
    #_static
    (§ defn #_"int" (§ fn getSigOpCount) [#_"byte[]" (§ name ßprogram)])
        (§ throws #_"ScriptException")
    (§ block
        (§ let #_"Script" (§ name ßscript) (§ new #_"Script"))
        (§ block
            (§ try )
            (§ block
                (.. ßscript (parse ßprogram))
            )
            (§ catch #_"ScriptException" (§ name _))
            (§ block
                ;; Ignore errors and count up to the parse-able length.
            )
            (getSigOpCount (.. ßscript chunks), false)
        )
    )

    ;;;
     ; Gets the count of P2SH Sig Ops in the Script scriptSig.
     ;;
    #_public
    #_static
    (§ defn #_"long" (§ fn getP2SHSigOpCount) [#_"byte[]" (§ name ßscriptSig)])
        (§ throws #_"ScriptException")
    (§ block
        (§ let #_"Script" (§ name ßscript) (§ new #_"Script"))
        (§ block
            (§ try )
            (§ block
                (.. ßscript (parse ßscriptSig))
            )
            (§ catch #_"ScriptException" (§ name _))
            (§ block
                ;; Ignore errors and count up to the parse-able length.
            )
            (§ for (§ var #_"int" (§ name ßi) (- (.. ßscript chunks (size)) 1)) :for (<= 0 ßi) :for (§ recur (- ßi 1)))
            (§ block
                (§ if (not (.. ßscript chunks (get ßi) (isOpCode))))
                (§ block
                    (§ let #_"Script" (§ name ßsubScript) (§ new #_"Script"))
                    (§ block
                        (.. ßsubScript (parse (.. ßscript chunks (get ßi) data)))
                        (§ return (getSigOpCount (.. ßsubScript chunks), true))
                    )
                )
            )
            0
        )
    )

    ;;;
     ; Returns number of signatures required to satisfy this script.
     ;;
    #_public
    (§ method #_"int" (§ fn getNumberOfSignaturesRequiredToSpend) [])
    (§ block
        ;; For N of M CHECKMULTISIG script we will need N signatures to spend.
        (§ if (isSentToMultiSig))
        (§ block
            (§ return (Script/decodeFromOpN (.. chunks (get 0) opcode)))
        )

        ;; pay-to-address and pay-to-pubkey require single sig
        (§ if (or (isSentToAddress) (isSentToRawPubKey)))
        (§ block
            (§ return 1)
        )

        (§ if (isPayToScriptHash))
        (§ block
            (§ throw (§ new #_"IllegalStateException" "For P2SH number of signatures depends on redeem script"))
        )

        (§ throw (§ new #_"IllegalStateException" "Unsupported script type"))
    )

    ;;;
     ; Returns number of bytes required to spend this script.  It accepts optional ECKey and redeemScript
     ; that may be required for certain types of script to estimate target size.
     ;;
    #_public
    (§ method #_"int" (§ fn getNumberOfBytesRequiredToSpend) [#_nilable #_"ECKey" (§ name ßpubKey), #_nilable #_"Script" (§ name ßredeemScript)])
    (§ block
        (§ if (isPayToScriptHash))
        (§ block
            ;; scriptSig: <sig> [sig] [sig...] <redeemscript>
            (Preconditions/checkArgument (some? ßredeemScript), "P2SH script requires ßredeemScript to be spent")
            (§ return (+ (* (.. ßredeemScript (getNumberOfSignaturesRequiredToSpend)) SIG_SIZE) (.. ßredeemScript (getProgram) length)))
        )
        (§ elseif (isSentToMultiSig))
        (§ block
            ;; scriptSig: OP_0 <sig> [sig] [sig...]
            (§ return (+ (* (getNumberOfSignaturesRequiredToSpend) SIG_SIZE) 1))
        )
        (§ elseif (isSentToRawPubKey))
        (§ block
            ;; scriptSig: <sig>
            (§ return SIG_SIZE)
        )
        (§ elseif (isSentToAddress))
        (§ block
            ;; scriptSig: <sig> <pubkey>
            (§ let #_"int" (§ name ßuncompressedPubKeySize) 65)
            (§ block
                (§ return (+ SIG_SIZE (if (some? ßpubKey) (.. ßpubKey (getPubKey) length) ßuncompressedPubKeySize)))
            )
        )
        (§ else )
        (§ block
            (§ throw (§ new #_"IllegalStateException" "Unsupported script type"))
        )
    )

    ;;;
     ; <p>Whether or not this is a scriptPubKey representing a pay-to-script-hash output.  In such outputs, the logic
     ; that controls reclamation is not actually in the output at all.  Instead there's just a hash, and it's up to the
     ; spending input to provide a program matching that hash.  This rule is "soft enforced" by the network as it does
     ; not exist in Bitcoin Core.  It means blocks containing P2SH transactions that don't match
     ; correctly are considered valid, but won't be mined upon, so they'll be rapidly re-orgd out of the chain.  This
     ; logic is defined by <a href="https://github.com/bitcoin/bips/blob/master/bip-0016.mediawiki">BIP 16</a>.</p>
     ;
     ; <p>bitcoinj does not support creation of P2SH transactions today.  The goal of P2SH is to allow short addresses
     ; even for complex scripts (e.g. multi-sig outputs) so they are convenient to work with in things like QRcodes or
     ; with copy/paste, and also to minimize the size of the unspent output set (which improves performance of the
     ; Bitcoin system).</p>
     ;;
    #_public
    (§ method #_"boolean" (§ fn isPayToScriptHash) [])
    (§ block
        ;; We have to check against the serialized form because BIP16 defines a P2SH output using an exact byte
        ;; template, not the logical program structure.  Thus you can have two programs that look identical when
        ;; printed out but one is a P2SH script and the other isn't! :( ;; )
        (§ let #_"byte[]" (§ name ßprogram) (getProgram))
        (§ block
            (and (== (.. ßprogram length) 23) (== (& 0xff (§ ai ßprogram 0)) OP_HASH160) (== (& 0xff (§ ai ßprogram 1)) 0x14) (== (& 0xff (§ ai ßprogram 22)) OP_EQUAL))
        )
    )

    ;;;
     ; Returns whether this script matches the format used for multisig outputs: [n] [keys...] [m] CHECKMULTISIG.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isSentToMultiSig) [])
    (§ block
        (§ if (< (.. chunks (size)) 4))
        (§ block
            (§ return false)
        )

        (§ let #_"ScriptChunk" (§ name ßchunk) (.. chunks (get (- (.. chunks (size)) 1))))
        (§ block
            ;; Must end in OP_CHECKMULTISIG[VERIFY].
            (§ if (not (.. ßchunk (isOpCode))))
            (§ block
                (§ return false)
            )
            (§ if (not (or (.. ßchunk (equalsOpCode OP_CHECKMULTISIG)) (.. ßchunk (equalsOpCode OP_CHECKMULTISIGVERIFY)))))
            (§ block
                (§ return false)
            )

            (§ try )
            (§ block
                ;; Second to last chunk must be an OP_N opcode and there should be that many data chunks (keys).
                (§ let #_"ScriptChunk" (§ name ßm) (.. chunks (get (- (.. chunks (size)) 2))))
                (§ block
                    (§ if (not (.. ßm (isOpCode))))
                    (§ block
                        (§ return false)
                    )

                    (§ let #_"int" (§ name ßnumKeys) (decodeFromOpN (.. ßm opcode)))
                    (§ block
                        (§ if (or (< ßnumKeys 1) (!= (.. chunks (size)) (+ 3 ßnumKeys))))
                        (§ block
                            (§ return false)
                        )

                        (§ for (§ var #_"int" (§ name ßi) 1) :for (< ßi (- (.. chunks (size)) 2)) :for (§ recur (+ ßi 1)))
                        (§ block
                            (§ if (.. chunks (get ßi) (isOpCode)))
                            (§ block
                                (§ return false)
                            )
                        )

                        ;; First chunk must be an OP_N opcode too.
                        (§ if (< (decodeFromOpN (.. chunks (get 0) opcode)) 1))
                        (§ block
                            (§ return false)
                        )
                    )
                )
            )
            (§ catch #_"IllegalStateException" (§ name ße))
            (§ block
                (§ return false) ;; Not an OP_N opcode.
            )
            true
        )
    )

    #_public
    (§ method #_"boolean" (§ fn isSentToCLTVPaymentChannel) [])
    (§ block
        ;; Check that opcodes match the pre-determined format.
        ;; chunk[1] = recipient pubkey
        ;; chunk[4] = locktime
        ;; chunk[8] = sender pubkey
        (and (== (.. chunks (size)) 10) (.. chunks (get 0) (equalsOpCode OP_IF)) (.. chunks (get 2) (equalsOpCode OP_CHECKSIGVERIFY)) (.. chunks (get 3) (equalsOpCode OP_ELSE)) (.. chunks (get 5) (equalsOpCode OP_CHECKLOCKTIMEVERIFY)) (.. chunks (get 6) (equalsOpCode OP_DROP)) (.. chunks (get 7) (equalsOpCode OP_ENDIF)) (.. chunks (get 9) (equalsOpCode OP_CHECKSIG)))
    )

    #_private
    #_static
    (§ defn- #_"boolean" (§ fn equalsRange) [#_"byte[]" (§ name ßa), #_"int" (§ name ßstart), #_"byte[]" (§ name ßb)])
    (§ block
        (§ if (< (.. ßa length) (+ ßstart (.. ßb length))))
        (§ block
            (§ return false)
        )

        (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi (.. ßb length)) :for (§ recur (+ ßi 1)))
        (§ block
            (§ if (!= (§ ai ßa (+ ßi ßstart)) (§ ai ßb ßi)))
            (§ block
                (§ return false)
            )
        )

        true
    )

    ;;;
     ; Returns the script bytes of inputScript with all instances of the specified script object removed.
     ;;
    #_public
    #_static
    (§ defn #_"byte[]" (§ fn removeAllInstancesOf) [#_"byte[]" (§ name ßinputScript), #_"byte[]" (§ name ßchunkToRemove)])
    (§ block
        ;; We usually don't end up removing anything.
        (§ let #_"UnsafeByteArrayOutputStream" (§ name ßbos) (§ new #_"UnsafeByteArrayOutputStream" (.. ßinputScript length)))
        (§ block

            (§ let #_"int" (§ name ßcursor) 0)
            (§ block
                (§ while (< ßcursor (.. ßinputScript length)))
                (§ block
                    (§ let #_"boolean" (§ name ßskip) (equalsRange ßinputScript, ßcursor, ßchunkToRemove))
                    (§ block

                        (§ let #_"int" (§ name ßopcode) (& 0xff (§ ai ßinputScript ßcursor)))
                        (§ block
                            (§ ass (§ name ßcursor) (+ ßcursor 1))
                            (§ let #_"int" (§ name ßadditionalBytes) 0)
                            (§ block
                                (§ if (< -1 ßopcode OP_PUSHDATA1))
                                (§ block
                                    (§ ass (§ name ßadditionalBytes) ßopcode)
                                )
                                (§ elseif (== ßopcode OP_PUSHDATA1))
                                (§ block
                                    (§ ass (§ name ßadditionalBytes) (+ (& 0xff (§ ai ßinputScript ßcursor)) 1))
                                )
                                (§ elseif (== ßopcode OP_PUSHDATA2))
                                (§ block
                                    (§ ass (§ name ßadditionalBytes) (+ (| (& 0xff (§ ai ßinputScript ßcursor)) (<< (& 0xff (§ ai ßinputScript (+ ßcursor 1))) 8)) 2))
                                )
                                (§ elseif (== ßopcode OP_PUSHDATA4))
                                (§ block
                                    (§ ass (§ name ßadditionalBytes) (+ (| (& 0xff (§ ai ßinputScript ßcursor)) (<< (& 0xff (§ ai ßinputScript (+ ßcursor 1))) 8) (<< (& 0xff (§ ai ßinputScript (+ ßcursor 1))) 16) (<< (& 0xff (§ ai ßinputScript (+ ßcursor 1))) 24)) 4))
                                )
                                (§ if (not ßskip))
                                (§ block
                                    (§ try )
                                    (§ block
                                        (.. ßbos (write ßopcode))
                                        (.. ßbos (write (Arrays/copyOfRange ßinputScript, ßcursor, (+ ßcursor ßadditionalBytes))))
                                    )
                                    (§ catch #_"IOException" (§ name ße))
                                    (§ block
                                        (§ throw (§ new #_"RuntimeException" ße))
                                    )
                                )
                                (§ ass (§ name ßcursor) (+ ßcursor ßadditionalBytes))
                            )
                        )
                    )
                )
                (.. ßbos (toByteArray))
            )
        )
    )

    ;;;
     ; Returns the script bytes of inputScript with all instances of the given op code removed.
     ;;
    #_public
    #_static
    (§ defn #_"byte[]" (§ fn removeAllInstancesOfOp) [#_"byte[]" (§ name ßinputScript), #_"int" (§ name ßopCode)])
    (§ block
        (removeAllInstancesOf ßinputScript, (§ new #_"byte[]" (§ coll (§ cast #_"byte" ßopCode))))
    )

    #_private
    #_static
    (§ defn- #_"boolean" (§ fn castToBool) [#_"byte[]" (§ name ßdata)])
    (§ block
        (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi (.. ßdata length)) :for (§ recur (+ ßi 1)))
        (§ block
            ;; "Can be negative zero" - Bitcoin Core (see OpenSSL's BN_bn2mpi)
            (§ if (!= (§ ai ßdata ßi) 0))
            (§ block
                (§ return (or (!= ßi (- (.. ßdata length) 1)) (!= (& 0xff (§ ai ßdata ßi)) 0x80)))
            )
        )
        false
    )

    ;;;
     ; Cast a script chunk to a BigInteger.
     ;
     ; @see #castToBigInteger(byte[], int) for values with different maximum sizes.
     ; @throws ScriptException if the chunk is longer than 4 bytes.
     ;;
    #_private
    #_static
    (§ defn- #_"BigInteger" (§ fn castToBigInteger) [#_"byte[]" (§ name ßchunk), #_"boolean" (§ name ßrequireMinimal)])
        (§ throws #_"ScriptException")
    (§ block
        (castToBigInteger ßchunk, 4, ßrequireMinimal)
    )

    ;;;
     ; Cast a script chunk to a BigInteger.  Normally you would want {@link #castToBigInteger(byte[])} instead, this
     ; is only for cases where the normal maximum length does not apply (i.e. CHECKLOCKTIMEVERIFY, CHECKSEQUENCEVERIFY).
     ;
     ; @param maxLength The maximum length in bytes.
     ; @param requireMinimal Check if the number is encoded with the minimum possible number of bytes.
     ; @throws ScriptException if the chunk is longer than the specified maximum.
     ;;
    #_private
    #_static
    (§ defn- #_"BigInteger" (§ fn castToBigInteger) [#_"byte[]" (§ name ßchunk), #_"int" (§ name ßmaxLength), #_"boolean" (§ name ßrequireMinimal)])
        (§ throws #_"ScriptException")
    (§ block
        (§ if (< ßmaxLength (.. ßchunk length)))
        (§ block
            (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_UNKNOWN_ERROR, (str "Script attempted to use an integer larger than " ßmaxLength " bytes")))
        )

        (§ if (and ßrequireMinimal (< 0 (.. ßchunk length))))
        (§ block
            ;; Check that the number is encoded with the minimum possible number of bytes.
            ;;
            ;; If the most-significant-byte - excluding the sign bit - is zero, then we're not minimal.
            ;; Note how this test also rejects the negative-zero encoding, 0x80.
            (§ if (== (& 0x7f (§ ai ßchunk (- (.. ßchunk length) 1))) 0))
            (§ block
                ;; One exception: if there's more than one byte and the most significant bit
                ;; of the second-most-significant-byte is set, it would conflict with the sign bit.
                ;; An example of this case is +-255, which encode to 0xff00 and 0xff80 respectively.
                ;; (big-endian)
                (§ if (or (<= (.. ßchunk length) 1) (== (& 0x80 (§ ai ßchunk (- (.. ßchunk length) 2))) 0)))
                (§ block
                    (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_UNKNOWN_ERROR, "non-minimally encoded script number"))
                )
            )
        )

        (Utils/decodeMPI (Utils/reverseBytes ßchunk), false)
    )

    #_public
    (§ method #_"boolean" (§ fn isOpReturn) [])
    (§ block
        (and (< 0 (.. chunks (size))) (.. chunks (get 0) (equalsOpCode OP_RETURN)))
    )

    ;;;
     ; Exposes the script interpreter.  Normally you should not use this directly, instead use
     ; {@link org.bitcoinj.core.TransactionInput#verify(org.bitcoinj.core.TransactionOutput)} or
