    (§ defn #_"void" (§ fn executeScript) [#_nilable #_"Transaction" (§ name ßtxContainingThis), #_"long" (§ name ßindex), #_"Script" (§ name ßscript), #_"LinkedList<byte[]>" (§ name ßstack), #_"boolean" (§ name ßenforceNullDummy)])
        (§ throws #_"ScriptException")
    (§ block
        (§ let #_"EnumSet<VerifyFlag>" (§ name ßflags) (if ßenforceNullDummy (EnumSet/of VerifyFlag/NULLDUMMY) (EnumSet/noneOf (§ klass #_"VerifyFlag"))))
