    #_public
    #_static
    (§ defn #_"void" (§ fn executeScript) [#_nilable #_"Transaction" (§ name ßtxContainingThis), #_"long" (§ name ßindex), #_"Script" (§ name ßscript), #_"LinkedList<byte[]>" (§ name ßstack), #_"Set<VerifyFlag>" (§ name ßverifyFlags)])
        (§ throws #_"ScriptException")
    (§ block
        (§ let #_"int" (§ name ßopCount) 0)
        (§ block
            (§ let #_"int" (§ name ßlastCodeSepLocation) 0)
            (§ block

                (§ let #_"LinkedList<byte[]>" (§ name ßaltstack) (§ new #_"LinkedList<>"))
                (§ block
                    (§ let #_"LinkedList<Boolean>" (§ name ßifStack) (§ new #_"LinkedList<>"))
                    (§ block

                        (§ for (§ var #_"ScriptChunk" (§ name ßchunk)) :for (.. ßscript chunks))
                        (§ block
                            (§ let #_"boolean" (§ name ßshouldExecute) (not (.. ßifStack (contains false))))
                            (§ block
                                (§ let #_"int" (§ name ßopcode) (.. ßchunk opcode))
                                (§ block

                                    ;; Check stack element size.
                                    (§ if (and (some? (.. ßchunk data)) (< MAX_SCRIPT_ELEMENT_SIZE (.. ßchunk data length))))
                                    (§ block
                                        (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_PUSH_SIZE, "Attempted to push a data string larger than 520 bytes"))
                                    )

                                    ;; Note how OP_RESERVED does not count towards the opcode limit.
                                    (§ if (< OP_16 ßopcode))
                                    (§ block
                                        (§ ass (§ name ßopCount) (+ ßopCount 1))
                                        (§ if (< MAX_OPS_PER_SCRIPT ßopCount))
                                        (§ block
                                            (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_OP_COUNT, "More script operations than is allowed"))
                                        )
                                    )

                                    ;; Disabled opcodes.
                                    (§ if (or (== ßopcode OP_CAT) (== ßopcode OP_SUBSTR) (== ßopcode OP_LEFT) (== ßopcode OP_RIGHT) (== ßopcode OP_INVERT) (== ßopcode OP_AND) (== ßopcode OP_OR) (== ßopcode OP_XOR) (== ßopcode OP_2MUL) (== ßopcode OP_2DIV) (== ßopcode OP_MUL) (== ßopcode OP_DIV) (== ßopcode OP_MOD) (== ßopcode OP_LSHIFT) (== ßopcode OP_RSHIFT)))
                                    (§ block
                                        (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_DISABLED_OPCODE, "Script included a disabled Script Op."))
                                    )

                                    (§ if (and ßshouldExecute (<= OP_0 ßopcode OP_PUSHDATA4)))
                                    (§ block
                                        ;; Check minimal push.
