                                            (.. ßstack (add (§ new #_"byte[]")))
                                        )
                                        (§ else )
                                        (§ block
                                            (.. ßstack (add (.. ßchunk data)))
                                        )
                                    )
                                    (§ elseif (or ßshouldExecute (<= OP_IF ßopcode OP_ENDIF)))
                                    (§ block
                                        (§ switch ßopcode)
                                        (§ block
                                            (§ case OP_IF)
                                            (§ block
                                                (§ if (not ßshouldExecute))
                                                (§ block
                                                    (.. ßifStack (add false))
                                                    (§ continue )
                                                )
                                                (§ if (< (.. ßstack (size)) 1))
                                                (§ block
                                                    (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_UNBALANCED_CONDITIONAL, "Attempted OP_IF on an empty stack"))
                                                )
                                                (.. ßifStack (add (castToBool (.. ßstack (pollLast)))))
                                                (§ continue )
                                            )
                                            (§ case OP_NOTIF)
                                            (§ block
                                                (§ if (not ßshouldExecute))
                                                (§ block
                                                    (.. ßifStack (add false))
                                                    (§ continue )
                                                )
                                                (§ if (< (.. ßstack (size)) 1))
                                                (§ block
                                                    (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_UNBALANCED_CONDITIONAL, "Attempted OP_NOTIF on an empty stack"))
                                                )
                                                (.. ßifStack (add (not (castToBool (.. ßstack (pollLast))))))
                                                (§ continue )
                                            )
                                            (§ case OP_ELSE)
                                            (§ block
                                                (§ if (.. ßifStack (isEmpty)))
                                                (§ block
                                                    (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_UNBALANCED_CONDITIONAL, "Attempted OP_ELSE without OP_IF/NOTIF"))
                                                )
                                                (.. ßifStack (add (not (.. ßifStack (pollLast)))))
                                                (§ continue )
                                            )
                                            (§ case OP_ENDIF)
                                            (§ block
                                                (§ if (.. ßifStack (isEmpty)))
                                                (§ block
                                                    (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_UNBALANCED_CONDITIONAL, "Attempted OP_ENDIF without OP_IF/NOTIF"))
                                                )
                                                (.. ßifStack (pollLast))
                                                (§ continue )
                                            )

