                                                    (§ let #_"BigInteger" (§ name ßOPWITHINnum2) (castToBigInteger (.. ßstack (pollLast)), (.. ßverifyFlags (contains VerifyFlag/MINIMALDATA))))
                                                    (§ block
                                                        (§ let #_"BigInteger" (§ name ßOPWITHINnum1) (castToBigInteger (.. ßstack (pollLast)), (.. ßverifyFlags (contains VerifyFlag/MINIMALDATA))))
