                                                            (§ if (and (<= (.. ßOPWITHINnum2 (compareTo ßOPWITHINnum1)) 0) (< (.. ßOPWITHINnum1 (compareTo ßOPWITHINnum3)) 0)))
                                                            (§ block
                                                                (.. ßstack (add (Utils/reverseBytes (Utils/encodeMPI BigInteger/ONE, false))))
                                                            )
                                                            (§ else )
                                                            (§ block
                                                                (.. ßstack (add (Utils/reverseBytes (Utils/encodeMPI BigInteger/ZERO, false))))
                                                            )
                                                            (§ break )
                                                        )
                                                    )
                                                )
                                            )
