                                                    (§ throw (§ new #_"RuntimeException" ße)) ;; Cannot happen.
                                                )
                                                (§ break )
                                            )
                                            (§ case OP_SHA256)
                                            (§ block
                                                (§ if (< (.. ßstack (size)) 1))
                                                (§ block
                                                    (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_INVALID_STACK_OPERATION, "Attempted OP_SHA256 on an empty stack"))
