                                                    (§ throw (§ new #_"IllegalStateException" "Script attempted signature check but no tx was provided"))
                                                )
                                                (§ ass (§ name ßopCount) (executeMultiSig ßtxContainingThis, (§ cast #_"int" ßindex), ßscript, ßstack, ßopCount, ßlastCodeSepLocation, ßopcode, ßverifyFlags))
                                                (§ break )
                                            )
