                                            (§ case OP_NOP4)
                                            (§ case OP_NOP5)
                                            (§ case OP_NOP6)
                                            (§ case OP_NOP7)
                                            (§ case OP_NOP8)
                                            (§ case OP_NOP9)
                                            (§ case OP_NOP10)
