                                                (§ if (.. ßverifyFlags (contains VerifyFlag/DISCOURAGE_UPGRADABLE_NOPS)))
                                                (§ block
                                                    (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_DISCOURAGE_UPGRADABLE_NOPS, (str "Script used a reserved opcode " ßopcode)))
                                                )
                                                (§ break )
                                            )

