        (§ let #_"boolean" (§ name ßrequireCanonical) (or (.. ßverifyFlags (contains VerifyFlag/STRICTENC)) (.. ßverifyFlags (contains VerifyFlag/DERSIG)) (.. ßverifyFlags (contains VerifyFlag/LOW_S))))
