                                                (§ try )
                                                (§ block
                                                    (§ let #_"TransactionSignature" (§ name ßsig) (TransactionSignature/decodeFromBitcoin (.. ßsigs (getFirst)), ßrequireCanonical))
                                                    (§ block
                                                        (§ let #_"Sha256Hash" (§ name ßhash) (.. ßtxContainingThis (hashForSignature ßindex, ßconnectedScript, (§ cast #_"byte" (.. ßsig sighashFlags)))))
                                                        (§ block
                                                            (§ if (ECKey/verify (.. ßhash (getBytes)), ßsig, ßpubKey))
                                                            (§ block
                                                                (.. ßsigs (pollFirst))
                                                            )
                                                        )
                                                    )
                                                )
                                                (§ catch #_"Exception" (§ name _))
                                                (§ block
                                                    ;; There is (at least) one exception that could be hit here (EOFException, if the sig is too short).
                                                    ;; Because I can't verify there aren't more, we use a very generic Exception catch.
                                                )

                                                (§ if (< (.. ßpubkeys (size)) (.. ßsigs (size))))
                                                (§ block
                                                    (§ ass (§ name ßvalid) false)
                                                    (§ break )
                                                )
                                            )
                                        )

                                        ;; We uselessly remove a stack object to emulate a Bitcoin Core bug.
                                        (§ let #_"byte[]" (§ name ßnullDummy) (.. ßstack (pollLast)))
                                        (§ block
                                            (§ if (and (.. ßverifyFlags (contains VerifyFlag/NULLDUMMY)) (< 0 (.. ßnullDummy length))))
                                            (§ block
                                                (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_SIG_NULLFAIL, (str "OP_CHECKMULTISIG(VERIFY) with non-null nulldummy: " (Arrays/toString ßnullDummy))))
                                            )

                                            (§ if (== ßopcode OP_CHECKMULTISIG))
                                            (§ block
                                                (.. ßstack (add (if ßvalid (§ new #_"byte[]" (§ coll 1 )) (§ new #_"byte[]"))))
                                            )
                                            (§ elseif (and (== ßopcode OP_CHECKMULTISIGVERIFY) (not ßvalid)))
                                            (§ block
                                                (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_SIG_NULLFAIL, "Script failed OP_CHECKMULTISIGVERIFY"))
                                            )

                                            ßopCount
                                        )
                                    )
                                )
                            )
                        )
                    )
                )
            )
        )
    )

    ;;;
     ; Verifies that this script (interpreted as a scriptSig) correctly spends the given scriptPubKey, enabling all validation rules.
     ; @param txContainingThis The transaction in which this input scriptSig resides.
     ;                         Accessing txContainingThis from another thread while this method runs results in undefined behavior.
     ; @param scriptSigIndex The index in txContainingThis of the scriptSig (note: NOT the index of the scriptPubKey).
     ; @param scriptPubKey The connected scriptPubKey containing the conditions needed to claim the value.
     ; @deprecated Use {@link #correctlySpends(org.bitcoinj.core.Transaction, long, org.bitcoinj.script.Script, java.util.Set)}
     ; instead so that verification flags do not change as new verification options are added.
     ;;
    #_deprecated
    #_public
    (§ method #_"void" (§ fn correctlySpends) [#_"Transaction" (§ name ßtxContainingThis), #_"long" (§ name ßscriptSigIndex), #_"Script" (§ name ßscriptPubKey)])
        (§ throws #_"ScriptException")
    (§ block
        (correctlySpends ßtxContainingThis, ßscriptSigIndex, ßscriptPubKey, ALL_VERIFY_FLAGS)
        nil
    )

    ;;;
     ; Verifies that this script (interpreted as a scriptSig) correctly spends the given scriptPubKey.
     ; @param txContainingThis The transaction in which this input scriptSig resides.
     ;                         Accessing txContainingThis from another thread while this method runs results in undefined behavior.
     ; @param scriptSigIndex The index in txContainingThis of the scriptSig (note: NOT the index of the scriptPubKey).
     ; @param scriptPubKey The connected scriptPubKey containing the conditions needed to claim the value.
     ; @param verifyFlags Each flag enables one validation rule.  If in doubt, use {@link #correctlySpends(Transaction, long, Script)}
     ;                    which sets all flags.
     ;;
    #_public
    (§ method #_"void" (§ fn correctlySpends) [#_"Transaction" (§ name ßtxContainingThis), #_"long" (§ name ßscriptSigIndex), #_"Script" (§ name ßscriptPubKey), #_"Set<VerifyFlag>" (§ name ßverifyFlags)])
        (§ throws #_"ScriptException")
    (§ block
        ;; Clone the transaction because executing the script involves editing it, and if we die, we'll leave
        ;; the tx half broken (also it's not so thread safe to work on it directly).
        (§ try )
        (§ block
            (§ ass (§ name ßtxContainingThis) (.. ßtxContainingThis (getParams) (getDefaultSerializer) (makeTransaction (.. ßtxContainingThis (bitcoinSerialize)))))
        )
        (§ catch #_"ProtocolException" (§ name ße))
        (§ block
            (§ throw (§ new #_"RuntimeException" ße)) ;; Should not happen unless we were given a totally broken transaction.
        )

        (§ if (or (< MAX_SCRIPT_SIZE (.. (getProgram) length)) (< MAX_SCRIPT_SIZE (.. ßscriptPubKey (getProgram) length))))
        (§ block
            (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_SCRIPT_SIZE, "Script larger than 10,000 bytes"))
        )

        (§ let #_"LinkedList<byte[]>" (§ name ßstack) (§ new #_"LinkedList<>"))
        (§ block
            (§ let #_"LinkedList<byte[]>" (§ name ßp2shStack) nil)
            (§ block

                (executeScript ßtxContainingThis, ßscriptSigIndex, this, ßstack, ßverifyFlags)
                (§ if (.. ßverifyFlags (contains VerifyFlag/P2SH)))
                (§ block
                    (§ ass (§ name ßp2shStack) (§ new #_"LinkedList<>" ßstack))
                )
                (executeScript ßtxContainingThis, ßscriptSigIndex, ßscriptPubKey, ßstack, ßverifyFlags)

                (§ if (== (.. ßstack (size)) 0))
                (§ block
                    (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_EVAL_FALSE, "Stack empty at end of script execution."))
                )

                (§ if (not (castToBool (.. ßstack (pollLast)))))
                (§ block
                    (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_EVAL_FALSE, (str "Script resulted in a non-true stack: " ßstack)))
                )

                ;; P2SH is pay to script hash.  It means that the scriptPubKey has a special form which is a valid
                ;; program but it has "useless" form that if evaluated as a normal program always returns true.
                ;; Instead, miners recognize it as special based on its template - it provides a hash of the real scriptPubKey
                ;; and that must be provided by the input.  The goal of this bizarre arrangement is twofold:
                ;;
                ;; (1) You can sum up a large, complex script (like a CHECKMULTISIG script) with an address that's the same
                ;;     size as a regular address.  This means it doesn't overload scannable QR codes/NFC tags or become
                ;;     un-wieldy to copy/paste.
                ;; (2) It allows the working set to be smaller: nodes perform best when they can store as many unspent outputs
                ;;     in RAM as possible, so if the outputs are made smaller and the inputs get bigger, then it's better for
                ;;     overall scalability and performance.

                ;; TODO: Check if we can take out enforceP2SH if there's a checkpoint at the enforcement block.
                (§ if (and (.. ßverifyFlags (contains VerifyFlag/P2SH)) (.. ßscriptPubKey (isPayToScriptHash))))
                (§ block
                    (§ for (§ var #_"ScriptChunk" (§ name ßchunk)) :for chunks)
                    (§ block
                        (§ if (and (.. ßchunk (isOpCode)) (< OP_16 (.. ßchunk opcode))))
                        (§ block
                            (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_SIG_PUSHONLY, "Attempted to spend a P2SH scriptPubKey with a script that contained script ops"))
                        )
                    )

                    (§ let #_"byte[]" (§ name ßscriptPubKeyBytes) (.. ßp2shStack (pollLast)))
                    (§ block
                        (§ let #_"Script" (§ name ßscriptPubKeyP2SH) (§ new #_"Script" ßscriptPubKeyBytes))
                        (§ block

                            (executeScript ßtxContainingThis, ßscriptSigIndex, ßscriptPubKeyP2SH, ßp2shStack, ßverifyFlags)

                            (§ if (== (.. ßp2shStack (size)) 0))
                            (§ block
                                (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_EVAL_FALSE, "P2SH stack empty at end of script execution."))
                            )

                            (§ if (not (castToBool (.. ßp2shStack (pollLast)))))
                            (§ block
                                (§ throw (§ new #_"ScriptException" ScriptError/SCRIPT_ERR_EVAL_FALSE, "P2SH script execution resulted in a non-true stack"))
                            )
                        )
                    )
                )
                nil
            )
        )
    )

    ;; Utility that doesn't copy for internal use.
    #_private
    (§ method- #_"byte[]" (§ fn getQuickProgram) [])
    (§ block
        (or program (getProgram))
    )

    ;;;
     ; Get the {@link org.bitcoinj.script.Script.ScriptType}.
     ; @return The script type.
     ;;
    #_public
    (§ method #_"ScriptType" (§ fn getScriptType) [])
    (§ block
        (§ if (isSentToAddress))
        (§ block
            (§ return ScriptType/P2PKH)
        )
        (§ if (isSentToRawPubKey))
        (§ block
            (§ return ScriptType/PUB_KEY)
        )
        (§ if (isPayToScriptHash))
        (§ block
            (§ return ScriptType/P2SH)
        )

        ScriptType/NO_TYPE
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn equals) [#_"Object" (§ name ßo)])
    (§ block
        (§ if (== this ßo))
        (§ block
            (§ return true)
        )
        (§ if (or (nil? ßo) (!= (getClass) (.. ßo (getClass)))))
        (§ block
            (§ return false)
        )
        (Arrays/equals (getQuickProgram), (.. (§ cast #_"Script" ßo) (getQuickProgram)))
    )

    #_override
    #_public
    (§ method #_"int" (§ fn hashCode) [])
    (§ block
        (Arrays/hashCode (getQuickProgram))
    )
)

#_(ns org.bitcoinj.script #_"ScriptBuilder"
    (:import [java.math BigInteger]
             [java.util ArrayList Arrays Collections List Stack])
    (:import [com.google.common.base Preconditions]
             [com.google.common.collect Lists])
   (:require [org.bitcoinj.core Address ECKey Utils]
             [org.bitcoinj.crypto TransactionSignature]
             #_static [org.bitcoinj.script.ScriptOpCodes *]))

;;;
 ; <p>Tools for the construction of commonly used script types.  You don't normally need this as it's hidden
 ; behind convenience methods on {@link org.bitcoinj.core.Transaction}, but they are useful when working with
 ; the protocol at a lower level.</p>
 ;;
#_public
(§ class #_"ScriptBuilder"
    #_private
    (§ field- #_"List<ScriptChunk>" (§ name chunks))

    ;;; Creates a fresh ScriptBuilder with an empty program. ;;
    #_public
    (§ constructor #_"ScriptBuilder" [])
    (§ block
        (§ ass (§ name chunks) (Lists/newLinkedList))
        this
    )

    ;;; Creates a fresh ScriptBuilder with the given program as the starting point. ;;
    #_public
    (§ constructor #_"ScriptBuilder" [#_"Script" (§ name ßtemplate)])
    (§ block
        (§ ass (§ name chunks) (§ new #_"ArrayList<>" (.. ßtemplate (getChunks))))
        this
    )

    ;;; Adds the given chunk to the end of the program. ;;
    #_public
    (§ method #_"ScriptBuilder" (§ fn addChunk) [#_"ScriptChunk" (§ name ßchunk)])
    (§ block
        (addChunk (.. chunks (size)), ßchunk)
    )

    ;;; Adds the given chunk at the given index in the program. ;;
    #_public
    (§ method #_"ScriptBuilder" (§ fn addChunk) [#_"int" (§ name ßindex), #_"ScriptChunk" (§ name ßchunk)])
    (§ block
        (.. chunks (add ßindex, ßchunk))
        this
    )

    ;;; Adds the given opcode to the end of the program. ;;
    #_public
    (§ method #_"ScriptBuilder" (§ fn op) [#_"int" (§ name ßopcode)])
    (§ block
        (op (.. chunks (size)), ßopcode)
    )

    ;;; Adds the given opcode to the given index in the program. ;;
    #_public
    (§ method #_"ScriptBuilder" (§ fn op) [#_"int" (§ name ßindex), #_"int" (§ name ßopcode)])
    (§ block
        (Preconditions/checkArgument (< OP_PUSHDATA4 ßopcode))
        (addChunk ßindex, (§ new #_"ScriptChunk" ßopcode, nil))
    )

    ;;; Adds a copy of the given byte array as a data element (i.e. PUSHDATA) at the end of the program. ;;
    #_public
    (§ method #_"ScriptBuilder" (§ fn data) [#_"byte[]" (§ name ßdata)])
    (§ block
        (if (== (.. ßdata length) 0) (smallNum 0) (data (.. chunks (size)), ßdata))
    )

    ;;; Adds a copy of the given byte array as a data element (i.e. PUSHDATA) at the given index in the program. ;;
    #_public
    (§ method #_"ScriptBuilder" (§ fn data) [#_"int" (§ name ßindex), #_"byte[]" (§ name ßdata)])
    (§ block
        ;; implements BIP62
        (§ let #_"byte[]" (§ name ßcopy) (Arrays/copyOf ßdata, (.. ßdata length)))
        (§ block
            (§ let #_"int" (§ name ßopcode))
            (§ block
                (§ if (== (.. ßdata length) 0))
                (§ block
                    (§ ass (§ name ßopcode) OP_0)
                )
                (§ elseif (== (.. ßdata length) 1))
                (§ block
                    (§ let #_"byte" (§ name ßb) (§ ai ßdata 0))
                    (§ block
                        (§ if (<= 1 ßb 16))
                        (§ block
                            (§ ass (§ name ßopcode) (Script/encodeToOpN ßb))
                        )
                        (§ else )
                        (§ block
                            (§ ass (§ name ßopcode) 1)
                        )
                    )
                )
                (§ elseif (< (.. ßdata length) OP_PUSHDATA1))
                (§ block
                    (§ ass (§ name ßopcode) (.. ßdata length))
                )
                (§ elseif (< (.. ßdata length) 256))
                (§ block
                    (§ ass (§ name ßopcode) OP_PUSHDATA1)
                )
                (§ elseif (< (.. ßdata length) 65536))
                (§ block
                    (§ ass (§ name ßopcode) OP_PUSHDATA2)
                )
                (§ else )
                (§ block
                    (§ throw (§ new #_"RuntimeException" "Unimplemented"))
                )
                (addChunk ßindex, (§ new #_"ScriptChunk" ßopcode, ßcopy))
            )
        )
    )

    ;;;
     ; Adds the given number to the end of the program.
     ; Automatically uses shortest encoding possible.
     ;;
    #_public
    (§ method #_"ScriptBuilder" (§ fn number) [#_"long" (§ name ßnum)])
    (§ block
        (if (<= 0 ßnum 16) (smallNum (§ cast #_"int" ßnum)) (bigNum ßnum))
    )

    ;;;
     ; Adds the given number to the given index in the program.
     ; Automatically uses shortest encoding possible.
     ;;
    #_public
    (§ method #_"ScriptBuilder" (§ fn number) [#_"int" (§ name ßindex), #_"long" (§ name ßnum)])
    (§ block
        (if (<= 0 ßnum 16) (smallNum ßindex, (§ cast #_"int" ßnum)) (bigNum ßindex, ßnum))
    )

    ;;;
     ; Adds the given number as a OP_N opcode to the end of the program.
     ; Only handles values 0-16 inclusive.
     ;
     ; @see #number(int)
     ;;
    #_public
    (§ method #_"ScriptBuilder" (§ fn smallNum) [#_"int" (§ name ßnum)])
    (§ block
        (smallNum (.. chunks (size)), ßnum)
    )

    ;;; Adds the given number as a push data chunk.
     ; This is intended to use for negative numbers or values > 16, and although
     ; it will accept numbers in the range 0-16 inclusive, the encoding would be
     ; considered non-standard.
     ;
     ; @see #number(int)
     ;;
    #_protected
    (§ method #_"ScriptBuilder" (§ fn bigNum) [#_"long" (§ name ßnum)])
    (§ block
        (bigNum (.. chunks (size)), ßnum)
    )

    ;;;
     ; Adds the given number as a OP_N opcode to the given index in the program.
     ; Only handles values 0-16 inclusive.
     ;
     ; @see #number(int)
     ;;
    #_public
    (§ method #_"ScriptBuilder" (§ fn smallNum) [#_"int" (§ name ßindex), #_"int" (§ name ßnum)])
    (§ block
        (Preconditions/checkArgument (<= 0 ßnum), "Cannot encode negative numbers with smallNum")
        (Preconditions/checkArgument (<= ßnum 16), "Cannot encode numbers larger than 16 with smallNum")

        (addChunk ßindex, (§ new #_"ScriptChunk" (Script/encodeToOpN ßnum), nil))
    )

    ;;;
     ; Adds the given number as a push data chunk to the given index in the program.
     ; This is intended to use for negative numbers or values > 16, and although
     ; it will accept numbers in the range 0-16 inclusive, the encoding would be
     ; considered non-standard.
     ;
     ; @see #number(int)
     ;;
    #_protected
    (§ method #_"ScriptBuilder" (§ fn bigNum) [#_"int" (§ name ßindex), #_"long" (§ name ßnum)])
    (§ block
        (§ let #_"byte[]" (§ name ßdata))
        (§ block

            (§ if (== ßnum 0))
            (§ block
                (§ ass (§ name ßdata) (§ new #_"byte[]" (§ count 0)))
            )
            (§ else )
            (§ block
                (§ let #_"Stack<Byte>" (§ name ßresult) (§ new #_"Stack<>"))
                (§ block
                    (§ let #_"boolean" (§ name ßneg) (< ßnum 0))
                    (§ block
                        (§ let #_"long" (§ name ßabsvalue) (Math/abs ßnum))
                        (§ block

                            (§ while (!= ßabsvalue 0))
                            (§ block
                                (.. ßresult (push (§ cast #_"byte" (& ßabsvalue 0xff))))
                                (§ ass (§ name ßabsvalue) (>> ßabsvalue 8))
                            )

                            (§ if (!= (& (.. ßresult (peek)) 0x80) 0))
                            (§ block
                                ;; The most significant byte is >= 0x80, so push an extra byte that
                                ;; contains just the sign of the value.
                                (.. ßresult (push (§ cast #_"byte" (if ßneg 0x80 0))))
                            )
                            (§ elseif ßneg)
                            (§ block
                                ;; The most significant byte is < 0x80 and the value is negative,
                                ;; set the sign bit so it is subtracted and interpreted as a
                                ;; negative when converting back to an integral.
                                (.. ßresult (push (§ cast #_"byte" (| (.. ßresult (pop)) 0x80))))
                            )

                            (§ ass (§ name ßdata) (§ new #_"byte[]" (§ count (.. ßresult (size)))))
                            (§ for (§ var #_"int" (§ name ßbyteIdx) 0) :for (< ßbyteIdx (.. ßdata length)) :for (§ recur (+ ßbyteIdx 1)))
                            (§ block
                                (§ ass (§ name (§ ai ßdata ßbyteIdx)) (.. ßresult (get ßbyteIdx)))
                            )
                        )
                    )
                )
            )

            ;; At most the encoded value could take up to 8 bytes, so we don't need
            ;; to use OP_PUSHDATA opcodes.
            (addChunk ßindex, (§ new #_"ScriptChunk" (.. ßdata length), ßdata))
        )
    )

    ;;; Creates a new immutable Script based on the state of the builder. ;;
    #_public
    (§ method #_"Script" (§ fn build) [])
    (§ block
        (§ new #_"Script" chunks)
    )

    ;;; Creates a scriptPubKey that encodes payment to the given address. ;;
    #_public
    #_static
    (§ defn #_"Script" (§ fn createOutputScript) [#_"Address" (§ name ßto)])
    (§ block
        (§ if (.. ßto (isP2SHAddress)))
        (§ block
            ;; OP_HASH160 <scriptHash> OP_EQUAL
            (§ return (.. (§ new #_"ScriptBuilder") (op OP_HASH160) (data (.. ßto (getHash160))) (op OP_EQUAL) (build)))
        )
        (§ else )
        (§ block
            ;; OP_DUP OP_HASH160 <pubKeyHash> OP_EQUALVERIFY OP_CHECKSIG
            (§ return (.. (§ new #_"ScriptBuilder") (op OP_DUP) (op OP_HASH160) (data (.. ßto (getHash160))) (op OP_EQUALVERIFY) (op OP_CHECKSIG) (build)))
        )
    )

    ;;; Creates a scriptPubKey that encodes payment to the given raw public key. ;;
    #_public
    #_static
    (§ defn #_"Script" (§ fn createOutputScript) [#_"ECKey" (§ name ßkey)])
    (§ block
        (.. (§ new #_"ScriptBuilder") (data (.. ßkey (getPubKey))) (op OP_CHECKSIG) (build))
    )

    ;;;
     ; Creates a scriptSig that can redeem a pay-to-address output.
     ; If given signature is null, incomplete scriptSig will be created with OP_0 instead of signature.
     ;;
    #_public
    #_static
    (§ defn #_"Script" (§ fn createInputScript) [#_nilable #_"TransactionSignature" (§ name ßsignature), #_"ECKey" (§ name ßpubKey)])
    (§ block
        (§ let #_"byte[]" (§ name ßpubkeyBytes) (.. ßpubKey (getPubKey)))
        (§ block
            (§ let #_"byte[]" (§ name ßsigBytes) (if (some? ßsignature) (.. ßsignature (encodeToBitcoin)) (§ new #_"byte[]")))
            (§ block
                (.. (§ new #_"ScriptBuilder") (data ßsigBytes) (data ßpubkeyBytes) (build))
            )
        )
    )

    ;;;
     ; Creates a scriptSig that can redeem a pay-to-pubkey output.
     ; If given signature is null, incomplete scriptSig will be created with OP_0 instead of signature.
     ;;
    #_public
    #_static
    (§ defn #_"Script" (§ fn createInputScript) [#_nilable #_"TransactionSignature" (§ name ßsignature)])
    (§ block
        (§ let #_"byte[]" (§ name ßsigBytes) (if (some? ßsignature) (.. ßsignature (encodeToBitcoin)) (§ new #_"byte[]")))
        (§ block
            (.. (§ new #_"ScriptBuilder") (data ßsigBytes) (build))
        )
    )

    ;;; Creates a program that requires at least N of the given keys to sign, using OP_CHECKMULTISIG. ;;
    #_public
    #_static
    (§ defn #_"Script" (§ fn createMultiSigOutputScript) [#_"int" (§ name ßthreshold), #_"List<ECKey>" (§ name ßpubkeys)])
    (§ block
        (Preconditions/checkArgument (< 0 ßthreshold))
        (Preconditions/checkArgument (<= ßthreshold (.. ßpubkeys (size))))
        (Preconditions/checkArgument (<= (.. ßpubkeys (size)) 16)) ;; That's the max we can represent with a single opcode.

        (§ let #_"ScriptBuilder" (§ name ßbuilder) (§ new #_"ScriptBuilder"))
        (§ block
            (.. ßbuilder (smallNum ßthreshold))
            (§ for (§ var #_"ECKey" (§ name ßkey)) :for ßpubkeys)
            (§ block
                (.. ßbuilder (data (.. ßkey (getPubKey))))
            )
            (.. ßbuilder (smallNum (.. ßpubkeys (size))))
            (.. ßbuilder (op OP_CHECKMULTISIG))
            (.. ßbuilder (build))
        )
    )

    ;;; Create a program that satisfies an OP_CHECKMULTISIG program. ;;
    #_public
    #_static
    (§ defn #_"Script" (§ fn createMultiSigInputScript) [#_"List<TransactionSignature>" (§ name ßsignatures)])
    (§ block
        (§ let #_"List<byte[]>" (§ name ßsigs) (§ new #_"ArrayList<>" (.. ßsignatures (size))))
        (§ block
            (§ for (§ var #_"TransactionSignature" (§ name ßsignature)) :for ßsignatures)
            (§ block
                (.. ßsigs (add (.. ßsignature (encodeToBitcoin))))
            )

            (createMultiSigInputScriptBytes ßsigs, nil)
        )
    )

    ;;; Create a program that satisfies an OP_CHECKMULTISIG program. ;;
    #_public
    #_static
    (§ defn #_"Script" (§ fn createMultiSigInputScript) [#_"TransactionSignature..." (§ name ßsignatures)])
    (§ block
        (createMultiSigInputScript (Arrays/asList ßsignatures))
    )

    ;;; Create a program that satisfies an OP_CHECKMULTISIG program, using pre-encoded signatures. ;;
    #_public
    #_static
    (§ defn #_"Script" (§ fn createMultiSigInputScriptBytes) [#_"List<byte[]>" (§ name ßsignatures)])
    (§ block
        (createMultiSigInputScriptBytes ßsignatures, nil)
    )

    ;;;
     ; Create a program that satisfies a pay-to-script hashed OP_CHECKMULTISIG program.
     ; If given signature list is null, incomplete scriptSig will be created with OP_0 instead of signatures.
     ;;
    #_public
    #_static
    (§ defn #_"Script" (§ fn createP2SHMultiSigInputScript) [#_nilable #_"List<TransactionSignature>" (§ name ßsignatures), #_"Script" (§ name ßmultisigProgram)])
    (§ block
        (§ let #_"List<byte[]>" (§ name ßsigs) (§ new #_"ArrayList<>"))
        (§ block
            (§ if (nil? ßsignatures))
            (§ block
                ;; Create correct number of empty signatures.
                (§ let #_"int" (§ name ßnumSigs) (.. ßmultisigProgram (getNumberOfSignaturesRequiredToSpend)))
                (§ block
                    (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi ßnumSigs) :for (§ recur (+ ßi 1)))
                    (§ block
                        (.. ßsigs (add (§ new #_"byte[]")))
                    )
                )
            )
            (§ else )
            (§ block
                (§ for (§ var #_"TransactionSignature" (§ name ßsignature)) :for ßsignatures)
                (§ block
                    (.. ßsigs (add (.. ßsignature (encodeToBitcoin))))
                )
            )
            (createMultiSigInputScriptBytes ßsigs, (.. ßmultisigProgram (getProgram)))
        )
    )

    ;;;
     ; Create a program that satisfies an OP_CHECKMULTISIG program, using pre-encoded signatures.
     ; Optionally, appends the script program bytes if spending a P2SH output.
     ;;
    #_public
    #_static
    (§ defn #_"Script" (§ fn createMultiSigInputScriptBytes) [#_"List<byte[]>" (§ name ßsignatures), #_nilable #_"byte[]" (§ name ßmultisigProgramBytes)])
    (§ block
        (Preconditions/checkArgument (<= (.. ßsignatures (size)) 16))

        (§ let #_"ScriptBuilder" (§ name ßbuilder) (§ new #_"ScriptBuilder"))
        (§ block
            (.. ßbuilder (smallNum 0)) ;; Work around a bug in CHECKMULTISIG that is now a required part of the protocol.
            (§ for (§ var #_"byte[]" (§ name ßsignature)) :for ßsignatures)
            (§ block
                (.. ßbuilder (data ßsignature))
            )
            (§ if (some? ßmultisigProgramBytes))
            (§ block
                (.. ßbuilder (data ßmultisigProgramBytes))
            )
            (.. ßbuilder (build))
        )
    )

    ;;;
     ; Returns a copy of the given scriptSig with the signature inserted in the given position.
     ;
     ; This function assumes that any missing sigs have OP_0 placeholders.  If given scriptSig
     ; already has all the signatures in place, IllegalArgumentException will be thrown.
     ;
     ; @param targetIndex Where to insert the signature.
     ; @param sigsPrefixCount How many items to copy verbatim (e.g. initial OP_0 for multisig).
     ; @param sigsSuffixCount How many items to copy verbatim at end (e.g. redeemScript for P2SH).
     ;;
    #_public
    #_static
    (§ defn #_"Script" (§ fn updateScriptWithSignature) [#_"Script" (§ name ßscriptSig), #_"byte[]" (§ name ßsignature), #_"int" (§ name ßtargetIndex), #_"int" (§ name ßsigsPrefixCount), #_"int" (§ name ßsigsSuffixCount)])
    (§ block
        (§ let #_"List<ScriptChunk>" (§ name ßinputChunks) (.. ßscriptSig (getChunks)))
        (§ block
            (§ let #_"int" (§ name ßtotalChunks) (.. ßinputChunks (size)))
            (§ block

                ;; Check if we have a place to insert, otherwise just return given scriptSig unchanged.
                ;; We assume here that OP_0 placeholders always go after the sigs, so
                ;; to find if we have sigs missing, we can just check the chunk in latest sig position.
                (§ let #_"boolean" (§ name ßhasMissingSigs) (.. ßinputChunks (get (- ßtotalChunks ßsigsSuffixCount 1)) (equalsOpCode OP_0)))
                (§ block
                    (Preconditions/checkArgument ßhasMissingSigs, "ScriptSig is already filled with signatures")

                    ;; copy the prefix
                    (§ let #_"ScriptBuilder" (§ name ßbuilder) (§ new #_"ScriptBuilder"))
                    (§ block
                        (§ for (§ var #_"ScriptChunk" (§ name ßchunk)) :for (.. ßinputChunks (subList 0, ßsigsPrefixCount)))
                        (§ block
                            (.. ßbuilder (addChunk ßchunk))
                        )

                        ;; Copy the sigs.
                        (§ let #_"int" (§ name ßpos) 0)
                        (§ block
                            (§ let #_"boolean" (§ name ßinserted) false)
                            (§ block
                                (§ for (§ var #_"ScriptChunk" (§ name ßchunk)) :for (.. ßinputChunks (subList ßsigsPrefixCount, (- ßtotalChunks ßsigsSuffixCount))))
                                (§ block
                                    (§ if (== ßpos ßtargetIndex))
                                    (§ block
                                        (§ ass (§ name ßinserted) true)
                                        (.. ßbuilder (data ßsignature))
                                        (§ ass (§ name ßpos) (+ ßpos 1))
                                    )
                                    (§ if (not (.. ßchunk (equalsOpCode OP_0))))
                                    (§ block
                                        (.. ßbuilder (addChunk ßchunk))
                                        (§ ass (§ name ßpos) (+ ßpos 1))
                                    )
                                )

                                ;; Add OP_0's if needed, since we skipped them in the previous loop.
                                (§ while (< ßpos (- ßtotalChunks ßsigsPrefixCount ßsigsSuffixCount)))
                                (§ block
                                    (§ if (== ßpos ßtargetIndex))
                                    (§ block
                                        (§ ass (§ name ßinserted) true)
                                        (.. ßbuilder (data ßsignature))
                                    )
                                    (§ else )
                                    (§ block
                                        (.. ßbuilder (addChunk (§ new #_"ScriptChunk" OP_0, nil)))
                                    )
                                    (§ ass (§ name ßpos) (+ ßpos 1))
                                )

                                ;; Copy the suffix.
                                (§ for (§ var #_"ScriptChunk" (§ name ßchunk)) :for (.. ßinputChunks (subList (- ßtotalChunks ßsigsSuffixCount), ßtotalChunks)))
                                (§ block
                                    (.. ßbuilder (addChunk ßchunk))
                                )

                                (Preconditions/checkState ßinserted)
                                (.. ßbuilder (build))
                            )
                        )
                    )
                )
            )
        )
    )

    ;;;
     ; Creates a scriptPubKey that sends to the given script hash.  Read
     ; <a href="https://github.com/bitcoin/bips/blob/master/bip-0016.mediawiki">BIP 16</a>
     ; to learn more about this kind of script.
     ;;
    #_public
    #_static
    (§ defn #_"Script" (§ fn createP2SHOutputScript) [#_"byte[]" (§ name ßhash)])
    (§ block
        (Preconditions/checkArgument (== (.. ßhash length) 20))

        (.. (§ new #_"ScriptBuilder") (op OP_HASH160) (data ßhash) (op OP_EQUAL) (build))
    )

    ;;;
     ; Creates a scriptPubKey for the given redeem script.
     ;;
    #_public
    #_static
    (§ defn #_"Script" (§ fn createP2SHOutputScript) [#_"Script" (§ name ßredeemScript)])
    (§ block
        (§ let #_"byte[]" (§ name ßhash) (Utils/sha256hash160 (.. ßredeemScript (getProgram))))
        (§ block
            (ScriptBuilder/createP2SHOutputScript ßhash)
        )
    )

    ;;;
     ; Creates a P2SH output script with given public keys and threshold.
     ; Given public keys will be placed in redeem script in the lexicographical sorting order.
     ;;
    #_public
    #_static
    (§ defn #_"Script" (§ fn createP2SHOutputScript) [#_"int" (§ name ßthreshold), #_"List<ECKey>" (§ name ßpubkeys)])
    (§ block
        (§ let #_"Script" (§ name ßredeemScript) (createRedeemScript ßthreshold, ßpubkeys))
        (§ block
            (createP2SHOutputScript ßredeemScript)
        )
    )

    ;;;
     ; Creates redeem script with given public keys and threshold.
     ; Given public keys will be placed in redeem script in the lexicographical sorting order.
     ;;
    #_public
    #_static
    (§ defn #_"Script" (§ fn createRedeemScript) [#_"int" (§ name ßthreshold), #_"List<ECKey>" (§ name ßpubkeys)])
    (§ block
        (§ ass (§ name ßpubkeys) (§ new #_"ArrayList<>" ßpubkeys))
        (Collections/sort ßpubkeys, ECKey/PUBKEY_COMPARATOR)
        (ScriptBuilder/createMultiSigOutputScript ßthreshold, ßpubkeys)
    )

    ;;;
     ; Creates a script of the form OP_RETURN [data].  This feature allows you to attach
     ; a small piece of data (like a hash of something stored elsewhere) to a zero valued
     ; output which can never be spent and thus does not pollute the ledger.
     ;;
    #_public
    #_static
    (§ defn #_"Script" (§ fn createOpReturnScript) [#_"byte[]" (§ name ßdata)])
    (§ block
        (Preconditions/checkArgument (<= (.. ßdata length) 80))

        (.. (§ new #_"ScriptBuilder") (op OP_RETURN) (data ßdata) (build))
    )

    #_public
    #_static
    (§ defn #_"Script" (§ fn createCLTVPaymentChannelOutput) [#_"BigInteger" (§ name ßtime), #_"ECKey" (§ name ßfrom), #_"ECKey" (§ name ßto)])
    (§ block
        (§ let #_"byte[]" (§ name ßtimeBytes) (Utils/reverseBytes (Utils/encodeMPI ßtime, false)))
        (§ block
            (§ if (< 5 (.. ßtimeBytes length)))
            (§ block
                (§ throw (§ new #_"RuntimeException" "Time too large to encode as 5-byte int"))
            )

            (.. (§ new #_"ScriptBuilder") (op OP_IF) (data (.. ßto (getPubKey))) (op OP_CHECKSIGVERIFY) (op OP_ELSE) (data ßtimeBytes) (op OP_CHECKLOCKTIMEVERIFY) (op OP_DROP) (op OP_ENDIF) (data (.. ßfrom (getPubKey))) (op OP_CHECKSIG) (build))
        )
    )

    #_public
    #_static
    (§ defn #_"Script" (§ fn createCLTVPaymentChannelRefund) [#_"TransactionSignature" (§ name ßsignature)])
    (§ block
        (§ let #_"ScriptBuilder" (§ name ßbuilder) (§ new #_"ScriptBuilder"))
        (§ block
            (.. ßbuilder (data (.. ßsignature (encodeToBitcoin))))
            (.. ßbuilder (data (§ new #_"byte[]" (§ coll 0 )))) ;; Use the CHECKLOCKTIMEVERIFY if branch.
            (.. ßbuilder (build))
        )
    )

    #_public
    #_static
    (§ defn #_"Script" (§ fn createCLTVPaymentChannelP2SHRefund) [#_"TransactionSignature" (§ name ßsignature), #_"Script" (§ name ßredeemScript)])
    (§ block
        (§ let #_"ScriptBuilder" (§ name ßbuilder) (§ new #_"ScriptBuilder"))
        (§ block
            (.. ßbuilder (data (.. ßsignature (encodeToBitcoin))))
            (.. ßbuilder (data (§ new #_"byte[]" (§ coll 0 )))) ;; Use the CHECKLOCKTIMEVERIFY if branch.
            (.. ßbuilder (data (.. ßredeemScript (getProgram))))
            (.. ßbuilder (build))
        )
    )

    #_public
    #_static
    (§ defn #_"Script" (§ fn createCLTVPaymentChannelP2SHInput) [#_"byte[]" (§ name ßfrom), #_"byte[]" (§ name ßto), #_"Script" (§ name ßredeemScript)])
    (§ block
        (§ let #_"ScriptBuilder" (§ name ßbuilder) (§ new #_"ScriptBuilder"))
        (§ block
            (.. ßbuilder (data ßfrom))
            (.. ßbuilder (data ßto))
            (.. ßbuilder (smallNum 1)) ;; Use the CHECKLOCKTIMEVERIFY if branch.
            (.. ßbuilder (data (.. ßredeemScript (getProgram))))
            (.. ßbuilder (build))
        )
    )

    #_public
    #_static
    (§ defn #_"Script" (§ fn createCLTVPaymentChannelInput) [#_"TransactionSignature" (§ name ßfrom), #_"TransactionSignature" (§ name ßto)])
    (§ block
        (createCLTVPaymentChannelInput (.. ßfrom (encodeToBitcoin)), (.. ßto (encodeToBitcoin)))
    )

    #_public
    #_static
    (§ defn #_"Script" (§ fn createCLTVPaymentChannelInput) [#_"byte[]" (§ name ßfrom), #_"byte[]" (§ name ßto)])
    (§ block
        (§ let #_"ScriptBuilder" (§ name ßbuilder) (§ new #_"ScriptBuilder"))
        (§ block
            (.. ßbuilder (data ßfrom))
            (.. ßbuilder (data ßto))
            (.. ßbuilder (smallNum 1)) ;; Use the CHECKLOCKTIMEVERIFY if branch.
            (.. ßbuilder (build))
        )
    )
)

#_(ns org.bitcoinj.script #_"ScriptChunk"
    (:import [java.io IOException OutputStream]
             [java.util Arrays])
    (:import [com.google.common.base Objects Preconditions])
   (:require [org.bitcoinj.core Utils]
             #_static [org.bitcoinj.script.ScriptOpCodes *]))

;;;
 ; A script element that is either a data push (signature, pubkey, etc.) or a non-push (logic, numeric, etc.) operation.
 ;;
#_public
(§ class #_"ScriptChunk"
    ;;; Operation to be executed.  Opcodes are defined in {@link ScriptOpCodes}. ;;
    #_public
    (§ field #_"int" (§ name opcode))
    ;;;
     ; For push operations, this is the vector to be pushed on the stack.
     ; For {@link ScriptOpCodes#OP_0}, the vector is empty.
     ; Null for non-push operations.
     ;;
    #_nilable
    #_public
    (§ field #_"byte[]" (§ name data))
    #_private
    (§ field- #_"int" (§ name startLocationInProgram))

    #_public
    (§ constructor #_"ScriptChunk" [#_"int" (§ name ßopcode), #_"byte[]" (§ name ßdata)])
    (§ block
        (§ this ßopcode, ßdata, -1)
        this
    )

    #_public
    (§ constructor #_"ScriptChunk" [#_"int" (§ name ßopcode), #_"byte[]" (§ name ßdata), #_"int" (§ name ßstartLocationInProgram)])
    (§ block
        (§ ass (§ name (.. this ßopcode)) ßopcode)
        (§ ass (§ name (.. this ßdata)) ßdata)
        (§ ass (§ name (.. this ßstartLocationInProgram)) ßstartLocationInProgram)
        this
    )

    #_public
    (§ method #_"boolean" (§ fn equalsOpCode) [#_"int" (§ name ßopcode)])
    (§ block
        (== ßopcode (.. this ßopcode))
    )

    ;;;
     ; If this chunk is a single byte of non-pushdata content (could be OP_RESERVED or some invalid Opcode).
     ;;
    #_public
    (§ method #_"boolean" (§ fn isOpCode) [])
    (§ block
        (< OP_PUSHDATA4 opcode)
    )

    ;;;
     ; Returns true if this chunk is pushdata content, including the single-byte pushdatas.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isPushData) [])
    (§ block
        (<= opcode OP_16)
    )

    #_public
    (§ method #_"int" (§ fn getStartLocationInProgram) [])
    (§ block
        (Preconditions/checkState (<= 0 startLocationInProgram))

        startLocationInProgram
    )

    ;;; If this chunk is an OP_N opcode returns the equivalent integer value. ;;
    #_public
    (§ method #_"int" (§ fn decodeOpN) [])
    (§ block
        (Preconditions/checkState (isOpCode))

        (Script/decodeFromOpN opcode)
    )

    ;;;
     ; Called on a pushdata chunk, returns true if it uses the smallest possible way (according to BIP62) to push the data.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isShortestPossiblePushData) [])
    (§ block
        (Preconditions/checkState (isPushData))

        (§ if (nil? data))
        (§ block
            (§ return true) ;; OP_N
        )
        (§ if (== (.. data length) 0))
        (§ block
            (§ return (== opcode OP_0))
        )
        (§ if (== (.. data length) 1))
        (§ block
            (§ let #_"byte" (§ name ßb) (§ ai data 0))
            (§ block
                (§ if (<= 0x01 ßb 0x10))
                (§ block
                    (§ return (== opcode (- (+ OP_1 ßb) 1)))
                )
                (§ if (== (& ßb 0xff) 0x81))
                (§ block
                    (§ return (== opcode OP_1NEGATE))
                )
            )
        )
        (§ if (< (.. data length) OP_PUSHDATA1))
        (§ block
            (§ return (== opcode (.. data length)))
        )
        (§ if (< (.. data length) 256))
        (§ block
            (§ return (== opcode OP_PUSHDATA1))
        )
        (§ if (< (.. data length) 65536))
        (§ block
            (§ return (== opcode OP_PUSHDATA2))
        )

        ;; Can never be used, but implemented for completeness.
        (== opcode OP_PUSHDATA4)
    )

    #_public
    (§ method #_"void" (§ fn write) [#_"OutputStream" (§ name ßstream)])
        (§ throws #_"IOException")
    (§ block
        (§ if (isOpCode))
        (§ block
            (Preconditions/checkState (nil? data))
            (.. ßstream (write opcode))
        )
        (§ elseif (some? data))
        (§ block
            (§ if (< opcode OP_PUSHDATA1))
            (§ block
                (Preconditions/checkState (== (.. data length) opcode))
                (.. ßstream (write opcode))
            )
            (§ elseif (== opcode OP_PUSHDATA1))
            (§ block
                (Preconditions/checkState (<= (.. data length) 0xff))
                (.. ßstream (write OP_PUSHDATA1))
                (.. ßstream (write (.. data length)))
            )
            (§ elseif (== opcode OP_PUSHDATA2))
            (§ block
                (Preconditions/checkState (<= (.. data length) 0xffff))
                (.. ßstream (write OP_PUSHDATA2))
                (.. ßstream (write (& 0xff (.. data length))))
                (.. ßstream (write (& 0xff (>> (.. data length) 8))))
            )
            (§ elseif (== opcode OP_PUSHDATA4))
            (§ block
                (Preconditions/checkState (<= (.. data length) Script/MAX_SCRIPT_ELEMENT_SIZE))
                (.. ßstream (write OP_PUSHDATA4))
                (Utils/uint32ToByteStreamLE (.. data length), ßstream)
            )
            (§ else )
            (§ block
                (§ throw (§ new #_"RuntimeException" "Unimplemented"))
            )
            (.. ßstream (write data))
        )
        (§ else )
        (§ block
            (.. ßstream (write opcode)) ;; smallNum
        )
        nil
    )

    #_override
    #_public
    (§ method #_"String" (§ fn toString) [])
    (§ block
        (§ let #_"StringBuilder" (§ name ßsb) (§ new #_"StringBuilder"))
        (§ block
            (§ if (isOpCode))
            (§ block
                (.. ßsb (append (getOpCodeName opcode)))
            )
            (§ elseif (some? data)) ;; Data chunk.
            (§ block
                (.. ßsb (append (getPushDataName opcode)) (append "[") (append (.. Utils/HEX (encode data))) (append "]"))
            )
            (§ else ) ;; Small num.
            (§ block
                (.. ßsb (append (Script/decodeFromOpN opcode)))
            )
            (.. ßsb (toString))
        )
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn equals) [#_"Object" (§ name ßo)])
    (§ block
        (§ if (== this ßo))
        (§ block
            (§ return true)
        )
        (§ if (or (nil? ßo) (!= (getClass) (.. ßo (getClass)))))
        (§ block
            (§ return false)
        )
        (§ let #_"ScriptChunk" (§ name ßother) (§ cast #_"ScriptChunk" ßo))
        (§ block
            (and (== opcode (.. ßother opcode)) (== startLocationInProgram (.. ßother startLocationInProgram)) (Arrays/equals data, (.. ßother data)))
        )
    )

    #_override
    #_public
    (§ method #_"int" (§ fn hashCode) [])
    (§ block
        (Objects/hashCode opcode, startLocationInProgram, (Arrays/hashCode data))
    )
)

#_(ns org.bitcoinj.script #_"ScriptError"
    (:import [java.util HashMap Map]))

#_public
(§ enum #_"ScriptError"
    (§ item (SCRIPT_ERR_OK "OK"))
    (§ item (SCRIPT_ERR_UNKNOWN_ERROR "UNKNOWN_ERROR"))
    (§ item (SCRIPT_ERR_EVAL_FALSE "EVAL_FALSE"))
    (§ item (SCRIPT_ERR_OP_RETURN "OP_RETURN"))

    ;;; max sizes ;;
    (§ item (SCRIPT_ERR_SCRIPT_SIZE "SCRIPT_SIZE"))
    (§ item (SCRIPT_ERR_PUSH_SIZE "PUSH_SIZE"))
    (§ item (SCRIPT_ERR_OP_COUNT "OP_COUNT"))
    (§ item (SCRIPT_ERR_STACK_SIZE "STACK_SIZE"))
    (§ item (SCRIPT_ERR_SIG_COUNT "SIG_COUNT"))
    (§ item (SCRIPT_ERR_PUBKEY_COUNT "PUBKEY_COUNT"))

    ;;; failed verify operations ;;
    (§ item (SCRIPT_ERR_VERIFY "VERIFY"))
    (§ item (SCRIPT_ERR_EQUALVERIFY "EQUALVERIFY"))
    (§ item (SCRIPT_ERR_CHECKMULTISIGVERIFY "CHECKMULTISIGVERIFY"))
    (§ item (SCRIPT_ERR_CHECKSIGVERIFY "CHECKSIGVERIFY"))
    (§ item (SCRIPT_ERR_NUMEQUALVERIFY "NUMEQUALVERIFY"))

    ;;; logical/format/canonical errors ;;
    (§ item (SCRIPT_ERR_BAD_OPCODE "BAD_OPCODE"))
    (§ item (SCRIPT_ERR_DISABLED_OPCODE "DISABLED_OPCODE"))
    (§ item (SCRIPT_ERR_INVALID_STACK_OPERATION "INVALID_STACK_OPERATION"))
    (§ item (SCRIPT_ERR_INVALID_ALTSTACK_OPERATION "INVALID_ALTSTACK_OPERATION"))
    (§ item (SCRIPT_ERR_UNBALANCED_CONDITIONAL "UNBALANCED_CONDITIONAL"))

    ;;; CHECKLOCKTIMEVERIFY and CHECKSEQUENCEVERIFY ;;
    (§ item (SCRIPT_ERR_NEGATIVE_LOCKTIME "NEGATIVE_LOCKTIME"))
    (§ item (SCRIPT_ERR_UNSATISFIED_LOCKTIME "UNSATISFIED_LOCKTIME"))

    ;;; malleability ;;
    (§ item (SCRIPT_ERR_SIG_HASHTYPE "SIG_HASHTYPE"))
    (§ item (SCRIPT_ERR_SIG_DER "SIG_DER"))
    (§ item (SCRIPT_ERR_MINIMALDATA "MINIMALDATA"))
    (§ item (SCRIPT_ERR_SIG_PUSHONLY "SIG_PUSHONLY"))
    (§ item (SCRIPT_ERR_SIG_HIGH_S "SIG_HIGH_S"))
    (§ item (SCRIPT_ERR_SIG_NULLDUMMY "SIG_NULLDUMMY"))
    (§ item (SCRIPT_ERR_PUBKEYTYPE "PUBKEYTYPE"))
    (§ item (SCRIPT_ERR_CLEANSTACK "CLEANSTACK"))
    (§ item (SCRIPT_ERR_MINIMALIF "MINIMALIF"))
    (§ item (SCRIPT_ERR_SIG_NULLFAIL "NULLFAIL"))

    ;;; softfork safeness ;;
    (§ item (SCRIPT_ERR_DISCOURAGE_UPGRADABLE_NOPS "DISCOURAGE_UPGRADABLE_NOPS"))
    (§ item (SCRIPT_ERR_DISCOURAGE_UPGRADABLE_WITNESS_PROGRAM "DISCOURAGE_UPGRADABLE_WITNESS_PROGRAM"))

    ;;; segregated witness ;;
    (§ item (SCRIPT_ERR_WITNESS_PROGRAM_WRONG_LENGTH "WITNESS_PROGRAM_WRONG_LENGTH"))
    (§ item (SCRIPT_ERR_WITNESS_PROGRAM_WITNESS_EMPTY "WITNESS_PROGRAM_WITNESS_EMPTY"))
    (§ item (SCRIPT_ERR_WITNESS_PROGRAM_MISMATCH "WITNESS_PROGRAM_MISMATCH"))
    (§ item (SCRIPT_ERR_WITNESS_MALLEATED "WITNESS_MALLEATED"))
    (§ item (SCRIPT_ERR_WITNESS_MALLEATED_P2SH "WITNESS_MALLEATED_P2SH"))
    (§ item (SCRIPT_ERR_WITNESS_UNEXPECTED "WITNESS_UNEXPECTED"))
    (§ item (SCRIPT_ERR_WITNESS_PUBKEYTYPE "WITNESS_PUBKEYTYPE"))

    (§ item (SCRIPT_ERR_ERROR_COUNT "ERROR_COUNT"))

    #_private
    (§ field- #_"String" (§ name mnemonic))
    #_private
    #_static
    (§ def- #_"Map<String, ScriptError>" (§ name mnemonicToScriptErrorMap))

    #_private
    (§ constructor- #_"ScriptError" [#_"String" (§ name ßname)])
    (§ block
        (§ ass (§ name (.. this mnemonic)) ßname)
        this
    )

    #_static
    (§ block
        (§ ass (§ name mnemonicToScriptErrorMap) (§ new #_"HashMap<>"))
        (§ for (§ var #_"ScriptError" (§ name ßerr)) :for (ScriptError/values))
        (§ block
            (.. mnemonicToScriptErrorMap (put (.. ßerr (getMnemonic)), ßerr))
        )
    )

    #_public
    (§ method #_"String" (§ fn getMnemonic) [])
    (§ block
        mnemonic
    )

    #_public
    #_static
    (§ defn #_"ScriptError" (§ fn fromMnemonic) [#_"String" (§ name ßname)])
    (§ block
        (§ let #_"ScriptError" (§ name ßerr) (.. mnemonicToScriptErrorMap (get ßname)))
        (§ block
            (§ if (nil? ßerr))
            (§ block
                (§ throw (§ new #_"IllegalArgumentException" (str ßname " is not a valid name")))
            )
            ßerr
        )
    )
)

#_(ns org.bitcoinj.script #_"ScriptOpCodes"
    (:import [java.util Map])
    (:import [com.google.common.collect ImmutableMap]))

;;;
 ; Various constants that define the assembly-like scripting language that forms part of the Bitcoin protocol.
 ; See {@link org.bitcoinj.script.Script} for details.  Also provides a method to convert them to a string.
 ;;
#_public
(§ class #_"ScriptOpCodes"
    ;; push value
    #_public
    #_static
    (§ def #_"int" (§ name OP_0) 0x00) ;; push empty vector
    #_public
    #_static
    (§ def #_"int" (§ name OP_FALSE) OP_0)
    #_public
    #_static
    (§ def #_"int" (§ name OP_PUSHDATA1) 0x4c)
    #_public
    #_static
    (§ def #_"int" (§ name OP_PUSHDATA2) 0x4d)
    #_public
    #_static
    (§ def #_"int" (§ name OP_PUSHDATA4) 0x4e)
    #_public
    #_static
    (§ def #_"int" (§ name OP_1NEGATE) 0x4f)
    #_public
    #_static
    (§ def #_"int" (§ name OP_RESERVED) 0x50)
    #_public
    #_static
    (§ def #_"int" (§ name OP_1) 0x51)
    #_public
    #_static
    (§ def #_"int" (§ name OP_TRUE) OP_1)
    #_public
    #_static
    (§ def #_"int" (§ name OP_2) 0x52)
    #_public
    #_static
    (§ def #_"int" (§ name OP_3) 0x53)
    #_public
    #_static
    (§ def #_"int" (§ name OP_4) 0x54)
    #_public
    #_static
    (§ def #_"int" (§ name OP_5) 0x55)
    #_public
    #_static
    (§ def #_"int" (§ name OP_6) 0x56)
    #_public
    #_static
    (§ def #_"int" (§ name OP_7) 0x57)
