        (§ ass (§ name (.. this ßpassword)) ßpassword)
        (§ ass (§ name (.. this conn)) (§ new #_"ThreadLocal<>"))
        (§ ass (§ name (.. this allConnections)) (§ new #_"LinkedList<>"))

        (§ try )
