    ;;;
     ; <p>If there isn't a connection on the {@link ThreadLocal} then create and store it.</p>
     ; <p>This will also automatically set up the schema if it does not exist within the DB.</p>
     ; @throws BlockStoreException if successful connection to the DB couldn't be made.
     ;;
    #_protected
    #_synchronized
    (§ method #_"void" (§ fn maybeConnect) [])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ try )
        (§ block
            (§ if (and (some? (.. conn (get))) (not (.. conn (get) (isClosed)))))
            (§ block
                (§ return nil)
            )

            (§ if (or (nil? username) (nil? password)))
            (§ block
                (.. conn (set (DriverManager/getConnection connectionURL)))
            )
            (§ else )
            (§ block
                (§ let #_"Properties" (§ name ßprops) (§ new #_"Properties"))
                (§ block
                    (.. ßprops (setProperty "user", (.. this username)))
                    (.. ßprops (setProperty "password", (.. this password)))
                    (.. conn (set (DriverManager/getConnection connectionURL, ßprops)))
                )
            )
            (.. allConnections (add (.. conn (get))))
            (§ let #_"Connection" (§ name ßconnection) (.. conn (get)))
            (§ block
                ;; Set the schema if one is needed.
                (§ if (some? schemaName))
                (§ block
                    (§ let #_"Statement" (§ name ßs) (.. ßconnection (createStatement)))
                    (§ block
                        (§ for (§ var #_"String" (§ name ßsql)) :for (getCreateSchemeSQL))
                        (§ block
                            (.. ßs (execute ßsql))
                        )
                    )
                )
                (.. log (info (str "Made a new ßconnection to database " connectionURL)))
            )
        )
        (§ catch #_"SQLException" (§ name ße))
        (§ block
            (§ throw (§ new #_"BlockStoreException" ße))
        )
        nil
    )

