    (§ block
        (§ for (§ var #_"Connection" (§ name ßconn)) :for allConnections)
        (§ block
            (§ try )
            (§ block
                (§ if (not (.. ßconn (getAutoCommit))))
                (§ block
                    (.. ßconn (rollback))
                )
                (.. ßconn (close))
                (§ if (== ßconn (.. this ßconn (get))))
                (§ block
                    (.. this ßconn (set nil))
                )
            )
            (§ catch #_"SQLException" (§ name ße))
            (§ block
                (§ throw (§ new #_"RuntimeException" ße))
            )
        )
        (.. allConnections (clear))
        nil
    )

