    ;;;
     ; Create the tables in the database.
     ; @throws java.sql.SQLException if there is a database error.
     ; @throws BlockStoreException if the block store could not be created.
     ;;
    #_private
    (§ method- #_"void" (§ fn createTables) [])
        (§ throws #_"SQLException", #_"BlockStoreException")
    (§ block
        (§ let #_"Statement" (§ name ßs) (.. conn (get) (createStatement)))
        (§ block
            ;; Create all the database tables.
            (§ for (§ var #_"String" (§ name ßsql)) :for (getCreateTablesSQL))
            (§ block
                (§ if (.. log (isDebugEnabled)))
                (§ block
                    (.. log (debug "DatabaseFullPrunedBlockStore : CREATE table [SQL= {0}]", ßsql))
                )
                (.. ßs (executeUpdate ßsql))
            )
            ;; Create all the database indexes.
            (§ for (§ var #_"String" (§ name ßsql)) :for (getCreateIndexesSQL))
            (§ block
                (§ if (.. log (isDebugEnabled)))
                (§ block
                    (.. log (debug "DatabaseFullPrunedBlockStore : CREATE index [SQL= {0}]", ßsql))
                )
                (.. ßs (executeUpdate ßsql))
            )
            (.. ßs (close))

            ;; Insert the initial settings for this store.
            (§ let #_"PreparedStatement" (§ name ßps) (.. conn (get) (prepareStatement (getInsertSettingsSQL))))
            (§ block
                (.. ßps (setString 1, CHAIN_HEAD_SETTING))
                (.. ßps (setNull 2, Types/BINARY))
                (.. ßps (execute))
                (.. ßps (setString 1, VERIFIED_CHAIN_HEAD_SETTING))
                (.. ßps (setNull 2, Types/BINARY))
                (.. ßps (execute))
                (.. ßps (setString 1, VERSION_SETTING))
                (.. ßps (setBytes 2, (.. "03" (getBytes))))
                (.. ßps (execute))
                (.. ßps (close))

                (createNewStore params)
