    ;;;
     ; Create a new store for the given {@link org.bitcoinj.core.NetworkParameters}.
     ; @param params The network.
     ; @throws BlockStoreException if the store couldn't be created.
     ;;
    #_private
    (§ method- #_"void" (§ fn createNewStore) [#_"NetworkParameters" (§ name ßparams)])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ try )
        (§ block
            ;; Set up the genesis block.  When we start out fresh, it is by definition the top of the chain.
            (§ let #_"StoredBlock" (§ name ßstoredGenesisHeader) (§ new #_"StoredBlock" (.. ßparams (getGenesisBlock) (cloneAsHeader)), (.. ßparams (getGenesisBlock) (getWork)), 0))
            (§ block
                ;; The coinbase in the genesis block is not spendable.  This is because of how Bitcoin Core inits
                ;; its database - the genesis transaction isn't actually in the db so its spent flags can never be updated.
                (§ let #_"List<Transaction>" (§ name ßgenesisTransactions) (Lists/newLinkedList))
                (§ block
                    (§ let #_"StoredUndoableBlock" (§ name ßstoredGenesis) (§ new #_"StoredUndoableBlock" (.. ßparams (getGenesisBlock) (getHash)), ßgenesisTransactions))
                    (§ block
                        (put ßstoredGenesisHeader, ßstoredGenesis)
                        (setChainHead ßstoredGenesisHeader)
                        (setVerifiedChainHead ßstoredGenesisHeader)
                    )
                )
            )
        )
        (§ catch #_"VerificationException" (§ name ße))
        (§ block
            (§ throw (§ new #_"RuntimeException" ße)) ;; Cannot happen.
        )
        nil
    )

    ;;;
     ; Initialise the store state from the database.
     ; @throws java.sql.SQLException if there is a database error.
     ; @throws BlockStoreException if there is a block store error.
     ;;
    #_private
    (§ method- #_"void" (§ fn initFromDatabase) [])
        (§ throws #_"SQLException", #_"BlockStoreException")
    (§ block
        (§ let #_"PreparedStatement" (§ name ßps) (.. conn (get) (prepareStatement (getSelectSettingsSQL))))
        (§ block
            (§ let #_"ResultSet" (§ name ßrs))
            (§ block
                (.. ßps (setString 1, CHAIN_HEAD_SETTING))
                (§ ass (§ name ßrs) (.. ßps (executeQuery)))
                (§ if (not (.. ßrs (next))))
                (§ block
                    (§ throw (§ new #_"BlockStoreException" "corrupt database block store - no chain head pointer"))
                )

                (§ let #_"Sha256Hash" (§ name ßhash) (Sha256Hash/wrap (.. ßrs (getBytes 1))))
                (§ block
                    (.. ßrs (close))
                    (§ ass (§ name (.. this chainHeadBlock)) (get ßhash))
                    (§ ass (§ name (.. this chainHeadHash)) ßhash)
                    (§ if (== (.. this chainHeadBlock) nil))
                    (§ block
                        (§ throw (§ new #_"BlockStoreException" "corrupt database block store - head block not found"))
                    )

                    (.. ßps (setString 1, VERIFIED_CHAIN_HEAD_SETTING))
                    (§ ass (§ name ßrs) (.. ßps (executeQuery)))
                    (§ if (not (.. ßrs (next))))
                    (§ block
                        (§ throw (§ new #_"BlockStoreException" "corrupt database block store - no verified chain head pointer"))
                    )

                    (§ ass (§ name ßhash) (Sha256Hash/wrap (.. ßrs (getBytes 1))))
                    (.. ßrs (close))
                    (.. ßps (close))
                    (§ ass (§ name (.. this verifiedChainHeadBlock)) (get ßhash))
                    (§ ass (§ name (.. this verifiedChainHeadHash)) ßhash)
                    (§ if (== (.. this verifiedChainHeadBlock) nil))
                    (§ block
                        (§ throw (§ new #_"BlockStoreException" "corrupt database block store - verified head block not found"))
                    )
                    nil
                )
            )
        )
    )

    #_protected
    (§ method #_"void" (§ fn putUpdateStoredBlock) [#_"StoredBlock" (§ name ßstoredBlock), #_"boolean" (§ name ßwasUndoable)])
        (§ throws #_"SQLException")
    (§ block
        (§ try )
        (§ block
            (§ let #_"PreparedStatement" (§ name ßps) (.. conn (get) (prepareStatement (getInsertHeadersSQL))))
            (§ block
                ;; We skip the first 4 bytes because (on mainnet) the minimum target has 4 0-bytes.
                (§ let #_"byte[]" (§ name ßhashBytes) (§ new #_"byte[]" (§ count 28)))
                (§ block
                    (System/arraycopy (.. ßstoredBlock (getHeader) (getHash) (getBytes)), 4, ßhashBytes, 0, 28)
